ALTER PROCEDURE [dbo].[CROSS_TABLE_MON_sp]
	@TABLENAME VARCHAR(100),
	@NEWTABLE VARCHAR(100)
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @sqlcmd varchar(100)
	if exists(select * from INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = @NEWTABLE)
	begin
		set @sqlcmd = 'drop table ' + @NEWTABLE
		exec(@sqlcmd)
	end
	Declare @createscript varchar(max)
	Declare @Oldtable SYSNAME 
	set @Oldtable = 'dbo.'+ @TABLENAME
	Exec GetGenerateCreate_sp @Oldtable ,@createscript output
	Exec RENAME_TABLE_IDX_sp @OLD_TABLENAME=@TABLENAME,@NEW_TABLENAME=@NEWTABLE
	if exists(select * from INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = @TABLENAME)
	BEGIN
		INSERT INTO LOG_AUTOMATION (mis_date,table_name,exception_log,proc_time ) VALUES (GETDATE(),'CreateTable','Table已經存在無法Create '+@TABLENAME,GETDATE())
	END
	ELSE
	BEGIN
		Exec (@createscript)
	END
END

