ALTER PROCEDURE [dbo].[MAPPING_BUSINESS_sp] 
	@tablename varchar(100),
	@asofdate varchar(10)
AS
/*
DECLARE @tablename varchar(100)
Set @tablename = 'CARDS'
exec MAPPING_BUSINESS_sp @tablename,'20181222'
*/
BEGIN
	SET NOCOUNT ON;
	DECLARE @LOG VARCHAR(1000)
	SET @LOG= @tablename + '-log='
	DECLARE @STEP VARCHAR(50)
	SET @STEP = '1'
	SET @LOG = @LOG + '>' + @STEP
	Declare @cteSQL Nvarchar(4000)
	Declare @sqlstr Nvarchar(4000)
	Declare @temptable varchar(100)
	Declare @SQLCmd Nvarchar(4000)
	Declare @Output Nvarchar(4000)
	set @temptable = 'tempdb..' + @tablename
	SET @asofdate = convert(varchar(10),cast(@asofdate as date),111)
	if exists(select * from INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'map_location')
	begin
		drop table map_location
	end
	if exists(select * from INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'map_logo')
	begin
		drop table map_logo
	end
	Declare @last_alstable varchar(100)
	Declare @last_alsdate varchar(8)
	Declare @last_cldate varchar(8)
	declare @last_cltable varchar(100)
	BEGIN TRY
		SET @STEP = '2-ALSEOM'
		SET @LOG = @LOG + '>' + @STEP
		if object_id('tempdb..#GET_ALSEOM') is not null drop table #GET_ALSEOM
		select CARDNMBR,ACCTTYPE,BUSINESS,PRODTYPE,SEGMENT into #GET_ALSEOM From ALSEOM Where 1>2
		set @last_alsdate = convert(varchar(8),cast(@asofdate as date),112)
		exec SP_GetTableName 'ALSEOM',@last_alsdate, @last_alstable output
		declare @day_cnt int
		set @day_cnt =0;
		while(LEFT(@last_alstable,1)<>'S' AND @day_cnt <=30)
		begin
			set @day_cnt = @day_cnt-1
			set @last_alsdate = convert(varchar(8),dateadd(day,@day_cnt,cast(@asofdate as date)),112)
			exec SP_GetTableName 'ALSEOM',@last_alsdate, @last_alstable output
		end
		SET @SQLCmd = N'INSERT INTO #GET_ALSEOM '
			SET @SQLCmd = @SQLCmd + N' SELECT DISTINCT CARDNMBR,ACCTTYPE,BUSINESS,PRODTYPE,SEGMENT FROM ' + RIGHT(@last_alstable,LEN(@last_alstable)-2)
			SET @SQLCmd = @SQLCmd + N' WHERE MIS_DATE=''' + @last_alsdate + ''' '
			EXECUTE sp_executesql @SQLCmd
		SET @STEP = '3-CARD_LEVEL'
		SET @LOG = @LOG + '>' + @STEP
		set @last_cldate = convert(varchar(8),cast(@asofdate as date),112)
		exec sp_gettablename 'CARD_LEVEL',@last_cldate, @last_cltable output
		set @day_cnt =0;
		while(left(@last_cltable,1)<>'S' and @day_cnt >=-30)
		begin
			set @day_cnt = @day_cnt-1
			set @last_cldate = convert(varchar(8),dateadd(day,@day_cnt,cast(@asofdate as date)),112)
			exec sp_gettablename 'CARD_LEVEL',@last_cldate, @last_cltable output
		end
		/** For db table mapping **/
		if exists(select * from syscolumns where id=object_id(@tablename))
		begin
			SET @STEP = '4-LOCATION/LOGO'
			SET @LOG = @LOG + '>' + @STEP
			if exists(select * from syscolumns where id = object_id(@tablename) and name='LOCATION')
			begin		
				set @sqlstr = 'Select ' + STUFF((SELECT ',' +COLUMN_NAME
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('LOCATION','acctnmbr','cardnmbr') FOR XML PATH('')),1,1,'') 
										+ ' into dbo.map_location from ' +@tablename + ' where location is not null and location <>'''''
				if @tablename = '#_CCH'
				begin
					set @sqlstr = @sqlstr + ' and rte_cch !='+'''I99'''
				end
				exec (@sqlstr) 
			end
			if exists(select * from syscolumns where id = object_id(@tablename) and name='logo')
			begin		
				set @sqlstr = 'Select ' + STUFF((SELECT ',' +COLUMN_NAME
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('logo','acctnmbr','cardnmbr') FOR XML PATH('')),1,1,'') 
										+ ' into dbo.map_logo from ' +@tablename + ' where logo is not null and logo <>'''''
				exec (@sqlstr) 
			end
			/*-------ECMS MAPPING-------*/
			if exists(select * from syscolumns where id=object_id(@tablename) and name='LOGO')
			begin
				SET @STEP = '5-Mapping ECS (Logo From Card Level)'
				SET @LOG = @LOG + '>' + @STEP
				SET @cteSQL =  'WITH CL_LOGO '+char(13)+'AS ('+char(13)+'SELECT DISTINCT ACCTNMBR,CARDNMBR,LOGO '+char(13)+'FROM CARD_LEVEL_Mapping)'
				SET @sqlstr = @cteSQL+char(13)+' update '+@tablename+' set ' + @tablename + '.logo = b.logo from CL_LOGO b'+char(13)+' where ' + @tablename + '.CARDNMBR=b.CARDNMBR and ' + @tablename +'.mis_date = '''+ @asofdate+''''
				exec(@sqlstr)
				SET @STEP = '6-Mapping by Logo'
				SET @LOG = @LOG + '>' + @STEP
				SET @cteSQL = 'WITH LOGO1 '+char(13)+'AS ('+char(13)+'SELECT DISTINCT LOCATION,LOGO,BUSINESS,PRODTYPE '+char(13)+'FROM CARDTYPE '+char(13)+')'
				set @sqlstr = @cteSQL+char(13)+' update '+@tablename+' set '
								+STUFF((SELECT ',' + TABLE_NAME+'.'+COLUMN_NAME + ' = b.'+ COLUMN_NAME 
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('LOCATION','business','prodtype') FOR XML PATH(''))
								 ,1,1,'') 
				set @sqlstr =  @sqlstr + ' from LOGO1 b'+char(13)+' where ' + @tablename + '.logo=b.logo and ' + @tablename +'.mis_date = '''+ @asofdate+''''
				exec(@sqlstr)
				set @sqlstr = 'update '+@tablename+' set '
								+STUFF((SELECT ',' + TABLE_NAME+'.'+COLUMN_NAME + ' = ' +
										case COLUMN_NAME 
											when 'location' then  '''510011'''
											else '''DC''' end
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('LOCATION','business','prodtype') FOR XML PATH(''))
								 ,1,1,'')
							 +' WHERE logo ='+ '''900''' + ' and ' + @tablename +'.mis_date  = '''+ @asofdate+''''
				exec (@sqlstr)
			end
			if exists(select * from syscolumns where id=object_id(@tablename) and name='CARDNMBR')
			begin
				SET @STEP = '7-Mapping by Cardnmbr'
				SET @LOG = @LOG + '>' + @STEP
				SET @cteSQL = 'WITH CARDNMBR1 '+char(13)+'AS ('+char(13)+'SELECT CARDNMBR,LOCATION,LOGO,business,prodtype
	 FROM CARDTYPE WHERE CARDNMBR IN (SELECT CARDNMBR FROM CARDTYPE GROUP BY CARDNMBR HAVING COUNT(1) = 1)) '
				set @sqlstr = @cteSQL+char(13)+' update '+@tablename+' set '
								+STUFF((SELECT ',' + TABLE_NAME+'.'+COLUMN_NAME + ' = b.'+ COLUMN_NAME 
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('location','logo','prodtype','business') FOR XML PATH(''))
								 ,1,1,'') 
				set @sqlstr =  @sqlstr + ' from CARDNMBR1 b'+char(13)+' where LTRIM(RTRIM(' + @tablename + '.CARDNMBR)) like RTRIM(LTRIM(b.CARDNMBR)) +'+'''%'''+'  and ' + @tablename +'.mis_date = '''+ @asofdate+''''
				exec (@sqlstr)
			end
			if exists(select * from syscolumns where id=object_id(@tablename) and name='PGM_CODE') 
			begin
				exec('Update ' +@tablename+ ' set ' + @tablename + '.PGM_CODE='+'''RW''' + 'from CTL_BAU_REWRITE b
				WHERE '+@tablename+'.custid = b.idno ' + ' and ' + @tablename +'.mis_date  = '''+ @asofdate+'''')
				exec('Update ' +@tablename+ ' set ' + @tablename + '.PGM_CODE='+'''DRP''' + 'from EVER_DRP  b
				WHERE '+@tablename+'.custid = b.custid ' + ' and ' + @tablename +'.mis_date  = '''+ @asofdate+'''')
			end
			if exists(select * from syscolumns where id=object_id(@tablename) and name='location') 
			   and exists(select * from syscolumns where id=object_id(@tablename) and name='business')
			begin
				exec('Update ' +@tablename+ ' set ' + @tablename + '.BUSINESS='+'''CTL''' + 
				' WHERE '+@tablename+'.LOCATION ='+'''660103''' + ' and ' + @tablename +'.mis_date = '''+ @asofdate+'''')
			end
			/*--------------------------*/
			/*-------ALS  MAPPING-------*/
			if exists(select * from syscolumns where id=object_id(@tablename) and name = 'CARDNMBR')
			begin
				SET @STEP = '8-Mapping ALS'
				SET @LOG = @LOG + '>' + @STEP
				set @sqlstr = ' update '+@tablename+' set '
								+STUFF((SELECT ',' + TABLE_NAME+'.'+COLUMN_NAME + ' = b.'+ COLUMN_NAME 
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('SEGMENT','BUSINESS','PRODTYPE') FOR XML PATH(''))
								 ,1,1,'') 
				set @sqlstr =  @sqlstr + ' from #GET_ALSEOM b'+char(13)+' where ' + @tablename + '.CARDNMBR=b.CARDNMBR  and ' + @tablename +'.mis_date  = '''+ @asofdate+''''
				exec (@sqlstr)
				if exists(select * from syscolumns where id=object_id(@tablename) and name = 'ACCTTYPE')
				begin
				set @sqlstr =  ' update '+ @tablename + ' set ' + @tablename + '.accttype=b.accttype from #GET_ALSEOM b where ' + @tablename +
					'.CARDNMBR = b.CARDNMBR and '+ @tablename + '.mis_date= '''+ @asofdate+''' and ' + @tablename + '.accttype = '''''
				exec (@sqlstr)
				end
			end
			SET @STEP = '9-Mapping By ACCTTYPE'
			SET @LOG = @LOG + '>' + @STEP
			if exists(select * from syscolumns where id=object_id(@tablename) and name = 'CARDNMBR') 
			   and exists(select * from syscolumns where id=object_id(@tablename) and name = 'ACCTTYPE') and @tablename != 'REC_COMBO_NEW'
			begin
				SET @cteSQL = 'WITH ALSTYPE2 AS (SELECT DISTINCT ACCT,BUSINESS,LOCATION,ACCTTYPE,PRODTYPE,SEGMENT FROM ACCTTYPE)'
				set @sqlstr = @cteSQL+char(13)+' update '+@tablename+' set '
								+STUFF((SELECT ',' + TABLE_NAME+'.'+COLUMN_NAME + ' = b.'+ COLUMN_NAME 
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('SEGMENT','LOCATION','BUSINESS','PRODTYPE') FOR XML PATH(''))
								 ,1,1,'') 
				set @sqlstr =  @sqlstr + ' from ALSTYPE2 b'+char(13)+' where left('+@tablename+'.CARDNMBR,3) =b.ACCT and '+@tablename+'.accttype = b.accttype and ' + @tablename +'.mis_date = '''+ @asofdate+''''
				exec (@sqlstr)
			end
			SET @STEP = '10-Mapping By Cardnmbr'
			SET @LOG = @LOG + '>' + @STEP
			if exists(select * from syscolumns where id=object_id(@tablename) and name = 'CARDNMBR') and @tablename != 'REC_COMBO_NEW'
			begin
				set @sqlstr = ' update '+@tablename+' set '
								+STUFF((SELECT ',' + TABLE_NAME+'.'+COLUMN_NAME + ' = b.'+ COLUMN_NAME 
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('SEGMENT','LOCATION','BUSINESS','PRODTYPE') FOR XML PATH(''))
								 ,1,1,'') 
				set @sqlstr =  @sqlstr + ' from dbo.MB_ACCTTYPE_VW b'+char(13)+'  where left('+@tablename+'.CARDNMBR,3) = b.ACCT' + ' and ' + @tablename +'.mis_date = '''+ @asofdate+''''
				EXEC (@sqlstr)
			end
			SET @STEP = '11-Sepcial accttype mappin'
			SET @LOG = @LOG + '>' + @STEP
			if exists(select * from syscolumns where id=object_id(@tablename) and name = 'CARDNMBR') and @tablename != 'REC_COMBO_NEW'
			begin
				set @sqlstr = ' update '+@tablename+' set '
								+STUFF((SELECT ',' + TABLE_NAME+'.'+COLUMN_NAME + ' = b.'+ COLUMN_NAME 
										FROM INFORMATION_SCHEMA.COLUMNS 
										WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('SEGMENT','BUSINESS','PRODTYPE') FOR XML PATH(''))
								 ,1,1,'') 
				set @sqlstr =  @sqlstr + ' from accttype_sepcial b'+char(13)+'  where ' + @tablename+ '.cardnmbr = b.cardnmbr' + ' and ' + @tablename +'.mis_date= '''+ @asofdate+''''
				EXEC (@sqlstr)
			end
			SET @STEP = '12-Original Location and Logo'
			SET @LOG = @LOG + '>' + @STEP
			if exists(select * from syscolumns where id = object_id(@tablename) and name='LOCATION') and @tablename !='REC_COMBO_NEW'
			begin
				set @sqlstr = 'Update ' + @tablename + ' Set '+@tablename+'.location = b.location from map_location b where '
							+STUFF((SELECT ' and ' + TABLE_NAME+'.'+COLUMN_NAME + ' = b.'+ COLUMN_NAME FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = @tablename AND COLUMN_NAME IN ('ACCTNMBR','CARDNMBR') FOR XML PATH('')),1,5,'')
