ALTER PROCEDURE [dbo].[RENAME_TABLE_IDX_sp]  
	@OLD_TABLENAME varchar(100),
	@NEW_TABLENAME varchar(100)
AS
BEGIN
	SET NOCOUNT ON;
	Declare @sqlstr Nvarchar(800)
	if exists(select * from INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = @NEW_TABLENAME)
	BEGIN
		INSERT INTO LOG_AUTOMATION (mis_date,table_name,exception_log,proc_time ) VALUES (GETDATE(),'RenameTable','無法Reanme '+@OLD_TABLENAME + ' to ' + @NEW_TABLENAME,GETDATE())
	END
	ELSE 
	BEGIN
		DECLARE @OBJ_ID INT
		SET @OBJ_ID = (SELECT [OBJECT_ID]  FROM SYS.tables WHERE NAME = @OLD_TABLENAME)
		DECLARE @sql NVARCHAR(MAX), @cr CHAR(2);
		SELECT @sql = N'', @cr = CHAR(13) + CHAR(10);
		DECLARE @TableLimit TINYINT, @ColumnLimit TINYINT;
		SELECT @TableLimit = 24, @ColumnLimit = 10;
		SELECT @sql = @sql + @cr + N'EXEC sp_rename @objname = N''' 
			+ CASE is_unique_constraint WHEN 0 THEN
			QUOTENAME(REPLACE(OBJECT_NAME(i.[object_id]), '''', '''''')) + '.' ELSE '' END
            + QUOTENAME(REPLACE(i.name, '''', '''''')) + ''', @newname = N'''
            + CASE is_unique_constraint 
				WHEN 1 THEN 'UQ_' 
				ELSE 'IX_' + CASE is_unique WHEN 1 THEN 'U_'  ELSE '' END 
              END 
			+ CASE has_filter WHEN 1 THEN 'F_'  ELSE '' END
            +@NEW_TABLENAME
			+ '_' + STUFF((SELECT '_' + LEFT(REPLACE(c.name, '''', ''), @ColumnLimit)
                FROM sys.columns AS c 
                    INNER JOIN sys.index_columns AS ic
                    ON ic.column_id = c.column_id
                    AND ic.[object_id] = c.[object_id]
                WHERE ic.[object_id] = i.[object_id] 
                AND ic.index_id = i.index_id
                AND is_included_column = 0
                ORDER BY ic.index_column_id FOR XML PATH(''), 
                TYPE).value('.', 'nvarchar(max)'), 1, 1, '') +''',@objtype=''index'';'
			FROM sys.indexes AS i
			WHERE index_id > 0 AND is_primary_key = 0 AND type IN (1,2)
				AND OBJECTPROPERTY(i.[object_id], 'IsMsShipped') = 0
				AND i.[object_id]=@OBJ_ID
		SET @sql=@sql+@cr+'EXEC sp_rename '''+@OLD_TABLENAME+''','''+@NEW_TABLENAME+''''
		EXEC (@sql)
	END
END

