ALTER PROCEDURE [dbo].[SP_GetTableName]
	@TEMP_TABLE VARCHAR(50)
  , @V_MIS_DATE VARCHAR(8)	--資料日期
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @CONN VARCHAR(3)
	IF (@V_MIS_DATE >= CONVERT(VARCHAR,GETDATE(),112) and @TEMP_TABLE <> 'MELITA_ALL')
	BEGIN
		SET @OUTPUT = 'F;資料日不可大於' + CONVERT(VARCHAR,DATEADD(dd,-1,GETDATE()),112)
	END
	ELSE
	BEGIN
		SET @TEMP_TABLE = UPPER(@TEMP_TABLE)
		DECLARE @V_MIS_DATE_MM VARCHAR(2)
		SET @V_MIS_DATE_MM = SUBSTRING(@V_MIS_DATE,5,2)
		DECLARE @V_TODAY_BEF VARCHAR(8)
		SET @V_TODAY_BEF = CONVERT(VARCHAR, DATEADD(D,-1,GETDATE()), 112)
		DECLARE @V_TODAY_BEF_MM VARCHAR(2)
		SET @V_TODAY_BEF_MM = SUBSTRING(@V_TODAY_BEF,5,2)
		DECLARE @DBName VARCHAR(20)
		DECLARE @CROSS_FLAG CHAR(1)
		DECLARE @DATE VARCHAR(8)
		SET @DATE = convert(varchar,DATEADD(dd,-1,DATEADD(YYYY,-1,getdate())),112)
		IF (@V_MIS_DATE >= @DATE)	--資料日期小於去年的前一天，則到HSIS取資料
		BEGIN
			SET @DBName = 'COGDB3'
			DECLARE @TABLE_NAME VARCHAR(50)
			DECLARE @FREQUENCY CHAR(1)
			Select @FREQUENCY = frequency From CROSSMONTH_TABLE Where table_name = @TEMP_TABLE
			DECLARE @CNT INT
			SELECT @CNT = Count(*) FROM CROSSMONTH_TABLE Where table_name = @TEMP_TABLE
			IF (@CNT > 0 )
			BEGIN
				SET @CROSS_FLAG='Y'
				DECLARE @FLAG CHAR(1)
				SET @FLAG = 'F'
				IF (@V_TODAY_BEF = @V_MIS_DATE)
				BEGIN
					IF (@FREQUENCY = 'D')
					BEGIN
						SET @TABLE_NAME = @TEMP_TABLE + '_NEW'
					END
					ELSE
					BEGIN
						SET @FLAG = 'T'
					END
				END
				ELSE
				BEGIN
					SET @FLAG = 'T'
				END
				IF (@FLAG = 'T')
				BEGIN
					IF (@V_TODAY_BEF_MM != @V_MIS_DATE_MM)
					BEGIN
						SET @TABLE_NAME = @TEMP_TABLE + '_' + @V_MIS_DATE_MM
					END
					ELSE
					BEGIN
						SET @TABLE_NAME = @TEMP_TABLE
					END
					DECLARE @MM_BEF VARCHAR(2)	--取上個月月份
					SET @MM_BEF = SUBSTRING(CONVERT(VARCHAR,DATEADD(M,-1,GETDATE()),112),5,2)
					IF (DAY(GETDATE())=2 AND @MM_BEF=@V_MIS_DATE_MM)
					BEGIN
						SELECT @CNT = COUNT(*) FROM CROSSMONTH_TABLE WHERE LEFT(CONVERT(VARCHAR,CROSSTIME,112),6) = LEFT(CONVERT(VARCHAR,GETDATE(),112),6) AND TABLE_NAME=@TEMP_TABLE
						IF (@CNT = 0)
						BEGIN
							SET @TABLE_NAME = @TEMP_TABLE
						END
					END
				END
			END
			ELSE
			BEGIN
				SET @CROSS_FLAG='N'
				SET @TABLE_NAME = @TEMP_TABLE
			END
			SELECT @CNT = COUNT(*) FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME = @TABLE_NAME
			IF (@CNT > 0)
			BEGIN
				DECLARE @BATCH_NAME VARCHAR(50)
				IF (@TEMP_TABLE = 'CARDS' or @TEMP_TABLE = 'BUDTRAN' or @TEMP_TABLE = 'ASSETREC' or @TEMP_TABLE = 'CARDSREC')
				BEGIN
					SET @BATCH_NAME = 'CARDS_DBF'
				END
				ELSE IF(@TEMP_TABLE = 'CACS_EXT_ALS' or @TEMP_TABLE = 'CACS_EXT_ECMS')
				BEGIN
					SET @BATCH_NAME = 'CACS_EXT'
				END
				ELSE IF(@TEMP_TABLE='FIP_LEGAL_CODE_HIST')
				BEGIN
					SET @BATCH_NAME = 'FIP_CCH'
				END
				ELSE IF(@TEMP_TABLE='WOHISTORY')
				BEGIN
					SET @BATCH_NAME = 'GEN_WO'
				END
				ELSE IF(LEFT(@TEMP_TABLE,8) = 'CUS_IDRP')
				BEGIN
					SET @BATCH_NAME = 'CUS_IDRP'
				END
				ELSE IF(LEFT(@TEMP_TABLE,9) = 'CUS_BADRP')
				BEGIN
					SET @BATCH_NAME = 'CUS_BADRP'
				END
				ELSE
				BEGIN
					SET @BATCH_NAME = @TEMP_TABLE
				END
					Select @CNT = Count(*) From CONTROL_TABLE 
					Where TABLE_NAME = @BATCH_NAME and DATEAS=@V_MIS_DATE
					And (BAT_END is not null or RESULT='Success')
					if(@CNT=0 AND @BATCH_NAME='Payment_All_Detail')
					begin
						Select @CNT = Count(*) From CONTROL_TABLE 
						Where TABLE_NAME = 'Payment_All_Detail' 
						AND DATEAS>=@V_MIS_DATE 
						AND DATEAS <= dateadd(d,-1,left(convert(varchar,dateadd(m,1,@V_MIS_DATE),112),6)+'01')	--取當月月底日
						And (BAT_END is not null or RESULT='Success')
					END
					IF (@FREQUENCY='S')
					BEGIN
						Select @CNT = Count(*) From CONTROL_TABLE 
						Where TABLE_NAME = @BATCH_NAME and DATEAS like left(@V_MIS_DATE,4) + '-' + substring(@V_MIS_DATE,5,2) +'%'
						And (BAT_END is not null or RESULT='Success')
					END
					IF (@CNT > 0 )
					BEGIN
						IF (@BATCH_NAME in ('CLIENT_MASTER','CUS_IDRP','CUS_BADRP','IDRP_BASE','Payment_All_Detail'))
						BEGIN
							IF (@BATCH_NAME='Payment_All_Detail')
							BEGIN
								Select top 1 @V_TODAY_BEF = CONVERT(varchar,DATEAS,112)
								From CONTROL_TABLE 
								Where TABLE_NAME = 'Payment_All_Detail'
								AND DATEAS>=@V_MIS_DATE
								AND DATEAS <= dateadd(d,-1,left(convert(varchar,dateadd(m,1,@V_MIS_DATE),112),6)+'01')	--取當月月底日
								Order by BAT_BEGIN desc
							END
							Select @CNT = Count(*) From CONTROL_TABLE 
							Where TABLE_NAME = @BATCH_NAME and DATEAS=@V_TODAY_BEF
							And (BAT_END is not null or RESULT='Success')
							IF(@CNT>0)
							BEGIN
								SET @OUTPUT = 'S;[COGDB3].dbo.' + @TABLE_NAME
							END
							ELSE
							BEGIN
								Select @CNT = Count(*) From CONTROL_TABLE 
								Where TABLE_NAME = @BATCH_NAME and DATEAS=@V_TODAY_BEF
								And BAT_BEGIN is not null
								And BAT_END is null
								And isnull(RESULT,'') = ''
								IF(@CNT>0)
								BEGIN
									SET @OUTPUT = 'L;TableName=' + @TABLE_NAME + '-' + @V_TODAY_BEF + '-此Table資料處理中，請稍後再試!'
								END
								ELSE
								BEGIN
									SET @OUTPUT = 'S;[COGDB3].dbo.' + @TABLE_NAME
								END
							END
						END
						ELSE
						BEGIN
							IF (@CROSS_FLAG='Y')
							BEGIN
								IF (@V_TODAY_BEF = @V_MIS_DATE)
								BEGIN
									SET @OUTPUT = 'S;[COGDB3].dbo.' + @TABLE_NAME
								END
								ELSE
								BEGIN
									IF (@V_MIS_DATE_MM = @V_TODAY_BEF_MM)
									BEGIN
										IF(@FREQUENCY='D')
										BEGIN
											SET @OUTPUT = 'S;[COGDB3].dbo.' + @TABLE_NAME
										END
										ELSE
										BEGIN
											Select @CNT = Count(*) From CONTROL_TABLE 
											Where TABLE_NAME = @BATCH_NAME and DATEAS=@V_TODAY_BEF
											And BAT_BEGIN is not null
											And BAT_END is null
											And isnull(RESULT,'') = ''
											IF(@CNT>0)
											BEGIN
												SET @OUTPUT = 'L;TableName=' + @TABLE_NAME + '-' + @V_TODAY_BEF + '-此Table資料處理中，請稍後再試!'
											END
											ELSE
											BEGIN
												SET @OUTPUT = 'S;[COGDB3].dbo.' + @TABLE_NAME
											END
										END
									END
									ELSE
									BEGIN
										SET @OUTPUT = 'S;[COGDB3].dbo.' + @TABLE_NAME
									END
								END
							END
							ELSE
							BEGIN
								IF (@V_TODAY_BEF = @V_MIS_DATE)
								BEGIN
									SET @OUTPUT = 'S;[COGDB3].dbo.' + @TABLE_NAME
								END
								ELSE
								BEGIN
									Select @CNT = Count(*) From CONTROL_TABLE 
									Where TABLE_NAME = @BATCH_NAME and DATEAS=@V_TODAY_BEF
									And BAT_BEGIN is not null
									And BAT_END is null
									And isnull(RESULT,'') = ''
									IF(@CNT>0)
									BEGIN
										SET @OUTPUT = 'L;TableName=' + @TABLE_NAME + '-' + @V_TODAY_BEF + '-此Table資料處理中，請稍後再試!'
									END
									ELSE
									BEGIN
										SET @OUTPUT = 'S;[COGDB3].dbo.' + @TABLE_NAME
									END
								END
							END
						END
					END
					ELSE
					BEGIN
						SET @OUTPUT = 'F;TableName=' + @TABLE_NAME + '-' + @V_MIS_DATE + '-資料尚未完成，請通知MIS TEAM!'
					END
			END
			ELSE
			BEGIN
				SET @OUTPUT = 'F;[COGDB3] TableName=' + @TABLE_NAME + '-不存在，請先建立!'
			END
		END
		ELSE
		BEGIN
			SET @DATE = convert(varchar,DATEADD(dd,-1,DATEADD(YYYY,-3,getdate())),112)
			IF (@V_MIS_DATE >= @DATE)	--資料日期小於去年的前一天，則到HSIS取資料
			BEGIN
				SET @DBName = 'COGDB6'
				SET @TABLE_NAME = @TEMP_TABLE + '_' + LEFT(@V_MIS_DATE,6)
				SET @OUTPUT = 'S;[COGDB6].dbo.' + @TABLE_NAME
			END
			ELSE
			BEGIN
				SET @OUTPUT = 'F;資料不存在，已超過保留期限!'
			END
		END
	END
	RETURN
END

