ALTER PROCEDURE [dbo].[SP_MoveToHSIS]
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @TableName		varchar(20)
	DECLARE @COGDB3Month_A	tinyint
	DECLARE @COGDB3Note_A	varchar(6)
	DECLARE @COGDB3Month_B	tinyint
	DECLARE @COGDB3Note_B	varchar(6)
	DECLARE @COGDB6Month	tinyint
	DECLARE @COGDB6Note		varchar(6)
	DECLARE @intLoop INT
	DECLARE @SQLCmd NVARCHAR(4000)
	if object_id('tempdb..#SQLTBLTmp') is not null drop table #SQLTBLTmp
	CREATE TABLE #SQLTBLTmp
	(
		DataType VARCHAR(10),
		TableName VARCHAR(50),
		SQLCMD NVARCHAR(1000),
		SQLCMD2 NVARCHAR(1000)
	)
	DECLARE CURSOR_TEMP CURSOR FAST_FORWARD FOR
		Select TableName,COGDB3Month_A,COGDB3Note_A,COGDB3Month_B,COGDB3Note_B,COGDB6Month,COGDB6Note
		from dbo.DataSet_BackUp where IsValid=1 
		UNION
		Select 'D02_LOG_SUMMARY',COGDB3Month_A,COGDB3Note_A,COGDB3Month_B,COGDB3Note_B,COGDB6Month,COGDB6Note 
		from dbo.DataSet_BackUp where IsValid=1 and TableName='D02' 
		UNION
		Select 'D02_ORG_SUMMARY',COGDB3Month_A,COGDB3Note_A,COGDB3Month_B,COGDB3Note_B,COGDB6Month,COGDB6Note 
		from dbo.DataSet_BackUp where IsValid=1 and TableName='D02'
		order by COGDB3Month_A,TableName
	OPEN CURSOR_TEMP
	FETCH NEXT FROM CURSOR_TEMP INTO @TableName,@COGDB3Month_A,@COGDB3Note_A,@COGDB3Month_B,@COGDB3Note_B,@COGDB6Month,@COGDB6Note
	WHILE @@FETCH_STATUS = 0
	BEGIN
		DECLARE @CNT INT
		if object_id('tempdb..#MAXDT') is not null drop table #MAXDT
		Create Table #MAXDT
		(
			MIS_DATE VARCHAR(8)
		)
		DECLARE @MAX_MISDATE VARCHAR(8)
		IF (@COGDB3Month_B > 0 and @COGDB3Note_B='MonEnd')
		BEGIN
			SET @intLoop = @COGDB3Month_A
			WHILE (@intLoop <= @COGDB3Month_B+1)
			BEGIN
				DECLARE @DATE VARCHAR(8) = CONVERT(VARCHAR,DATEADD(M,-@intLoop,GETDATE()),112)
				DECLARE @TableName_MM VARCHAR(50) = @TableName + '_' + SUBSTRING(@DATE,5,2)
				DECLARE @TableName_MMTmp VARCHAR(50) = @TableName_MM + '_TMP'
				SELECT @CNT=COUNT(*) FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME=@TableName_MM
				IF (@CNT>0)
				BEGIN
					DECLARE @SQLCMDTmp NVARCHAR(1000)
					truncate table #MAXDT
					SET @SQLCMDTmp = N'Insert Into #MAXDT Select CONVERT(VARCHAR,MAX(MIS_DATE),112) From ' + @TableName_MM
					EXEC sp_executesql @SQLCMDTmp
					Select @MAX_MISDATE= MIS_DATE From #MAXDT
					DECLARE @MON_END VARCHAR(8) = CONVERT(VARCHAR,DATEADD(D,-1,DATEADD(M,1,CAST(LEFT(@DATE,6)+'01' AS DATE))),112)
					DECLARE @DATE_END VARCHAR(8) = IIF(@MAX_MISDATE=null,@MON_END,@MAX_MISDATE)
					DECLARE @DATE_END2 VARCHAR(8) = CONVERT(VARCHAR,DATEADD(D,-1,CAST(@MON_END AS DATE)),112)
					DECLARE @DELDATE NVARCHAR(1000) = 'BETWEEN ''' + LEFT(@DATE_END,6) + '01'' AND ''' + @DATE_END2 + ''' '					
					DECLARE @BATCH_NAME VARCHAR(50)
					IF (@TableName = 'CARDS' or @TableName = 'BUDTRAN' or @TableName = 'ASSETREC' or @TableName = 'CARDSREC')
					BEGIN
						SET @BATCH_NAME = 'CARDS_DBF'
					END
					ELSE IF(@TableName = 'CACS_EXT_ALS' or @TableName = 'CACS_EXT_ECMS')
					BEGIN
						SET @BATCH_NAME = 'CACS_EXT'
					END
					ELSE IF(@TableName='FIP_LEGAL_CODE_HIST')
					BEGIN
						SET @BATCH_NAME = 'FIP_CCH'
					END
					ELSE IF(LEFT(@TableName,8) = 'CUS_IDRP')
					BEGIN
						SET @BATCH_NAME = 'CUS_IDRP'
					END
					ELSE IF(LEFT(@TableName,9) = 'CUS_BADRP')
					BEGIN
						SET @BATCH_NAME = 'CUS_BADRP'
					END
					ELSE
					BEGIN
						SET @BATCH_NAME = @TableName
					END
					SET @SQLCMDTmp = N'if object_id('''+@TableName_MMTmp+''') is not null drop table ' + @TableName_MMTmp + ' '
					SET @SQLCMDTmp = @SQLCMDTmp + N'Select * Into dbo.' + @TableName_MMTmp + ' From ' + @TableName_MM + ' Where MIS_DATE=''' + @DATE_END + ''' '
					SET @SQLCMDTmp = @SQLCMDTmp + N' Truncate table ' + @TableName_MM
					SET @SQLCMDTmp = @SQLCMDTmp + N' Insert Into ' + @TableName_MM + ' Select * From ' + @TableName_MMTmp
					IF (@MON_END != @DATE_END)
					BEGIN
						SET @SQLCMDTmp = @SQLCMDTmp + N' Update ' + @TableName_MM + ' Set MIS_DATE=''' + @MON_END + ''' '
					END
					DECLARE @SQLCMD2Tmp NVARCHAR(1000) 
					IF (@MON_END != @DATE_END)
					BEGIN
						SET @SQLCMD2Tmp = N'Update dbo.Control_Table Set DATEAS=''' + @MON_END + ''' Where Table_Name=''' + @BATCH_NAME + ''' and DATEAS=''' + @DATE_END + ''' '
						SET @SQLCMD2Tmp = @SQLCMD2Tmp + N' Delete From dbo.Control_Table Where Table_Name=''' + @BATCH_NAME + ''' and DATEAS ' + @DELDATE + ' '
					END
					ELSE
					BEGIN
						SET @SQLCMD2Tmp = N'Delete From dbo.Control_Table Where Table_Name=''' + @BATCH_NAME + ''' and DATEAS ' + @DELDATE + ' '
					END
					IF (@TableName='D02_LOG_SUMMARY' or @TableName='D02_ORG_SUMMARY')
					BEGIN
						SET @SQLCMD2Tmp=''
					END
					insert into #SQLTBLTmp values ('DELETE',@TableName_MM,@SQLCMDTmp,@SQLCMD2Tmp)
				END
				Set @intLoop = @intLoop + 1
			END
		END
		IF (@COGDB6Month > 0)
		BEGIN
			DECLARE @LAST_YEARMonEnd VARCHAR(8) = CONVERT(VARCHAR,DATEADD(D,-1,CAST(LEFT(CONVERT(VARCHAR,DATEADD(M,-11,GETDATE()),112),6)+'01' AS DATE)),112)
			PRINT @LAST_YEARMonEnd
			DECLARE @TableName_TMP VARCHAR(50)=@TableName +'_' + Substring(@LAST_YEARMonEnd,5,2)
			DECLARE @TableName_BK VARCHAR(50)=@TableName +'_' + LEFT(@LAST_YEARMonEnd,6)
			PRINT @TableName_BK
			SELECT @CNT=COUNT(*) FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME=@TableName_TMP
			IF (@CNT>0)
			BEGIN
				truncate table #MAXDT
				SET @SQLCmd = N'Insert Into #MAXDT Select CONVERT(VARCHAR,MAX(MIS_DATE),112) From ' + @TableName_TMP
				EXEC sp_executesql @SQLCmd
				Select @MAX_MISDATE= MIS_DATE From #MAXDT
				DECLARE @WHERE NVARCHAR(200)=''
				IF (@COGDB6Note='MonEnd')
				BEGIN
					SET @WHERE=' WHERE MIS_DATE=''' + @MAX_MISDATE + ''' '
				END	
				IF EXISTS(SELECT * FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME = @TableName_BK)
				BEGIN
					DECLARE @CNT_TMP INT=0
					DECLARE @CNT_BK INT=0
					SET @SQLCmd = 'Select @CNT_TMP=Count(*) from ' + @TableName_TMP + @WHERE
					PRINT @SQLCmd
					insert into #SQLTBLTmp values ('COPY',@TableName_TMP,@SQLCmd,'')
					SET @SQLCmd = 'Select @CNT_BK=Count(*) from ' + @TableName_BK + @WHERE
					PRINT @SQLCmd
					insert into #SQLTBLTmp values ('COPY',@TableName_TMP,@SQLCmd,'')
					IF (@CNT_TMP != @CNT_BK)
					BEGIN
						SET @SQLCmd = 'DROP TABLE ' + @TableName_BK
						PRINT @SQLCmd
						insert into #SQLTBLTmp values ('COPY',@TableName_TMP,@SQLCmd,'')
						SET @SQLCmd = 'Select * Into ' + @TableName_BK + ' From ' + @TableName_TMP + @WHERE
						PRINT @SQLCmd
						insert into #SQLTBLTmp values ('COPY',@TableName_TMP,@SQLCmd,'')
					END
				END
				ELSE
				BEGIN
					SET @SQLCmd = 'Select * Into dbo.' + @TableName_BK + ' From ' + @TableName_TMP + @WHERE
					PRINT @SQLCmd
					insert into #SQLTBLTmp values ('COPY',@TableName_TMP,@SQLCmd,'')
				END
			END
		END
		FETCH NEXT FROM CURSOR_TEMP INTO @TableName,@COGDB3Month_A,@COGDB3Note_A,@COGDB3Month_B,@COGDB3Note_B,@COGDB6Month,@COGDB6Note
	END
