ALTER PROCEDURE [dbo].[TRANS_AS_CIR_sp]
	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	DECLARE @V_MIS_DATE2 VARCHAR(8)
	SET @V_MIS_DATE2 = CONVERT(VARCHAR,@D_MIS_DATE,112)
	DECLARE @V_MON_E VARCHAR(10)
	SET @V_MON_E = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE)+1, 0)),23)
	DECLARE @TODAY_MM VARCHAR(2)
	SET @TODAY_MM = SUBSTRING(CONVERT(VARCHAR,GETDATE(),112),5,2)
	DECLARE @V_MIS_DATE_BEF VARCHAR(8)
	SET @V_MIS_DATE_BEF = CONVERT(VARCHAR,DATEADD(mm,-1,@D_MIS_DATE),112)
	DECLARE @V_MIS_DATE_BEF_MM VARCHAR(2)
	SET @V_MIS_DATE_BEF_MM = SUBSTRING(@V_MIS_DATE_BEF,5,2)
	DECLARE @V_MIS_DATE_BEF_MM_END VARCHAR(10)
	SET @V_MIS_DATE_BEF_MM_END =  CONVERT(VARCHAR,DATEADD(dd,-1,CAST(SUBSTRING(CONVERT(VARCHAR,@D_MIS_DATE,112),1,6)+'01' AS DATE)),23)
	BEGIN TRY
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		PRINT @YYYYMMDD
		SET @STEP = '1'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD, @OUTPUT1 output		
		PRINT @OUTPUT1
		SET @STEP = '2'
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'BUDTRAN',@YYYYMMDD, @OUTPUT2 output		
		PRINT @OUTPUT2
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S')
		BEGIN
			EXEC SP_INSERT_LOG @V_MIS_DATE,AS_CIR,'TRANS_AS_CIR_sp處理開始','N'
			SET @STEP = '4'
			DECLARE @ALSEOM VARCHAR(50)
			SET @ALSEOM = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)			
			PRINT @ALSEOM
			DECLARE @BUDTRAN VARCHAR(50)
			SET @BUDTRAN = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)			
			PRINT @BUDTRAN
			if object_id('tempdb..#ALSEOM') is not null drop table #ALSEOM
			Select * Into #ALSEOM From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM SELECT * FROM ' + @ALSEOM + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '5'
			if object_id('tempdb..#BUDTRAN') is not null drop table #BUDTRAN
			Select * Into #BUDTRAN From BUDTRAN Where 1> 2
			SET @SQLCmd = N'INSERT INTO #BUDTRAN SELECT * FROM ' + @BUDTRAN + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			/*
			IF (@TODAY_MM = @V_MIS_DATE_MM)
			BEGIN
				SET @TABLE_NAME = 'AS_CIRR'
				SET @TABLE_NAME_BEF = 'AS_CIRR_' + @V_MIS_DATE_BEF_MM
			END
			ELSE
			BEGIN
				SET @TABLE_NAME = 'AS_CIRR_' + @V_MIS_DATE_MM
				SET @TABLE_NAME_BEF = 'AS_CIRR_' + @V_MIS_DATE_BEF_MM
			END
			*/
			SET @STEP = '6'
			DECLARE @TABLE_NAME VARCHAR(10)
			DECLARE @OUTPUT3 VARCHAR(50)
			EXEC dbo.SP_WriteTableName 'AS_CIR',@V_MIS_DATE2,@OUTPUT3 output
			PRINT 'AS_CIR this month = ' + @OUTPUT3
			SET @TABLE_NAME = @OUTPUT3
			SET @TABLE_NAME = replace(@TABLE_NAME,'AS_CIR','AS_CIRR')
			SET @SQLCmd = N'DELETE FROM ' + @TABLE_NAME + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''''
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '7'
