			EXEC dbo.SP_WriteTableName 'AS_CIR',@V_MIS_DATE_BEF,@OUTPUT3 output
			PRINT 'AS_CIR last month = ' + @OUTPUT3
			SET @TABLE_NAME_BEF = @OUTPUT3
