				insert into #ALSEOM_TEMP EXEC dbo.SP_ChkTable_GetData 'ALSEOM', @DATE_S
				SET @STEP = '10'
				INSERT INTO #ALS_M1
				SELECT BILLDAY, COUNT(*) AS ACCT, @DATE_S AS DATEAS 
				FROM #ALSEOM_TEMP
				WHERE LOANSTAT='AC' AND NBV > 0 AND CDC = 1
				AND ACCTNMBR IN (SELECT ACCTNMBR FROM #ALSEOM_BEF WHERE LOANSTAT='AC' AND NBV > 0 AND CDC = 0)
