				SELECT BILLDAY, COUNT(*) AS ACCT, @DATE_S AS DATEAS 
				FROM #ALSEOM_TEMP
				WHERE LOANSTAT='AC' AND NBV > 0 AND CDC = 1
				AND ACCTNMBR IN (SELECT ACCTNMBR FROM #ALSEOM_BEF WHERE LOANSTAT='AC' AND NBV > 0 AND CDC = 0)
				AND BILLDAY <= DAY(CAST(@V_MIS_DATE AS DATE)) AND ISNULL(SEGMENT,'') <> ''
				GROUP BY BILLDAY 
				HAVING COUNT(*) > 15
				SET @DATE_S = DATEADD(dd,1,@DATE_S)
			END  
			SET @STEP = '11'
			DECLARE @CNT INT
			SELECT @CNT=COUNT(*) FROM #ALS_M1 WHERE DATEAS=@V_MIS_DATE
			DECLARE @M1_CYC1 INT
			DECLARE @M1_CYC2 INT
