			END
			PRINT '@M1_CYC1=' + CAST(@M1_CYC1 AS VARCHAR)
			PRINT '@M1_CYC2=' + CAST(@M1_CYC2 AS VARCHAR)
			SET @STEP = '12'
			DECLARE @M2_CYC1 int
