ALTER PROCEDURE [dbo].[TRANS_BC_sp]
	@DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @TABLE_NAME VARCHAR(10)
	DECLARE @TABLE_NAME_BEF VARCHAR(10)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@DATE AS DATE)
	DECLARE @V_MON_E VARCHAR(10)
	SET @V_MON_E = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE)+1, 0)),23)
	DECLARE @TODAY_MM VARCHAR(2)
	SET @TODAY_MM = SUBSTRING(CONVERT(VARCHAR,GETDATE(),112),5,2)
	DECLARE @V_MIS_DATE_MM VARCHAR(2)
	SET @V_MIS_DATE_MM = SUBSTRING(CONVERT(VARCHAR,@D_MIS_DATE,112),5,2)
	BEGIN TRY
		SET @STEP = '1'
		EXEC SP_INSERT_LOG @DATE,'BC','BC處理開始','N'
		IF (@TODAY_MM = @V_MIS_DATE_MM)
		BEGIN
			SET @TABLE_NAME = 'BC'
		END
		ELSE
		BEGIN
			SET @TABLE_NAME = 'BC_' + @V_MIS_DATE_MM
		END
		SET @SQLCmd = N'DELETE FROM ' + @TABLE_NAME + ' WHERE MIS_DATE = ''' + @DATE + ''''
		EXECUTE sp_executesql @SQLCmd
		SET @STEP = '2'
		if object_id('tempdb..#CARDS') is not null drop table #CARDS
		select * into #CARDS from CARDS where 1>2
		insert into #CARDS EXEC dbo.SP_ChkTable_GetData 'CARDS', @DATE
		if object_id('tempdb..#CARDSREC') is not null drop table #CARDSREC
		select * into #CARDSREC from CARDSREC where 1>2
		insert into #CARDSREC EXEC dbo.SP_ChkTable_GetData 'CARDSREC', @DATE
		SET @STEP = '3'
		IF (@DATE = @V_MON_E)
		BEGIN
			SET @STEP = '8'
			SET @SQLCmd = N'INSERT INTO ' + @TABLE_NAME
			SET @SQLCmd = @SQLCmd + N' SELECT * FROM #CARDS WHERE CYCLEID >= ' + CAST(DAY(@D_MIS_DATE) AS VARCHAR) + ' AND CACS_STATE <> ''O89'' AND WOAMT = 0 AND LOCATION != ''020107'' '
			SET @SQLCmd = @SQLCmd + N' UNION '
			SET @SQLCmd = @SQLCmd + N' SELECT * FROM #CARDS WHERE CYCLEID >= ' + CAST(DAY(@D_MIS_DATE) AS VARCHAR) + ' AND WODATE = ''' + @DATE + ''' AND WOAMT > 0 '
			SET @SQLCmd = @SQLCmd + N' UNION '
			SET @SQLCmd = @SQLCmd + N' SELECT * FROM #CARDS WHERE CYCLEID >= ' + CAST(DAY(@D_MIS_DATE) AS VARCHAR) + ' AND CACS_STATE <> ''O89'' AND WOAMT = 0 AND LOCATION = ''020107'' AND CDC != 1 '
			EXECUTE sp_executesql @SQLCmd		
		END
		ELSE
		BEGIN
			SET @STEP = '4'
			SET @SQLCmd = N'INSERT INTO ' + @TABLE_NAME
			SET @SQLCmd = @SQLCmd + N' SELECT * FROM #CARDS WHERE CYCLEID=' + CAST(DAY(@D_MIS_DATE) AS VARCHAR) + ' AND CACS_STATE <> ''O89'' AND WOAMT = 0 AND LOCATION != ''020107'' '
			SET @SQLCmd = @SQLCmd + N' UNION '
			SET @SQLCmd = @SQLCmd + N' SELECT * FROM #CARDS WHERE CYCLEID=' + CAST(DAY(@D_MIS_DATE) AS VARCHAR) + ' AND WODATE = ''' + @DATE + ''' AND WOAMT > 0 '
			SET @SQLCmd = @SQLCmd + N' UNION '
			SET @SQLCmd = @SQLCmd + N' SELECT * FROM #CARDS WHERE CYCLEID=' + CAST(DAY(@D_MIS_DATE) AS VARCHAR) + ' AND CACS_STATE <> ''O89'' AND WOAMT = 0 AND LOCATION = ''020107'' AND CDC != 1 '
			EXECUTE sp_executesql @SQLCmd
		END
		SET @STEP = '5'			
		if object_id('tempdb..#CARDS_BEF') is not null drop table #CARDS_BEF
		select * INTO #CARDS_BEF from #CARDS Where CYCLEID=DAY(DATEADD(d,-4,@D_MIS_DATE)) AND CACS_STATE <> 'O89' AND WOAMT = 0 AND LOCATION = '020107' AND CDC = 1
		IF (MONTH(DATEADD(d,-4,@D_MIS_DATE)) <> MONTH(@D_MIS_DATE))
		BEGIN
			SET @STEP = '6'
			DECLARE @DATE_BEF_MM VARCHAR(2)
			SET @DATE_BEF_MM = SUBSTRING(CONVERT(VARCHAR,DATEADD(d,-4,@D_MIS_DATE),112),5,2)
			SET @TABLE_NAME_BEF = 'BC_' + @DATE_BEF_MM
			SET @SQLCmd = N'INSERT INTO ' + @TABLE_NAME_BEF
			SET @SQLCmd = @SQLCmd + N' SELECT * FROM #CARDS_BEF'
			EXECUTE sp_executesql @SQLCmd				
		END
		ELSE
		BEGIN
			SET @STEP = '7'
			SET @SQLCmd = N'INSERT INTO ' + @TABLE_NAME
			SET @SQLCmd = @SQLCmd + N' SELECT * FROM #CARDS_BEF'
			EXECUTE sp_executesql @SQLCmd
		END
		SET @STEP = '9'
		UPDATE #CARDSREC SET CDC=7 WHERE WODATE = @D_MIS_DATE
		SET @STEP = '10'
		SET @SQLCmd = N'INSERT INTO ' + @TABLE_NAME + '(MIS_DATE,DATEAS,ID_TAG,LOCATION,ACCTNMBR,CARDNMBR,CYCLEID,CDC,CACS_STATE,BALANCE,LAST_PMT,DATE_PMT,DATE_CNTCT,WODATE,WOAMT,BP_CNT,DATE_STATE,CUSTID,PTP_AMT,PTP_DATE,RESPONCOLL,LOGO,BUSINESS) '
		SET @SQLCmd = @SQLCmd + N' SELECT MIS_DATE,DATEAS,ID_TAG,LOCATION,ACCTNMBR,CARDNMBR,CYCLEID,CDC,CACS_STATE,BALANCE,LAST_PMT,DATE_PMT,DATE_CONCT,WODATE,WOAMT,BP_CNT,DATE_STATE,CUSTID,PTP_AMT,PTP_DATE,RESPONCOLL,LOGO,BUSINESS '
		SET @SQLCmd = @SQLCmd + N' FROM #CARDSREC '
		SET @SQLCmd = @SQLCmd + N' WHERE WODATE = ''' + @DATE + ''''
		EXECUTE sp_executesql @SQLCmd
		SET @STEP = '11'
		EXEC SP_INSERT_LOG @DATE,'BC','BC處理完成','N'
		SET @OUTPUT = 'S'
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @DATE,'TRANS_BC_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

