			--TEST			
			--DECLARE @SQLCMD NVARCHAR(4000)
			--DECLARE @OUTPUT2 VARCHAR(100) = 'S;[COGDB3].DBO.CARDS'
			--DECLARE @V_MIS_DATE VARCHAR(10)='2019-03-18'

