			SET @SQLCMD = N'INSERT INTO #GEN_WO SELECT * FROM ' + RIGHT(@OUTPUT3,LEN(@OUTPUT3)-2) + ' WHERE MIS_DATE BETWEEN ''' + @V_MISDATE_FIRSTDAY + ''' AND ''' + @V_MIS_DATE + ''' '
			--PRINT @SQLCMD
