			SET @STEP = '9'
			
			IF OBJECT_ID('TEMPDB..#BADRP') IS NOT NULL DROP TABLE #BADRP
			SELECT * INTO #BADRP FROM #TEMPA3 WHERE PROD='DRP' AND CYCDUE<=6 AND PRODTYPE!='PCL'

			IF OBJECT_ID('TEMPDB..#BADRP_W') IS NOT NULL DROP TABLE #BADRP_W
			SELECT MIS_DATE, PROD, MIS_DATE AS DATEAS ,BUSINESS,PRODTYPE,'980' AS LOGO,CYCLE AS CYCLE, 6 AS CYCDUE,COUNT(*) AS ACCT, SUM(WOAMT) AS AMT
			INTO #BADRP_W
			FROM #WO_A1  WHERE PROD='DRP' 
			GROUP BY PROD, MIS_DATE,BUSINESS,PRODTYPE, CYCLE			

			SET @STEP = '10'

			IF OBJECT_ID('TEMPDB..#RW') IS NOT NULL DROP TABLE #RW
			SELECT * INTO #RW FROM #TEMPA3 WHERE PROD='RW' AND CYCDUE<=6 AND PRODTYPE!='PCL'
			
			IF OBJECT_ID('TEMPDB..#RW_W') IS NOT NULL DROP TABLE #RW_W
			SELECT MIS_DATE, PROD, MIS_DATE AS DATEAS,BUSINESS,PRODTYPE,'980' AS LOGO,CYCLE AS CYCLE, 6 AS CYCDUE,COUNT(*) AS ACCT, SUM(WOAMT) AS AMT 
			INTO #RW_W
			FROM #WO_A1  WHERE PROD='RW' 
			GROUP BY PROD, MIS_DATE,BUSINESS,PRODTYPE, CYCLE

			SET @STEP = '11'
			

			IF OBJECT_ID('TEMPDB..#CIL_W') IS NOT NULL DROP TABLE #CIL_W
			SELECT MIS_DATE, PROD, MIS_DATE AS DATEAS,BUSINESS,PRODTYPE,'757' AS LOGO,CYCLE AS CYCLE, 6 AS CYCDUE,COUNT(*) AS ACCT, SUM(WOAMT) AS AMT 
			INTO #CIL_W
			FROM #WO_A1  WHERE PRODTYPE='CTL' 
			GROUP BY PROD, MIS_DATE,BUSINESS,PRODTYPE, CYCLE
		
			SET @STEP = '12'


			UPDATE #CTL_CP SET #CTL_CP.ACCT=#CTL_CP.ACCT-A.ACCT
							 , #CTL_CP.AMT=#CTL_CP.AMT-A.AMT
			FROM #RW A
			WHERE A.LOGO=#CTL_CP.LOGO AND A.CYCLE=#CTL_CP.CYCLE AND A.CYCDUE=#CTL_CP.CYCDUE 

			UPDATE #CTL_CP SET #CTL_CP.ACCT=#CTL_CP.ACCT-A.ACCT
							 , #CTL_CP.AMT=#CTL_CP.AMT-A.AMT
			FROM #BADRP A
			WHERE A.LOGO=#CTL_CP.LOGO AND A.CYCLE=#CTL_CP.CYCLE AND A.CYCDUE=#CTL_CP.CYCDUE 

			UPDATE #CTL_CP SET #CTL_CP.ACCT=#CTL_CP.ACCT-A.ACCT
							 , #CTL_CP.AMT=#CTL_CP.AMT-A.AMT
			FROM #BAUN A
			WHERE A.LOGO=#CTL_CP.LOGO AND A.CYCLE=#CTL_CP.CYCLE AND A.CYCDUE=#CTL_CP.CYCDUE 

			UPDATE #CTL_CP SET #CTL_CP.ACCT=#CTL_CP.ACCT-A.ACCT
							 , #CTL_CP.AMT=#CTL_CP.AMT-A.AMT
			FROM #CIL A
			WHERE A.LOGO=#CTL_CP.LOGO AND A.CYCLE=#CTL_CP.CYCLE AND A.CYCDUE=#CTL_CP.CYCDUE 

			--INSERT INTO CP030D_TEMP 
			--SELECT A.MIS_DATE, A.DATEAS, 'CTL_CP030D' AS TYPE, A.LOGO, A.CYCLE, A.CYCDUE, ISNULL(A.ACCT-B.ACCT-C.ACCT-D.ACCT,0) AS ACCT, ISNULL(A.AMT-B.AMT-C.AMT-D.AMT,0) AMT, A.BUSINESS, A.PRODTYPE 
			--FROM #CTL_CP A 
			--LEFT JOIN #BAUN B ON A.LOGO=B.LOGO AND A.CYCLE=B.CYCLE AND A.CYCDUE=B.CYCDUE 
			--LEFT JOIN #DRP C ON A.LOGO=C.LOGO AND A.CYCLE=C.CYCLE AND C.CYCDUE=B.CYCDUE
			--LEFT JOIN #RW D ON A.LOGO=D.LOGO AND A.CYCLE=D.CYCLE AND A.CYCDUE=D.CYCDUE 
			
			INSERT INTO CP030D_TEMP 
			SELECT MIS_DATE, DATEAS, 'BAU' AS TYPE, LOGO, CYCLE, CYCDUE, ACCT, AMT, BUSINESS, PRODTYPE 
			FROM #CTL_CP
						
			IF OBJECT_ID('TEMPDB..#BAU_W') IS NOT NULL DROP TABLE #BAU_W
			SELECT MIS_DATE, PROD, MIS_DATE AS DATEAS,BUSINESS,PRODTYPE, LOGO,CYCLE AS CYCLE, 8 AS CYCDUE,COUNT(*) AS ACCT, SUM(WOAMT) AS AMT 
			INTO #BAU_W
			FROM #WO_A1  
			WHERE ISNULL(PROD,'')=''
			AND PRODTYPE != 'PCL' 
			GROUP BY PROD, MIS_DATE,BUSINESS,PRODTYPE, LOGO, CYCLE
						
			INSERT INTO CP030D_TEMP 
			SELECT @V_MIS_DATE, @V_MIS_DATE, 'BAU' AS TYPE, LOGO, CYCLE, CYCDUE, ACCT, AMT, '' BUSINESS, '' PRODTYPE 
			FROM #BAU_W

			SET @STEP = '13'
			
			INSERT INTO CP030D_TEMP 
			SELECT MIS_DATE, DATEAS, 'BADRP' AS TYPE,LOGO, CYCLE, CYCDUE, ACCT, AMT, '' BUSINESS, PRODTYPE 
			FROM #BADRP
			UNION
			SELECT MIS_DATE, DATEAS, 'BADRP' AS TYPE,LOGO, CYCLE, CYCDUE, ACCT, AMT, BUSINESS, PRODTYPE 
			FROM #BADRP_W

			SET @STEP = '14'

			INSERT INTO CP030D_TEMP 
			SELECT MIS_DATE, DATEAS, 'BAUN' AS TYPE,LOGO, CYCLE, CYCDUE, ACCT, AMT, BUSINESS, PRODTYPE 
			FROM #BAUN

			SET @STEP = '15'

			INSERT INTO CP030D_TEMP 
			SELECT MIS_DATE, DATEAS, 'RW' AS TYPE, LOGO, CYCLE, CYCDUE, ACCT, AMT, BUSINESS, PRODTYPE 
			FROM #RW
			UNION
			SELECT MIS_DATE, DATEAS, 'RW' AS TYPE, LOGO, CYCLE, CYCDUE, ACCT, AMT, BUSINESS, PRODTYPE 
			FROM #RW_W

			SET @STEP = '16'

			INSERT INTO CP030D_TEMP 
			SELECT MIS_DATE, DATEAS, 'CIL' AS TYPE, LOGO, CYCLE, CYCDUE, ACCT, AMT, BUSINESS,PRODTYPE 
			FROM #CIL
			UNION
			SELECT MIS_DATE, DATEAS, 'CIL' AS TYPE, LOGO, CYCLE, CYCDUE, ACCT, AMT, BUSINESS, PRODTYPE 
			FROM #CIL_W

			SET @STEP = '17'

			--去除空格
			UPDATE CP030D_TEMP SET BUSINESS=DBO.ALLTRIM(BUSINESS) 
			
			SET @STEP = '18'
			
			--DELETE FROM CP030D WHERE MIS_DATE=@V_MIS_DATE
			--INSERT INTO CP030D SELECT * FROM CP030D_TEMP
			
			--今天的月份
			DECLARE @TODAY_MM VARCHAR(2)
			SET @TODAY_MM = SUBSTRING(CONVERT(VARCHAR,GETDATE(),112),5,2)
			DECLARE @V_MIS_DATE_MM VARCHAR(2)
			DECLARE @D_MIS_DATE DATE = CAST(@V_MIS_DATE AS DATE)
			SET @V_MIS_DATE_MM = SUBSTRING(CONVERT(VARCHAR,@D_MIS_DATE,112),5,2)
			DECLARE @TABLE_NAME VARCHAR(50)
			IF (@TODAY_MM = @V_MIS_DATE_MM)
			BEGIN
				SET @TABLE_NAME = 'CP030D'
			END
			ELSE
			BEGIN
				SET @TABLE_NAME = 'CP030D_' + @V_MIS_DATE_MM
			END

			SET @SQLCMD = N'DELETE FROM ' + @TABLE_NAME + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''''
			--PRINT @SQLCMD
			EXECUTE SP_EXECUTESQL @SQLCMD

			SET @SQLCMD = N'INSERT INTO ' + @TABLE_NAME + ' SELECT * FROM CP030D_TEMP '
			--PRINT @SQLCMD
			EXECUTE SP_EXECUTESQL @SQLCMD

			
			SET @STEP = '20'

			EXEC SP_INSERT_LOG @V_MIS_DATE,'CP030D','TRANS_CP030D_SP處理完成','N'
			SET @OUTPUT = 'S'
		END


	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'CP030D',@OUTPUT,'Y'
	END CATCH

	RETURN

END
