	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	BEGIN TRY
		SET @STEP = '1'
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_FIP_CCH_sp','FIP CCH處理開始','N'
		DELETE FROM FIP_LEGAL_CODE_HIST WHERE MIS_DATE = @V_MIS_DATE
		SET @STEP = '2'
		if object_id('tempdb..#CCH') is not null drop table #CCH
		select * into #CCH from CCH where 1>2
		insert into #CCH EXEC dbo.SP_ChkTable_GetData 'CCH', @V_MIS_DATE
		SET @STEP = '3'
		if object_id('tempdb..#A1') is not null drop table #A1
		SELECT MIS_DATE, DATE_CCH AS DATEAS, USERID, LOCATION, TIME_CCH, LEFT(RTE_CCH,1) AS FA, CARDNMBR, LTR_CCH AS LTR_PMT, AC_CCH, RTE_CCH,
		CAST(Null AS numeric(5, 1)) AS MIN_CCH, CDC, CAST(CONTACT AS INT) CONTACT, CAST(PTP AS INT) PTP, CAST(CYCLEID AS int) CYCLEID, CUSTID 
		INTO #A1
		FROM #CCH 
		WHERE LEFT(LTR_CCH,2) IN (SELECT LEFT(LEGAL_CD2,2) FROM FIP_LEGAL_CODE)
		AND ISNULL(LTR_CCH,'') <> ''
		SET @STEP = '4'
