		SELECT MIS_DATE,DATEAS,USERID,LOCATION,TIME_CCH,FA,CARDNMBR,LTR_PMT,AC_CCH,RTE_CCH,MIN_CCH,CDC,CONTACT,PTP,CYCLEID,CUSTID FROM #A1
		SET @STEP = '5'
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_FIP_CCH_sp','FIP CCH處理完成','N'
		SET @OUTPUT = 'S'
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_FIP_CCH_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

