ALTER PROCEDURE [dbo].[TRANS_IDRP_BASE_sp]
	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	BEGIN TRY
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'CLIENT_MASTER',@YYYYMMDD, @OUTPUT2 output
		DECLARE @OUTPUT3 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ACCT_DAILY',@YYYYMMDD, @OUTPUT3 output
		DECLARE @OUTPUT4 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'CARD_LEVEL',@YYYYMMDD, @OUTPUT4 output
		DECLARE @OUTPUT5 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'REC_COMBO',@YYYYMMDD, @OUTPUT5 output
		DECLARE @OUTPUT6 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'BUDTRAN',@YYYYMMDD, @OUTPUT6 output
		PRINT @OUTPUT1
		PRINT @OUTPUT2
		PRINT @OUTPUT3
		PRINT @OUTPUT4
		PRINT @OUTPUT5
		PRINT @OUTPUT6
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S' and left(@OUTPUT3,1) = 'S' and left(@OUTPUT4,1) = 'S' and left(@OUTPUT5,1) = 'S' and left(@OUTPUT6,1) = 'S')
		BEGIN
			Truncate table IDRP_BASE_TEMP
			SET @STEP = '4'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','TRANS_IDRP_BASE_sp處理開始','N'
			DECLARE @ALSEOM VARCHAR(50)
			SET @ALSEOM = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @CLIENT_MASTER VARCHAR(50)
			SET @CLIENT_MASTER = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			DECLARE @ACCT_DAILY VARCHAR(50)
			SET @ACCT_DAILY = RIGHT(@OUTPUT3,LEN(@OUTPUT3)-2)
			DECLARE @CARD_LEVEL VARCHAR(50)
			SET @CARD_LEVEL = RIGHT(@OUTPUT4,LEN(@OUTPUT4)-2)
			DECLARE @REC_COMBO VARCHAR(50)
			SET @REC_COMBO = RIGHT(@OUTPUT5,LEN(@OUTPUT5)-2)
			DECLARE @BUDTRAN VARCHAR(50)
			SET @BUDTRAN = RIGHT(@OUTPUT6,LEN(@OUTPUT6)-2)
			SET @STEP = '5'
			if object_id('tempdb..#ALSEOM') is not null drop table #ALSEOM
			Select * Into #ALSEOM From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM SELECT * FROM ' + @ALSEOM + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','ALSEOM ready ok','N'
			if object_id('tempdb..#CLIENT_MASTER') is not null drop table #CLIENT_MASTER
			Select * Into #CLIENT_MASTER From CLIENT_MASTER Where 1> 2
			CREATE NONCLUSTERED INDEX IDX_CLIENT_MASTER_TEMP ON #CLIENT_MASTER(CUST_NBR ASC)
			SET @SQLCmd = N'INSERT INTO #CLIENT_MASTER SELECT * FROM ' + @CLIENT_MASTER --+ ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','CLIENT_MASTER ready ok','N'
			if object_id('tempdb..#ACCT_DAILY') is not null drop table #ACCT_DAILY
			Select * Into #ACCT_DAILY From ACCT_DAILY Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ACCT_DAILY SELECT * FROM ' + @ACCT_DAILY + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','ACCT_DAILY ready ok','N'
			if object_id('tempdb..#CARD_LEVEL') is not null drop table #CARD_LEVEL
			Select * Into #CARD_LEVEL From CARD_LEVEL Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CARD_LEVEL SELECT * FROM ' + @CARD_LEVEL + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','CARD_LEVEL ready ok','N'
			if object_id('tempdb..#REC_COMBO') is not null drop table #REC_COMBO
			Select * Into #REC_COMBO From REC_COMBO Where 1> 2
			SET @SQLCmd = N'INSERT INTO #REC_COMBO SELECT * FROM ' + @REC_COMBO + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','REC_COMBO ready ok','N'
			if object_id('tempdb..#BUDTRAN') is not null drop table #BUDTRAN
			Select * Into #BUDTRAN From BUDTRAN Where 1> 2
			SET @SQLCmd = N'INSERT INTO #BUDTRAN SELECT * FROM ' + @BUDTRAN + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd			
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','BUDTRAN ready ok','N'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','Get RMS data from REC_COMBO...','N'	
			SET @STEP = '6'			
			if object_id('tempdb..#IDRP_BASE_RMS') is not null drop table #IDRP_BASE_RMS
			SELECT MIS_DATE, 'Z1_RMS' AS SOURCE, MIS_DATE AS ASOFDATE, ACCTNMBR, CARDNMBR, 'W' AS CDC, RIGHT(dbo.ALLTRIM(CUSTID),10) AS CUSTID, ID_TAG, CUSTNAME,LOCATION
			, IIF(LOGO<>'',LOGO,ACCTTYPE) AS ACCTTYPE, PRODTYPE, WODATE, '5 ' AS ACCTSTAT, CACS_STATE, STATUS1 AS RMS_STATUS, ACCTBLOCK1, ACCTBLOCK2, CARDBLOCK, CURR_P AS PRINCIPAL, CURR_F AS FEE, CURR_I AS INT, BALANCE AS TTLBLNC
			into #IDRP_BASE_RMS
			FROM #REC_COMBO
			WHERE STATUS1 <> 'L02'
			AND ((LEFT(STATUS1,1) not in ('D','A','9')) OR (LEFT(STATUS1,2) in ('96','97','D6','D7','A6','A7')) OR (STATUS1 in ('991','99V','941','993','992','994')))
			ALTER TABLE #IDRP_BASE_RMS add CARD_IND VARCHAR(1)
			ALTER TABLE #IDRP_BASE_RMS add CYCLE VARCHAR(2)
			SET @STEP = '7'
			DECLARE @MSG VARCHAR(200)
			Update #IDRP_BASE_RMS Set #IDRP_BASE_RMS.CARD_IND=#CARD_LEVEL.CRD_IND
									, #IDRP_BASE_RMS.PRODTYPE='CC'
									, #IDRP_BASE_RMS.CARDBLOCK=#CARD_LEVEL.CRD_BLK+#CARD_LEVEL.CRD_BLK_RS
			From #CARD_LEVEL
			Where #IDRP_BASE_RMS.CARDNMBR=#CARD_LEVEL.CARDNMBR
			And #CARD_LEVEL.CRD_IND='P'
			SET @MSG = 'RMS data Update CRD_IND=P from CARD_LEVEL, ACCT Count: ' + cast(@@ROWCOUNT as varchar)
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE',@MSG,'N'	
			DECLARE @ACCTNMBR VARCHAR(16)
			DECLARE @CARDNMBR VARCHAR(16)
			DECLARE @CRD_IND VARCHAR(1)
			DECLARE @CARDBLOCK VARCHAR(3)
			DECLARE @CUST_NBR VARCHAR(16)
			DECLARE @CNT INT
			select @CNT = Count(a.ACCTNMBR)
			from #CARD_LEVEL a
			inner join #IDRP_BASE_RMS b on a.CARDNMBR=b.CARDNMBR
			where isnull(a.CRD_IND,'') <> 'P'
			SET @MSG = 'Get RMS data CRD_IND <> P from CARD_LEVEL, ACCT Count: ' + cast(@CNT as varchar)
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE',@MSG,'N'	
			DECLARE CURSOR_IDRP_BASE_RMS CURSOR FAST_FORWARD FOR
				select a.ACCTNMBR, a.CARDNMBR, a.CRD_IND, a.CRD_BLK+a.CRD_BLK_RS CARDBLOCK, a.CUST_NBR
				from #CARD_LEVEL a
				inner join #IDRP_BASE_RMS b on a.CARDNMBR=b.CARDNMBR
				where isnull(a.CRD_IND,'') <> 'P'
			OPEN CURSOR_IDRP_BASE_RMS
			FETCH NEXT FROM CURSOR_IDRP_BASE_RMS INTO @ACCTNMBR, @CARDNMBR, @CRD_IND, @CARDBLOCK, @CUST_NBR
			WHILE @@FETCH_STATUS = 0
			BEGIN
					SET @STEP = '10'
					Select @CNT=Count(*) 
					From #CLIENT_MASTER Where CUST_NBR=@CUST_NBR
					IF (@CNT>0)
					BEGIN
						SET @STEP = '11'
						DECLARE @IDNO VARCHAR(15)
						DECLARE @ID_TAG VARCHAR(10)
						Select @IDNO=IDNO, @ID_TAG=ID_TAG From #CLIENT_MASTER Where CUST_NBR=@CUST_NBR
						INSERT INTO #IDRP_BASE_RMS(MIS_DATE,SOURCE,ASOFDATE,ACCTNMBR,CARDNMBR,CARD_IND,PRODTYPE,CARDBLOCK,CUSTID,ID_TAG)
						VALUES(@V_MIS_DATE,'Z1_RMS',@V_MIS_DATE,@ACCTNMBR,@CARDNMBR,@CRD_IND,'CC',@CARDBLOCK,@IDNO,@ID_TAG)
					END
					ELSE
					BEGIN
						SET @STEP = '12'
						INSERT INTO #IDRP_BASE_RMS(MIS_DATE,SOURCE,ASOFDATE,ACCTNMBR,CARDNMBR,CARD_IND,PRODTYPE,CARDBLOCK)
						VALUES(@V_MIS_DATE,'Z1_RMS',@V_MIS_DATE,@ACCTNMBR,@CARDNMBR,@CRD_IND,'CC',@CARDBLOCK)
					END
				SET @STEP = '13'
				FETCH NEXT FROM CURSOR_IDRP_BASE_RMS INTO @ACCTNMBR, @CARDNMBR, @CRD_IND, @CARDBLOCK, @CUST_NBR
			END
			CLOSE CURSOR_IDRP_BASE_RMS
			DEALLOCATE CURSOR_IDRP_BASE_RMS
			SET @STEP = '14'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','RMS data mapping ACCT_DAILY...','N'	
			Update #IDRP_BASE_RMS Set #IDRP_BASE_RMS.CYCLE=#ACCT_DAILY.CYCLE
									, #IDRP_BASE_RMS.ACCTBLOCK1=#ACCT_DAILY.BLK1+#ACCT_DAILY.RSN1
									, #IDRP_BASE_RMS.ACCTBLOCK2=#ACCT_DAILY.BLK2+#ACCT_DAILY.RSN2
			From #ACCT_DAILY
			Where #ACCT_DAILY.ACCTNMBR=#IDRP_BASE_RMS.ACCTNMBR
			And CAST(Left(#IDRP_BASE_RMS.CARDNMBR,2) AS INT) >= 36
			SET @STEP = '15'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','Get ALS data from ALSEOM...','N'	
			if object_id('tempdb..#IDRP_BASE_ALS') is not null drop table #IDRP_BASE_ALS
			SELECT MIS_DATE,'A1_ALSEOM' AS SOURCE,
			ASOFDATE,ACCTNMBR,CARDNMBR,PRODTYPE,LOANSTAT AS ACCTSTAT,ACCTTYPE,ENRPRIN AS PRINCIPAL,RESAMT AS RESERVE,LATECGL AS FEE,UNINT AS INT,
			ENRPRIN+LATECGL+UNINT AS TTLBLNC,BILLDAY AS CYCLE,dbo.ALLTRIM(PMTDUE) AS CDC, CUSTID,ID_TAG
			into #IDRP_BASE_ALS
			FROM #ALSEOM
			WHERE LOANSTAT='AC' 
			AND PRODTYPE NOT IN ('TDBB','TDP')
			AND ACCTNMBR NOT IN (Select ACCTNMBR FROM #IDRP_BASE_RMS)
			SET @STEP = '16'
			ALTER TABLE #IDRP_BASE_ALS ADD CACS_STATE VARCHAR(3)
			ALTER TABLE #IDRP_BASE_ALS ADD LOCATION VARCHAR(8)
			ALTER TABLE #IDRP_BASE_ALS ADD CUSTNAME NVARCHAR(44)
			ALTER TABLE #IDRP_BASE_ALS ADD WODATE DATE
			ALTER TABLE #IDRP_BASE_ALS ADD RMS_STATUS VARCHAR(3)
			ALTER TABLE #IDRP_BASE_ALS ADD ACCTBLOCK1 VARCHAR(3)
			ALTER TABLE #IDRP_BASE_ALS ADD ACCTBLOCK2 VARCHAR(3)
			SET @STEP = '17'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','ALS data mapping BUDTRAN...','N'	
			Update #IDRP_BASE_ALS Set #IDRP_BASE_ALS.CACS_STATE=#BUDTRAN.CACS_STATE
									, #IDRP_BASE_ALS.LOCATION=#BUDTRAN.LOCATION
									, #IDRP_BASE_ALS.CUSTNAME=Replace(#BUDTRAN.custname,',','')
			From #BUDTRAN
			Where #BUDTRAN.CARDNMBR=#IDRP_BASE_ALS.CARDNMBR
			SET @STEP = '18'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','ALS data mapping REC_COMBO...','N'
			Update #IDRP_BASE_ALS Set #IDRP_BASE_ALS.CDC='W'
									, #IDRP_BASE_ALS.WODATE=#REC_COMBO.WODATE
									, #IDRP_BASE_ALS.RMS_STATUS=#REC_COMBO.STATUS1
									, #IDRP_BASE_ALS.ACCTBLOCK1=#REC_COMBO.ACCTBLOCK1
									, #IDRP_BASE_ALS.ACCTBLOCK2=#REC_COMBO.ACCTBLOCK2
									, #IDRP_BASE_ALS.LOCATION=#REC_COMBO.LOCATION
									, #IDRP_BASE_ALS.CUSTNAME=Replace(#REC_COMBO.custname,',','')
			From #REC_COMBO
			Where #REC_COMBO.CARDNMBR=#IDRP_BASE_ALS.CARDNMBR
			SET @STEP = '19'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','Get A data from ACCT_DAILY...','N'
			if object_id('tempdb..#ACCT_DAILY1') is not null drop table #ACCT_DAILY1
			SELECT ACCTNMBR,
			dbo.ALLTRIM(IIF(CYC_DUE<=1,0,CYC_DUE-1)) AS CDC,
			WODATE,
			CYCLE,
			BLK1+RSN1 AS ACCTBLOCK1,
			BLK2+RSN2 AS ACCTBLOCK2,
			BALANCE AS TTLBLNC
			into #ACCT_DAILY1
			FROM #ACCT_DAILY
			WHERE (STATUS1 IN ('A','D','Z') OR (STATUS1 IN ('T') AND (BALANCE>0 OR dbo.ALLTRIM(BLK1) NOT IN ('A','B','U','L','F','') OR dbo.ALLTRIM(BLK2) NOT IN ('A','B','U','L','F',''))))
			AND BLK1!='W'
			SET @STEP = '20'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','Get B data from CARD_LEVEL...','N'
			if object_id('tempdb..#CARD_LEVEL1') is not null drop table #CARD_LEVEL1
			SELECT ACCTNMBR,
			CARDNMBR,
			CRD_IND AS CARD_IND,
			CRD_BLK+CRD_BLK_RS AS CARDBLOCK,
			'CC' AS PRODTYPE,
			LOGO AS ACCTTYPE,
			CUST_NBR,
			CLNT_NBR
			into #CARD_LEVEL1
			FROM #CARD_LEVEL
			WHERE ACCTNMBR IN (SELECT ACCTNMBR FROM #ACCT_DAILY1)
			SET @STEP = '21'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','Get C data from CLIENT_MASTER...','N'
			if object_id('tempdb..#CLIENT_MASTER1') is not null drop table #CLIENT_MASTER1
			SELECT IDNO AS CUSTID,ID_TAG,CUST_NBR,CLNT_NBR
			into #CLIENT_MASTER1
			FROM #CLIENT_MASTER
			WHERE CUST_NBR IN (SELECT CUST_NBR FROM #CARD_LEVEL1)
			SET @STEP = '22'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','Get ECS data from A/B/C...','N'
			if object_id('tempdb..#IDRP_BASE_ECS') is not null drop table #IDRP_BASE_ECS
			SELECT @V_MIS_DATE MIS_DATE,
			'E1_ECS_D' AS SOURCE,
			@V_MIS_DATE AS ASOFDATE,
			A.*,
			B.CDC,B.WODATE,B.CYCLE,B.ACCTBLOCK1,B.ACCTBLOCK2,B.TTLBLNC,
			C.CUSTID,C.ID_TAG
			into #IDRP_BASE_ECS
			FROM #CARD_LEVEL1 A
			LEFT JOIN #ACCT_DAILY1 B ON A.ACCTNMBR=B.ACCTNMBR
			LEFT JOIN #CLIENT_MASTER1 C ON A.CUST_NBR=C.CUST_NBR and A.CLNT_NBR=C.CLNT_NBR
			SET @STEP = '23'
			SELECT @CNT = Count(*)
			FROM #IDRP_BASE_ECS WHERE isnull(CUSTID,'')='' AND ACCTNMBR !='4563139423176947'
			IF(@CNT > 0)
			BEGIN
				EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','ECS CUSTID mapping IDRP_BASE old data...','N'
				SET @STEP = '24'
				Update #IDRP_BASE_ECS Set #IDRP_BASE_ECS.CUSTID=IDRP_BASE.CUSTID
						  				 , #IDRP_BASE_ECS.ID_TAG=IDRP_BASE.ID_TAG
				From IDRP_BASE
				Where #IDRP_BASE_ECS.acctnmbr=IDRP_BASE.acctnmbr
				and (isnull(#IDRP_BASE_ECS.CUSTID,'')='' or isnull(#IDRP_BASE_ECS.ID_TAG,'')='')
				/*
				Select a.CUSTID,a.ID_TAG,b.CUSTID,b.ID_TAG
				From IDRP_BASE a
				inner join #IDRP_BASE_ECS b on a.acctnmbr=b.acctnmbr
				Where (isnull(b.CUSTID,'')='' or isnull(b.ID_TAG,'')='')
				and a.MIS_DATE=convert(varchar,DATEADD(DD,-1,CAST('20190113' AS DATE)),112)
				*/
			END
			SET @STEP = '25'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','Gen IDRP_BASE_TEMP data','N'
			Insert into IDRP_BASE_TEMP(MIS_DATE,SOURCE,ASOFDATE,ACCTNMBR,CARDNMBR,CDC,CUSTID,ID_TAG,CUSTNAME,LOCATION,ACCTTYPE,PRODTYPE,WODATE,ACCTSTAT,CACS_STATE,RMS_STATUS,ACCTBLOCK1,ACCTBLOCK2,CARDBLOCK,PRINCIPAL,FEE,INT,TTLBLNC,CARD_IND,CYCLE,RESERVE)
			Select MIS_DATE,SOURCE,ASOFDATE,ACCTNMBR,CARDNMBR,CDC,CUSTID,ID_TAG,CUSTNAME,LOCATION,ACCTTYPE,PRODTYPE,WODATE,ACCTSTAT,CACS_STATE,RMS_STATUS,ACCTBLOCK1,ACCTBLOCK2,'' CARDBLOCK,PRINCIPAL,FEE,INT,TTLBLNC,'' CARD_IND,CYCLE, RESERVE  
			From #IDRP_BASE_ALS
			UNION
			Select MIS_DATE,SOURCE,ASOFDATE,ACCTNMBR,CARDNMBR,CDC,CUSTID,ID_TAG,'' CUSTNAME,'' LOCATION,ACCTTYPE,PRODTYPE,WODATE,'' ACCTSTAT,'' CACS_STATE,'' RMS_STATUS,ACCTBLOCK1,ACCTBLOCK2,CARDBLOCK,0 PRINCIPAL,0 FEE,0 INT,TTLBLNC,CARD_IND,CYCLE, 0 RESERVE  
			From #IDRP_BASE_ECS
			UNION
			Select MIS_DATE,SOURCE,ASOFDATE,ACCTNMBR,CARDNMBR,CDC,CUSTID,ID_TAG,CUSTNAME,LOCATION,ACCTTYPE,PRODTYPE,WODATE,ACCTSTAT,CACS_STATE,RMS_STATUS,ACCTBLOCK1,ACCTBLOCK2,CARDBLOCK,PRINCIPAL,FEE,INT,TTLBLNC,isnull(CARD_IND,'') CARD_IND,isnull(CYCLE,'') CYCLE, 0 RESERVE 
			From #IDRP_BASE_RMS
			SET @STEP = '26'
			Update IDRP_BASE_TEMP Set PRODTYPE='CC' Where left(ACCTNMBR,2)='36' AND PRODTYPE='DC'
			Update IDRP_BASE_TEMP Set PRODTYPE='MP' Where PRODTYPE='ACT'
			Update IDRP_BASE_TEMP Set PRODTYPE='CC' Where PRODTYPE='VC'
			SET @STEP = '30'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE','TRANS_IDRP_BASE_sp處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', DATASET尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'IDRP_BASE',@OUTPUT,'Y'
	END CATCH
	RETURN
END

