ALTER PROCEDURE [dbo].[TRANS_PAYMENT_sp]
	@DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(2)
	DECLARE @GLACCT VARCHAR(16)
	DECLARE @ACCTNMBR VARCHAR(16)
	DECLARE @CARDNMBR VARCHAR(18)
	DECLARE	@OA VARCHAR(4)
	DECLARE	@CUSTNAME NVARCHAR(30)
	DECLARE	@TXSTATUS VARCHAR(3)
	DECLARE	@WODATE	DATE
	DECLARE	@WOAMT NUMERIC(11,2)
	DECLARE	@CACS_STATE	VARCHAR(3)
	DECLARE	@PRODTYPE VARCHAR(5)
	DECLARE	@BUSINESS VARCHAR(8) 
	DECLARE @DESCR VARCHAR(50)
	DECLARE @TXCODE VARCHAR(2)
	DECLARE CURSOR_PAYMENT_TEMP CURSOR FAST_FORWARD FOR
	  SELECT GLACCT,ACCTNMBR
	  FROM dbo.PAYMENT_TEMP
	  WHERE MIS_DATE=@DATE
	BEGIN TRY
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp','開始處理PAYMENT_TEMP CURSOR','N'
		OPEN CURSOR_PAYMENT_TEMP
		FETCH NEXT FROM CURSOR_PAYMENT_TEMP INTO @GLACCT, @ACCTNMBR
		WHILE @@FETCH_STATUS = 0
		BEGIN
			SET @CARDNMBR = ''
			SET @OA = ''
			SET @CUSTNAME = ''
			SET @TXSTATUS = ''
			SET @WODATE = Null
			SET @WOAMT = Null
			SET @CACS_STATE = ''
			SET @PRODTYPE = ''
			SET @BUSINESS = ''
			SET @DESCR = ''
			SET @TXCODE = ''
			SET @STEP = '1'	-- 用ACCT取得卡號
			Select @CARDNMBR = CARDNMBR From CACS_EXT_ECMS where mis_date=@DATE and acctnmbr=@ACCTNMBR
			SET @STEP = '2'	-- 用ACCT取得卡號
			Select @DESCR=[DESC], @TXCODE='03' From GL_I where GLACCT=@GLACCT
			Select @DESCR=[DESC], @TXCODE='02' From GL_P where GLACCT=@GLACCT
			SET @STEP = '3'	-- 用卡號取得其他資料
			Select  @OA	= OA,
					@CUSTNAME = CUSTNAME,
					@TXSTATUS = STATUS1,
					@WODATE	= WODATE,
					@WOAMT = WOAMT,
					@CACS_STATE	= CACS_STATE,
					@PRODTYPE = PRODTYPE,
					@BUSINESS = BUSINESS
			From REC_COMBO
			Where mis_date=@DATE and cardnmbr=@CARDNMBR
			SET @STEP = '4'	-- 更新回TEMP主檔
			Update PAYMENT_TEMP Set CARDNMBR = @CARDNMBR,
									OA = @OA,
									CUSTNAME = @CUSTNAME,
									TXSTATUS = @TXSTATUS,
									WODATE	= @WODATE,
									WOAMT = @WOAMT,
									CACS_STATE = @CACS_STATE,
									PRODTYPE = @PRODTYPE,
									BUSINESS = @BUSINESS,
									DESCR = @DESCR,
									TXCODE = @TXCODE
			Where MIS_DATE=@DATE and ACCTNMBR=@ACCTNMBR And GLACCT=@GLACCT	--BY GLACCT UPDATE
			FETCH NEXT FROM CURSOR_PAYMENT_TEMP INTO @GLACCT, @ACCTNMBR
		END
		CLOSE CURSOR_PAYMENT_TEMP
		DEALLOCATE CURSOR_PAYMENT_TEMP
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp','開始處理TTL_AMT資料','N'
		SET @STEP = '5'	-- 處理TTL_AMT
		if object_id('tempdb..#TTL_AMT') is not null drop table #TTL_AMT
		CREATE TABLE #TTL_AMT
		(
			[MIS_DATE] [date] NOT NULL,
			[REPORTDATE] [date] NOT NULL,
			[ASOFDATE] [date] NOT NULL,
			[TRN_CD] [varchar](2) NOT NULL,
			[GLACCT] [varchar](10) NOT NULL,
			[OA] [varchar](4) NULL,
			[CUSTNAME] [varchar](18) NULL,
			[ACCTNMBR] [varchar](16) NOT NULL,
			[CARDNMBR] [varchar](16) NULL,
			[DATEPOST] [date] NULL,
			[TXCODE] [varchar](2) NULL,
			[TRANCODE] [varchar](3) NOT NULL,
			[DESCR] [varchar](35) NULL,
			[AMT] [decimal](12, 2) NOT NULL,
			[TXSTATUS] [varchar](3) NULL,
			[LOANTYPE] [varchar](4) NULL,
			[WODATE] [date] NULL,
			[WOAMT] [decimal](12, 2) NULL,
			[CACS_STATE] [varchar](3) NULL,
			[PRODTYPE] [varchar](3) NULL,
			[BUSINESS] [varchar](8) NULL,
			AMT_02 NUMERIC(14,2)
		)
		insert into #TTL_AMT
		Select *, (Select SUM(AMT) FROM PAYMENT_TEMP Where TXCODE='02') AMT_02
		From PAYMENT_TEMP Where TXCODE='02'
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp','開始處理AMT03資料','N'
		SET @STEP = '6'	-- 處理AMT03
		if object_id('tempdb..#AMT03') is not null drop table #AMT03
		CREATE TABLE #AMT03
		(
			[MIS_DATE] [date] NOT NULL,
			[REPORTDATE] [date] NOT NULL,
			[ASOFDATE] [date] NOT NULL,
			[TRN_CD] [varchar](2) NOT NULL,
			[GLACCT] [varchar](10) NOT NULL,
			[OA] [varchar](4) NULL,
			[CUSTNAME] [varchar](18) NULL,
			[ACCTNMBR] [varchar](16) NOT NULL,
			[CARDNMBR] [varchar](16) NULL,
			[DATEPOST] [date] NULL,
			[TXCODE] [varchar](2) NULL,
			[TRANCODE] [varchar](3) NOT NULL,
			[DESCR] [varchar](35) NULL,
			[AMT] [decimal](12, 2) NOT NULL,
			[TXSTATUS] [varchar](3) NULL,
			[LOANTYPE] [varchar](4) NULL,
			[WODATE] [date] NULL,
			[WOAMT] [decimal](12, 2) NULL,
			[CACS_STATE] [varchar](3) NULL,
			[PRODTYPE] [varchar](3) NULL,
			[BUSINESS] [varchar](8) NULL,
			AMT_03 NUMERIC(14,2)
		)
		insert into #AMT03
		Select *, (Select SUM(AMT) FROM PAYMENT_TEMP Where TXCODE='03') AMT_03
		From PAYMENT_TEMP Where TXCODE='03'
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp','開始處理TMEP資料','N'
		SET @STEP = '7'	-- 處理TEMP
		if object_id('tempdb..#TEMP') is not null drop table #TEMP
		Create table #TEMP
		(
			[MIS_DATE] [date] NOT NULL,
			[REPORTDATE] [date] NOT NULL,
			[ASOFDATE] [date] NOT NULL,
			[TRN_CD] [varchar](2) NOT NULL,
			[GLACCT] [varchar](10) NOT NULL,
			[OA] [varchar](4) NULL,
			[CUSTNAME] [varchar](18) NULL,
			[ACCTNMBR] [varchar](16) NOT NULL,
			[CARDNMBR] [varchar](16) NULL,
			[DATEPOST] [date] NULL,
			[TXCODE] [varchar](2) NULL,
			[TRANCODE] [varchar](3) NOT NULL,
			[DESCR] [varchar](35) NULL,
			[AMT] [decimal](12, 2) NOT NULL,
			[TXSTATUS] [varchar](3) NULL,
			[LOANTYPE] [varchar](4) NULL,
			[WODATE] [date] NULL,
			[WOAMT] [decimal](12, 2) NULL,
			[CACS_STATE] [varchar](3) NULL,
			[PRODTYPE] [varchar](3) NULL,
			[BUSINESS] [varchar](8) NULL,
			AMT_02 NUMERIC(14,2),
			AMT_03 NUMERIC(14,2),
			PRINCIPAL NUMERIC(14,0),
			BALANCE NUMERIC(14,0),
			AMT_02_1 NUMERIC(14,0),
			AMT_05 NUMERIC(14,0)
		)		
		Insert into #TEMP
		select a.* ,b.AMT_03,c.PRINCIPAL, c.BALANCE,0,0
		from #TTL_AMT a
		left join #AMT03 b on a.CARDNMBR=b.CARDNMBR
		left join CACS_EXT_ECMS c on a.CARDNMBR=c.CARDNMBR where c.MIS_DATE = @DATE
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp','TEMP資料邏輯更新','N'
		SET @STEP = '8'
		UPDATE #TEMP SET AMT_05 = AMT_02 WHERE PRINCIPAL <= 0 AND TRN_CD='62'  AND AMT_02 >= 1
		UPDATE #TEMP SET AMT_05 = AMT_02-PRINCIPAL, AMT_02_1=PRINCIPAL WHERE PRINCIPAL>0 AND AMT_02-PRINCIPAL>=1 AND TRN_CD='62'
		UPDATE #TEMP SET  AMT_02_1 = AMT_02 WHERE PRINCIPAL>0 AND AMT_02-PRINCIPAL<1 OR (AMT_02 < 1 AND AMT_02>0)
		UPDATE #TEMP SET AMT_02_1 = AMT_02 WHERE TRN_CD='61' AND PRINCIPAL > 0
		UPDATE #TEMP SET AMT_05 = AMT_02 WHERE TRN_CD='61' AND PRINCIPAL <= 0
		SET @STEP = '9'
		Truncate Table PAYMENT_TEMP
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp','重新彙整PAYMENT_TEMP','N'
		SET @STEP = '10'
		Insert into PAYMENT_TEMP
		SELECT @DATE,REPORTDATE,ASOFDATE,TRN_CD,GLACCT,OA,CUSTNAME,ACCTNMBR,CARDNMBR,DATEPOST, '02' AS TXCODE,TRANCODE,DESCR,
		AMT_02_1 AS AMT,TXSTATUS,LOANTYPE,WODATE,WOAMT,CACS_STATE,PRODTYPE,BUSINESS
		FROM #TEMP 
		WHERE AMT_02_1>0 OR (TRN_CD='61' and principal > 0 )
		UNION
		SELECT @DATE,REPORTDATE,ASOFDATE,TRN_CD,GLACCT,OA,CUSTNAME,ACCTNMBR,CARDNMBR,DATEPOST, '03' AS TXCODE,TRANCODE,DESCR,
		AMT_03 AS AMT,TXSTATUS,LOANTYPE,WODATE,WOAMT,CACS_STATE,PRODTYPE,BUSINESS
		FROM  #AMT03
		UNION
		SELECT @DATE,REPORTDATE,ASOFDATE,TRN_CD,GLACCT,OA,CUSTNAME,ACCTNMBR,CARDNMBR,DATEPOST, '05' AS TXCODE,TRANCODE,DESCR,
		AMT_05 AS AMT,TXSTATUS,LOANTYPE,WODATE,WOAMT,CACS_STATE,PRODTYPE,BUSINESS
		FROM #TEMP 
		WHERE AMT_05>0  OR (TRN_CD='61' and principal <= 0 ) 
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp','PAYMENT_TEMP 開始寫入 PAYMENT','N'
		SET @STEP = '20'	-- TEMP寫回主檔	
		Delete From PAYMENT Where MIS_DATE=@DATE 	
		INSERT INTO PAYMENT SELECT * FROM PAYMENT_TEMP
		SET @STEP = '21'
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp','PAYMENT匯入完成','N'
		SET @OUTPUT = 'S'
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @DATE,'TRANS_PAYMENT_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

