ALTER PROCEDURE [dbo].[TRANS_PROD_CCH_sp]
	@asofdate varchar(12)
AS
BEGIN
	SET NOCOUNT ON;
	Declare @wasdate date
	Declare @strsql varchar(800)
	SET @wasdate = cast(@asofdate as date)
	select * into #_CCH_DAILY from CCH where date_cch = cast(@asofdate as date)
	if (select count(*) from #_CCH_DAILY)>0
		begin
			PRINT '1-Get CCH data'
			SELECT * INTO #_CCHD FROM PROD_CCH WHERE 1>2
			INSERT INTO #_CCHD ([mis_date],[date_cch],[time_cch],[location],[acctnmbr],[cardnmbr],[pmt_cch],[ltr_cch],[ac_cch],[p_cch],[c_cch],[userid],[dte_ptp1],[amt_ptp1],[dte_ptp2],[amt_ptp2],[ex_cch],[rte_cch],[cdc],[balance],[cycleid],[contact],[ptp],[custid],[id_tag],[logo],[accttype],[business],[prod_coll]) SELECT [mis_date],[date_cch],[time_cch],[location],[acctnmbr],[cardnmbr],[pmt_cch],[ltr_cch],[ac_cch],[p_cch],[c_cch],[userid],[dte_ptp1],[amt_ptp1],[dte_ptp2],[amt_ptp2],[ex_cch],[rte_cch],[cdc],[balance],[cycleid],[contact],[ptp],[custid],[id_tag],[logo],[accttype],[business],[prodtype] as[prod_coll]  FROM #_CCH_DAILY
			PRINT '2-MAP CARDS_DBF,ALSEOM,REC_COMBO'
			SELECT 'Mapping Data From CACS (date_cch-1)'; --為了呈現出CCH當下CACS的長相
			WITH was_cards AS (SELECT * FROM CARDS WHERE mis_date = @wasdate) 
			Update #_CCHD SET #_CCHD.USERID = IIF(#_CCHD.ac_cch = 'PY' ,b.Responcoll,#_CCHD.USERID),#_CCHD.CDC_PTP = b.CDC,#_CCHD.cycleid = b.CDC,#_CCHD.balance=b.balance,#_CCHD.wodate=b.wodate,#_CCHD.woamt=b.woamt,#_CCHD.b_score=b.b_score,#_CCHD.dlqsty=b.dlqsty,#_CCHD.dlqsce=b.dlqsce,#_CCHD.dlqcol=b.dlqcol,#_CCHD.bar=b.bar,#_CCHD.ttl_due = iif(#_CCHD.cdc=0,b.ttl_due,#_CCHD.ttl_due) FROM was_cards b WHERE #_CCHD.acctnmbr = b.acctnmbr;
			WITH was_carec AS (SELECT * FROM CARDSREC WHERE mis_date = @wasdate)
			Update #_CCHD SET #_CCHD.USERID = IIF(#_CCHD.ac_cch = 'PY' ,b.Responcoll,#_CCHD.USERID),#_CCHD.CDC_PTP = b.CDC,#_CCHD.cycleid = b.CDC,#_CCHD.balance=b.balance,#_CCHD.wodate=b.wodate,#_CCHD.woamt=b.woamt FROM was_carec b WHERE #_CCHD.acctnmbr = b.acctnmbr;
			WITH was_als AS (SELECT * FROM ALSEOM WHERE mis_date = @wasdate)
			Update #_CCHD SET #_CCHD.als_nbv =b.nbv,#_CCHD.cdc_ptp = b.cdc  FROM was_als b WHERE #_CCHD.acctnmbr = b.acctnmbr;
			WITH was_bud AS (SELECT * FROM BUDTRAN WHERE mis_date = @wasdate)
			Update #_CCHD SET #_CCHD.USERID = IIF(#_CCHD.ac_cch = 'PY' ,b.Responcoll,#_CCHD.USERID),#_CCHD.CDC_PTP = b.CDC,#_CCHD.cycleid = b.CDC,#_CCHD.balance=b.balance,#_CCHD.principal=b.principal,#_CCHD.date_delq = b.date_delq,#_CCHD.b_score =b.bscore,#_CCHD.bk_data4=b.bk_data4,#_CCHD.Nonstarter=b.Nonstarter,#_CCHD.ttl_due = iif(b.location=0 and b.cdc=0,b.instlamt,#_CCHD.ttl_due) FROM was_bud b WHERE #_CCHD.acctnmbr = b.acctnmbr;
			WITH was_asrec AS (SELECT * FROM ASSETREC WHERE mis_date = @wasdate)
			Update #_CCHD SET #_CCHD.USERID = IIF(#_CCHD.ac_cch = 'PY' ,b.Responcoll,#_CCHD.USERID),#_CCHD.CDC_PTP = b.CDC,#_CCHD.cycleid = b.cycleid,#_CCHD.balance = b.balance,#_CCHD.principal=b.principal FROM was_asrec b WHERE #_CCHD.acctnmbr=b.acctnmbr;
			WITH was_RMS AS (SELECT * FROM REC_COMBO WHERE mis_date = @asofdate)
			Update #_CCHD SET #_CCHD.wodate = b.wodate,#_CCHD.woamt = b.woamt FROM was_RMS b WHERE #_CCHD.acctnmbr = b.acctnmbr;
			SELECT 'UPD_DLQSCE'
			Update #_CCHD SET DLQSCE ='153' WHERE location='860103' and NONSTARTER='N' and Convert(int,b_score) between 1 and 539  --HR
			Update #_CCHD SET DLQSCE ='155' WHERE location='860103' and NONSTARTER='N' and Convert(int,b_score) > 539 --LR
			Update #_CCHD SET DLQSCE ='154' WHERE location='860103' and NONSTARTER='N' and Convert(int,b_score) =0 --BAU
			Update #_CCHD SET DLQSCE ='157' WHERE location='860103' and NONSTARTER='Y' --NS
			Update #_CCHD SET DLQSCE ='153' WHERE location='860104' and NONSTARTER='N' and Convert(int,b_score) between 1 and 512  --HR
			Update #_CCHD SET DLQSCE ='155' WHERE location='860104' and NONSTARTER='N' and Convert(int,b_score) > 512 --LR
			Update #_CCHD SET DLQSCE ='154' WHERE location='860104' and NONSTARTER='N' and Convert(int,b_score) =0 --BAU
			Update #_CCHD SET DLQSCE ='157' WHERE location='860104' and NONSTARTER='Y' --NS
			PRINT '3-UPDATE CARDNMBR TRANSFER'
			PRINT '4-UPDATE THE TIME OF SYSTEM CODE'
			Update #_CCHD SET time_cch ='2359' WHERE userid is null or userid='' or userid='AUTO'
			Update #_CCHD SET time_cch ='2358' WHERE AC_CCH='CR'
			Update #_CCHD SET time_cch ='2357' WHERE AC_CCH='PY'
			PRINT ' 5-CLEAN COLUMNS(PTP,BP,KP,REJECT,INDI01,DATE_PMT)'
			Update #_CCHD SET CONTACT = 0, PTP = 0, BP = 0, KP = 0, REJECT = 0, INDI01 = 0, DATE_PMT = null
			PRINT '6-UPDATE CALL,ATTEMPT,CONTACT,REJECT,PTP'
			Update #_CCHD SET call=1 WHERE AC_CCH IN('IC','OC','OM','IM')
			Update #_CCHD SET attempt = 1 WHERE USERID<>null and ac_cch <>'SP'
			Update #_CCHD SET contact = 1 WHERE AC_CCH IN('IC','OC','OM','IM') and C_CCH IN('A','C','Y','D','T','W')
			Update #_CCHD SET contact = 1 WHERE C_CCH IN ('A','C','Y','D','T','W') and amt_ptp1 >0 and dte_ptp1 <> null
			Update #_CCHD SET reject = 1 WHERE AC_CCH = 'RV' and P_CCH ='X' and C_CCH='D'
			Update #_CCHD SET ptp = 1 WHERE amt_ptp1>0 and dte_ptp1 <> null and C_CCH IN('A','C','Y','D','T','W')
			PRINT '7-APPEND CCHD TO CCHX_YYMM.DBF & PROD_CCHC.DBF'
			SELECT * INTO #_CCHD1 FROM #_CCHD  WHERE AMT_PTP1=0 and DTE_PTP1 is null and AC_CCH NOT IN('PY','BP','NG','P2') and RTE_CCH<>'I01' AND REJECT<>1
			DELETE FROM TPROD_CCHX WHERE DATE_CCH = Cast(@asofdate as date)
			INSERT INTO TPROD_CCHX Select * FROM #_CCHD1
			SELECT * INTO #_CCHD2 FROM #_CCHD WHERE AMT_PTP1!=0 OR DTE_PTP1 <> null OR AC_CCH IN('PY','BP','NG','P2') OR RTE_CCH='I01' OR REJECT=1
			DELETE FROM TPROD_CCHC WHERE DATE_CCH = Cast(@asofdate as date) or DATEDIFF(DAY,date_cch,Cast(@asofdate as  date)) >60
			INSERT INTO TPROD_CCHC SELECT * FROM #_CCHD2
			PRINT '8-取未逾期PTP RECORD存成CCHC(ORDER BY ACCTNMBR,DATE_CCH,TIME_CCH,AC_CCH)'
			SELECT * into #_temp_wo FROM TPROD_CCHC WHERE DATEDIFF(DAY,date_cch,Cast(@asofdate as date))>35 and woamt >0 
			SELECT DISTINCT acctnmbr INTO #_PY_22 FROM TPROD_CCHC WHERE substring(RTRIM(LTRIM(RTE_CCH)),2,2)='22' and AC_CCH='PY'
			SELECT * INTO #_temp_2thdrp FROM TPROD_CCHC WHERE acctnmbr IN (SELECT acctnmbr FROM #_PY_22) and DATEDIFF(DAY,date_cch,cast(@asofdate as date)) >35 and woamt =0
			SELECT DISTINCT acctnmbr INTO #_PY_Y FROM TPROD_CCHC WHERE substring(LTRIM(RTRIM(RTE_CCH)),1,1)='Y' and AC_CCH='PY' and acctnmbr NOT IN (SELECT ACCTNMBR FROM #_PY_22)
			SELECT * INTO #_temp_lead FROM TPROD_CCHC WHERE acctnmbr IN (SELECT acctnmbr FROM #_PY_Y) AND DATEDIFF(DAY,date_cch,Cast(@asofdate as date))>35 and woamt =0 
			SELECT * INTO #_CCHC FROM TPROD_CCHC WHERE DATEDIFF(DAY,date_cch,Cast(@asofdate as date)) <=35 
			INSERT INTO #_CCHC SELECT * FROM #_temp_wo
			INSERT INTO #_CCHC SELECT * FROM #_temp_2thdrp
			INSERT INTO #_CCHC SELECT * FROM #_temp_lead
			CREATE INDEX acctnmbr on #_CCHC (acctnmbr)
			CREATE INDEX cardnmbr on #_CCHC (acctnmbr)
			PRINT '9-UPDATE USERID'
			SELECT * INTO #_TEMP_CCH01 FROM #_CCHC WHERE date_cch = CAST(@asofdate as date)  ORDER BY ACCTNMBR,DATE_CCH,TIME_CCH,AC_CCH DESC
			CREATE INDEX acctnmbr on #_TEMP_CCH01 (acctnmbr)
			SELECT ACCTNMBR, RIGHT(REPLICATE('0', 4) +CAST(MAX(CAST(TIME_CCH AS INT ))  AS VARCHAR(4)),4) AS MAX_OC INTO #_MAX_OCCCH FROM #_TEMP_CCH01 WHERE AC_CCH ='OC' and C_CCH IN ('A','Y') GROUP BY ACCTNMBR;
			WITH TEMP_OC AS (SELECT A.*,B.MAX_OC FROM #_TEMP_CCH01 A LEFT JOIN #_MAX_OCCCH B ON A.ACCTNMBR=B.ACCTNMBR)
			SELECT * INTO #_LASTOC FROM TEMP_OC  WHERE time_cch = max_oc;
			SELECT * INTO #_PYACCT FROM #_TEMP_CCH01 WHERE ac_cch='PY'
			SELECT * INTO #_BPACCT FROM #_TEMP_CCH01 WHERE ac_cch='BP'
			SELECT * INTO #_RJACCT FROM #_TEMP_CCH01 WHERE reject =1
			SELECT A.*,B.USERID AS OC_USER,B.TIME_CCH AS OC_TIME,C.TIME_CCH AS BP_TIME,D.TIME_CCH AS RJ_TIME INTO #PYUSER FROM #_PYACCT A LEFT JOIN #_LASTOC B on A.acctnmbr = B.acctnmbr left join #_BPACCT C on A.acctnmbr = C.acctnmbr left join #_RJACCT D ON A.acctnmbr = D.acctnmbr
			UPDATE #PYUSER SET OC_TIME='0' WHERE OC_TIME IS NULL
			UPDATE #PYUSER SET BP_TIME='0' WHERE BP_TIME IS NULL
			UPDATE #PYUSER SET RJ_TIME='0' WHERE RJ_TIME IS NULL
			UPDATE #_TEMP_CCH01 Set #_TEMP_CCH01.userid = b.userid From #PYUSER b Where #_TEMP_CCH01.acctnmbr=b.acctnmbr  and  #_TEMP_CCH01.date_cch=cast(@asofdate as date) and cast(b.time_cch as int)>cast(b.OC_TIME as int) and cast(b.BP_TIME as int)<cast(b.OC_TIME as int) and cast(b.RJ_TIME as int) < cast(b.OC_TIME as int) and b.OC_TIME !='0'
			UPDATE #_TEMP_CCH01 Set userid='None' Where Userid is null or Userid =''
			UPDATE #_CCHC SET #_CCHC.userid = b.userid FROM #_TEMP_CCH01 b WHERE #_CCHC.acctnmbr+#_CCHC.time_cch+#_CCHC.ac_cch = b.acctnmbr+b.time_cch+b.ac_cch and #_CCHC.date_cch = cast(@asofdate as date)
			PRINT '10-UPDATE AMT_NG,DATE_NG'
			TRUNCATE TABLE PROD_CCH
			INSERT INTO PROD_CCH SELECT * FROM #_CCHC
		end
	else 
		begin
			Print('今天的CCH沒有資料，請確認是否正確!!!')
		end
END

