		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S' and left(@OUTPUT3,1) = 'S' and left(@OUTPUT4,1) = 'S' and left(@OUTPUT5,1) = 'S' 
			and left(@OUTPUT6,1) = 'S' and left(@OUTPUT7,1) = 'S' and left(@OUTPUT8,1) = 'S' and left(@OUTPUT9,1) = 'S' and left(@OUTPUT10,1) = 'S' 
			and left(@OUTPUT11,1) = 'S' and left(@OUTPUT12,1) = 'S' and left(@OUTPUT13,1) = 'S' and left(@OUTPUT14,1) = 'S')
		BEGIN
			SET @STEP = '2'
			Truncate table REC_COMBO_TEMP
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','TRANS_RECCOMBO_sp處理開始','N'
			DECLARE @CACS_CDS_IWO VARCHAR(50)
			SET @CACS_CDS_IWO = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @REC_COMBO VARCHAR(50)
			SET @REC_COMBO = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			DECLARE @CARDS VARCHAR(50)
