			SET @BUDTRAN = RIGHT(@OUTPUT5,LEN(@OUTPUT5)-2)
			DECLARE @ASSETREC VARCHAR(50)
			SET @ASSETREC = RIGHT(@OUTPUT6,LEN(@OUTPUT6)-2)
			DECLARE @CACS_CDS_IBD VARCHAR(50)
			SET @CACS_CDS_IBD = RIGHT(@OUTPUT7,LEN(@OUTPUT7)-2)
			DECLARE @CACS_CDS_UR VARCHAR(50)
			SET @CACS_CDS_UR = RIGHT(@OUTPUT8,LEN(@OUTPUT8)-2)
			DECLARE @CACS_CDS_ALS VARCHAR(50)
			SET @CACS_CDS_ALS = RIGHT(@OUTPUT9,LEN(@OUTPUT9)-2)
			DECLARE @CACS_PRI VARCHAR(50)
			SET @CACS_PRI = RIGHT(@OUTPUT10,LEN(@OUTPUT10)-2)
			DECLARE @CACS_EXT_ALS VARCHAR(50)
			SET @CACS_EXT_ALS = RIGHT(@OUTPUT11,LEN(@OUTPUT11)-2)
			DECLARE @CACS_EXT_ECMS VARCHAR(50)
			SET @CACS_EXT_ECMS = RIGHT(@OUTPUT12,LEN(@OUTPUT12)-2)
			DECLARE @PF24 VARCHAR(50)
			SET @PF24 = RIGHT(@OUTPUT13,LEN(@OUTPUT13)-2)
			DECLARE @ALSEOM VARCHAR(50)
			SET @ALSEOM = RIGHT(@OUTPUT14,LEN(@OUTPUT14)-2)
			SET @STEP = '3'
			if object_id('tempdb..#CACS_CDS_IWO') is not null drop table #CACS_CDS_IWO
			Select * Into #CACS_CDS_IWO From CACS_CDS_IWO Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CACS_CDS_IWO SELECT * FROM ' + @CACS_CDS_IWO + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CACS_CDS_IWO ready ok','N'
			if object_id('tempdb..#REC_COMBO_PRE') is not null drop table #REC_COMBO_PRE
			Select * Into #REC_COMBO_PRE From REC_COMBO Where 1> 2
			SET @SQLCmd = N'INSERT INTO #REC_COMBO_PRE SELECT * FROM ' + @REC_COMBO + ' WHERE MIS_DATE = ''' + @V_MIS_DATE_BEF + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','REC_COMBO_PRE ready ok','N'
			if object_id('tempdb..#CARDS') is not null drop table #CARDS
			Select * Into #CARDS From CARDS Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CARDS SELECT * FROM ' + @CARDS + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CARDS ready ok','N'
			if object_id('tempdb..#CARDSREC') is not null drop table #CARDSREC
			Select * Into #CARDSREC From CARDSREC Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CARDSREC SELECT * FROM ' + @CARDSREC + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CARDSREC ready ok','N'
			if object_id('tempdb..#BUDTRAN') is not null drop table #BUDTRAN
			Select * Into #BUDTRAN From BUDTRAN Where 1> 2
			SET @SQLCmd = N'INSERT INTO #BUDTRAN SELECT * FROM ' + @BUDTRAN + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','BUDTRAN ready ok','N'
			if object_id('tempdb..#ASSETREC') is not null drop table #ASSETREC
			Select * Into #ASSETREC From ASSETREC Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ASSETREC SELECT * FROM ' + @ASSETREC + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','ASSETREC ready ok','N'
			if object_id('tempdb..#CACS_CDS_IBD') is not null drop table #CACS_CDS_IBD
			Select * Into #CACS_CDS_IBD From CACS_CDS_IBD Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CACS_CDS_IBD SELECT * FROM ' + @CACS_CDS_IBD + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CACS_CDS_IBD ready ok','N'
			if object_id('tempdb..#CACS_CDS_UR') is not null drop table #CACS_CDS_UR
			Select * Into #CACS_CDS_UR From CACS_CDS_UR Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CACS_CDS_UR SELECT * FROM ' + @CACS_CDS_UR + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CACS_CDS_UR ready ok','N'
			if object_id('tempdb..#CACS_CDS_ALS') is not null drop table #CACS_CDS_ALS
			Select * Into #CACS_CDS_ALS From CACS_CDS_ALS Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CACS_CDS_ALS(MIS_DATE,CARDNMBR,PRODTYPE,LOANSTAT,BOOKDATE2,ACCTTYPE,PRINCIPAL,LATECGL,UNINT,DPD,RESERVE,FEESDUE) '
			SET @SQLCmd = @SQLCmd + N'SELECT MIS_DATE,CARDNMBR,PRODTYPE,LOANSTAT,BOOKDATE2,ACCTTYPE,PRINCIPAL,LATECGL,UNINT,DPD,RESERVE,FEESDUE FROM ' + @CACS_CDS_ALS + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CACS_CDS_ALS ready ok','N'
			if object_id('tempdb..#CACS_PRI') is not null drop table #CACS_PRI
			Select * Into #CACS_PRI From CACS_PRI Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CACS_PRI SELECT * FROM ' + @CACS_PRI + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CACS_PRI ready ok','N'
			if object_id('tempdb..#CACS_EXT_ALS') is not null drop table #CACS_EXT_ALS
			Select * Into #CACS_EXT_ALS From CACS_EXT_ALS Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CACS_EXT_ALS SELECT * FROM ' + @CACS_EXT_ALS + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CACS_EXT_ALS ready ok','N'
			if object_id('tempdb..#CACS_EXT_ECMS') is not null drop table #CACS_EXT_ECMS
			Select * Into #CACS_EXT_ECMS From CACS_EXT_ECMS Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CACS_EXT_ECMS SELECT * FROM ' + @CACS_EXT_ECMS + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','CACS_EXT_ECMS ready ok','N'
			if object_id('tempdb..#PF24') is not null drop table #PF24
			Select * Into #PF24 From PF24 Where 1> 2
			SET @SQLCmd = N'INSERT INTO #PF24 SELECT * FROM ' + @PF24 + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','PF24 ready ok','N'
			if object_id('tempdb..#ALSEOM') is not null drop table #ALSEOM
			Select * Into #ALSEOM From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM SELECT * FROM ' + @ALSEOM + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd						
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','ALSEOM ready ok','N'
			SET @STEP = '4'
			if object_id('tempdb..#_CACS') is not null drop table #_CACS
			SELECT ACCTNMBR,CARDNMBR,CUSTID,LOCATION,CACS_STATE,BALANCE,RESPONCOLL,DATE_STATE,DATE_PMT,LAST_OC,DOB,CUSTNAME 
			INTO #_CACS 
			FROM #CARDS
			UNION SELECT ACCTNMBR,CARDNMBR,CUSTID,LOCATION,CACS_STATE,BALANCE,RESPONCOLL,DATE_STATE,DATE_PMT,LAST_OC,DOB,CUSTNAME FROM #CARDSREC
			UNION SELECT ACCTNMBR,CARDNMBR,CUSTID,LOCATION,CACS_STATE,BALANCE,RESPONCOLL,DATE_STATE,DATE_PMT,LAST_OC,DOB,CUSTNAME FROM #BUDTRAN
			UNION SELECT ACCTNMBR,CARDNMBR,CUSTID,LOCATION,CACS_STATE,BALANCE,RESPONCOLL,DATE_STATE,DATE_PMT,LAST_OC,DOB,CUSTNAME FROM #ASSETREC
			if object_id('tempdb..#_CACS1') is not null drop table #_CACS1
			SELECT A.*,B.LOGO,B.WOAMT,B.PRINCIPAL,B.ACCTBLOCK1,B.ACCTBLOCK2,B.CARDBLOCK,B.DATEOPEN 
			INTO #_CACS1 
			FROM #_CACS A 
			LEFT JOIN #CARDS B ON A.CARDNMBR=B.CARDNMBR
			UPDATE #_CACS1 SET LOGO=A.LOGO
							 , WOAMT=A.WOAMT
							 , ACCTBLOCK1=A.ACCTBLOCK1
							 , ACCTBLOCK2=A.ACCTBLOCK2
							 , CARDBLOCK=A.CARDBLOCK
							 , DATEOPEN=A.DATEOPEN 
			FROM #CARDSREC A, #_CACS1 B 
			WHERE A.CARDNMBR=B.CARDNMBR
			UPDATE #_CACS1 SET PRINCIPAL=A.PRINCIPAL 
			FROM #BUDTRAN A, #_CACS1 B 
			WHERE A.CARDNMBR=B.CARDNMBR
			UPDATE #_CACS1 SET WOAMT=A.WOAMT
							 , PRINCIPAL=A.PRINCIPAL
							 , DATEOPEN=A.DATEOPEN 
			FROM #ASSETREC A, #_CACS1 B 
			WHERE A.CARDNMBR=B.CARDNMBR
			SET @STEP = '5'
			SELECT A.*,SPACE(16) AS ACCTNMBR,CAST(0 AS NUMERIC(12,2)) AS CURR_P,CAST(0 AS NUMERIC(12,2)) AS CURR_I,CAST(0 AS NUMERIC(12,2)) AS CURR_F,CAST(0 AS NUMERIC(12,2)) AS BALANCE,
				B.DEAD_DATE1 AS IBD_D1,B.DEAD_DATE2 AS IBD_D2,B.DEALER,
				C.TEXT5 AS PRG_CODE,C.TEXT6 AS INITIATE_D,
				SPACE(1) AS IN_ALS,D.LOANSTAT,D.ACCTTYPE,D.BOOKDATE2 AS DATE_OPEN,
				E.CACS_STATE,E.DATE_STATE,E.RESPONCOLL,E.DATE_CNTCT AS CACS_CNTCT,E.LAST_OC
			INTO #_REC1 
			FROM #CACS_CDS_IWO A 
			LEFT JOIN #CACS_CDS_IBD B ON A.CARDNMBR=B.CARDNMBR 
			LEFT JOIN #CACS_CDS_UR C ON A.CARDNMBR=C.CARDNMBR AND C.SEGID='UR1'
			LEFT JOIN #CACS_CDS_ALS D ON A.CARDNMBR=D.CARDNMBR 
			LEFT JOIN #CACS_PRI E ON A.CARDNMBR=E.CARDNMBR
			SET @STEP = '6'
			SELECT A.*,B.TEXT1 AS CHAPTERNUM,B.TEXT2 AS CASE_NUM,B.TEXT3 AS BANK_COURT,B.TEXT4 AS ASSET_IND,B.TEXT5 AS SEC_DEPOSI,B.TEXT6,B.TEXT7,B.TEXT8, 
				B.DATE1 AS FIELD_DTE, B.DATE2 AS PETITI_DTE, B.DATE3 AS CLAIM_DTE, B.DATE4 AS REAFF_DTE, B.DATE5 AS HEAR_DTE, B.DATE6 AS STIP_DTE, B.DATE7 AS DISCL_DTE, B.DATE8 AS AREA8_DTE,B.AMT8 AS AMT_OUTSID,B.NAME1 AS ACT_BANK,B.NAME2 AS ACT_TRUSTE,B.PHONE1 AS PHN_BANK,C.CUSTID,C.LOGO,C.ACCTBLOCK1,C.ACCTBLOCK2,C.CARDBLOCK,C.DOB,C.CUSTNAME,
				SPACE(5) AS SCOREID,SPACE(5) AS SCORE,D.LAST_PMNT AS  LASTPYMT,D.LAST_PMNTD AS LASTPAY,D.LIMITTED AS CRLMIT,SPACE(5) AS PRODTYPE  
			INTO #_REC2 
			FROM #_REC1 A  
			LEFT JOIN #PF24 B ON A.CARDNMBR=B.CARDNMBR 
			LEFT JOIN #_CACS1 C ON A.CARDNMBR=C.CARDNMBR 
			LEFT JOIN #CACS_EXT_ALS D ON A.CARDNMBR=D.ACCTNMBR AND ISNULL(D.ACCTNMBR,'') <> ''
			SET @STEP = '7'
			UPDATE #_REC2 SET PRODTYPE='AFTI' 
			FROM REC_AFTI A
			INNER JOIN #_REC2 B ON A.ACCTNMBR=B.ACCTNMBR
			WHERE ISNULL(B.PRODTYPE,'')=''
			UPDATE #_REC2 SET ACCTNMBR=CARDNMBR WHERE CARDNMBR<'36' OR CARDNMBR IN ('72210006340000','97146086000000')
			UPDATE #_REC2 SET CURR_P=RMS_P, CURR_I=RMS_I, CURR_F=RMS_F WHERE LOCATION='880103'
			UPDATE #_REC2 SET BALANCE=RMS_P+RMS_I+RMS_F WHERE LOCATION='880103'
			UPDATE #_REC2 SET IN_ALS='Y' 
			FROM #CACS_CDS_ALS A, #_REC2 B 
			WHERE A.CARDNMBR=B.CARDNMBR
			UPDATE #_REC2 SET ACCTNMBR=A.ACCTNMBR,LOCATION=A.LOCATION,CACS_STATE=A.CACS_STATE,RESPONCOLL=A.RESPONCOLL,DATE_STATE=A.DATE_STATE,LAST_OC=A.LAST_OC 
			FROM #_CACS1 A, #_REC2 B 
			WHERE A.CARDNMBR=B.CARDNMBR
			UPDATE #_REC2 SET CUSTNAME=A.CUSTNAME,DOB=A.DOB,SCOREID='000',SCORE='000',ACCTTYPE=A.ACCTTYPE 
			FROM #CACS_EXT_ALS A, #_REC2 B 
			WHERE A.ACCTNMBR=B.ACCTNMBR AND (B.ACCTNMBR IS NOT NULL AND B.ACCTNMBR !='')
			UPDATE #_REC2 SET CUSTID=A.CUSTID 
			FROM #CACS_EXT_ALS A, #_REC2 B 
			WHERE A.ACCTNMBR=B.ACCTNMBR AND (B.ACCTNMBR IS NOT NULL AND B.ACCTNMBR !='') AND LEN(A.CUSTID)=10
			UPDATE #_REC2 SET ACCTNMBR=A.ACCTNMBR,CUSTNAME=A.CUSTNAME,CUSTID=A.CUSTID,ACCTBLOCK1=A.ACCTBLOCK1,
									ACCTBLOCK2=A.ACCTBLOCK2,CARDBLOCK=A.CARDBLOCK,DOB=A.DOB,SCOREID=A.SCORE_ID,SCORE=RIGHT(A.REC_SCORE,3),
									LASTPYMT=A.LAST_PMT,LASTPAY=A.DATE_PMT,DATE_OPEN=A.DATEOPEN,LOGO=A.LOGO,CRLMIT=A.CRLIMIT 
			FROM #CACS_EXT_ECMS A, #_REC2 B 
			WHERE A.CARDNMBR=B.CARDNMBR
			UPDATE #_REC2 SET LASTPAY=A.DATE1,LASTPYMT=A.AMT8 
			FROM #PF24 A,#_REC2 B 
			WHERE B.LOCATION !='880103' AND B.ACCTNMBR<'36' AND A.CARDNMBR=B.CARDNMBR AND A.DATE1 !='' AND A.DATE1 > B.LASTPAY
			UPDATE #_REC2 SET LASTPAY=A.LASTPAY,LASTPYMT=A.LASTPYMT 
			FROM CACS_CDS_LBL A,#_REC2 B 
			WHERE B.LOCATION ='880103' AND A.CARDNMBR=B.CARDNMBR AND A.MIS_DATE=@V_MIS_DATE
			UPDATE #_REC2 SET BALANCE=A.BALANCE,CURR_I=A.INTEREST,CURR_F=A.FEE,CURR_P=(A.BALANCE-A.INTEREST-A.FEE) 
			FROM #CACS_EXT_ECMS A,#_REC2 B 
			WHERE B.LOCATION !='880103' AND (B.ACCTNMBR IS NOT NULL AND B.ACCTNMBR !='') AND A.CARDNMBR=B.CARDNMBR
			UPDATE #_REC2 SET CURR_F=A.LEGAL_FEE+A.LATE_FEE
			FROM #CACS_EXT_ALS A,#_REC2 B 
			WHERE B.LOCATION !='880103' AND (B.ACCTNMBR IS NOT NULL AND B.ACCTNMBR !='') AND A.ACCTNMBR=B.ACCTNMBR
			UPDATE #_REC2 SET CURR_I=A.UNINT,CURR_P=A.PRINCIPAL,BALANCE=(A.PRINCIPAL+A.UNINT+B.CURR_F) 
			FROM CACS_CDS_ALS A, #_REC2 B 
			WHERE B.LOCATION !='880103' AND (B.ACCTNMBR IS NOT NULL AND B.ACCTNMBR !='') AND A.CARDNMBR=B.CARDNMBR AND A.MIS_DATE=@V_MIS_DATE
			UPDATE #_REC2 SET CACS_STATE = 'X00' WHERE ISNULL(CACS_STATE,'')='' AND MIS_DATE=@V_MIS_DATE
			SELECT @CNT=COUNT(*) FROM #_REC2 WHERE CACS_STATE='X00'
			IF (@CNT > 0)
			BEGIN
				SET @LOGMSG = '有 ' + CAST(@CNT AS VARCHAR) + ' 筆X00 ACCOUNT，請詳查'
				EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO',@LOGMSG,'Y'
			END
			SET @STEP = '8'
			SELECT A.*,SPACE(8) AS BUSINESS,SPACE(4) AS OA,SPACE(3) AS STATUS1,SPACE(10) AS ID_TAG,SPACE(14) AS ASGN_KEY,B.LASTASGN1 
			INTO #_REC3 
			FROM #_REC2 A 
			LEFT JOIN #REC_COMBO_PRE B ON A.CARDNMBR = B.CARDNMBR
			SELECT @CNT=COUNT(*) FROM #_REC3 WHERE ISNULL(CACS_STATE,'')=''
			IF (@CNT > 0)
			BEGIN
				SET @LOGMSG = '有 ' + CAST(@CNT AS VARCHAR) + ' 筆空白CACS_STATE，請詳查'
				EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO',@LOGMSG,'Y'
			END
			SET @STEP = '9'
			UPDATE #_REC3 SET SCOREID='000',SCORE='000' WHERE LOGO IN ('755','757','749')
			UPDATE #_REC3 SET SCORE='000' WHERE CARDNMBR<'36'
			UPDATE #_REC3 SET STATUS1=BANK_COURT
			UPDATE #_REC3 SET STATUS1='S90' WHERE LEFT(LTRIM(ASSET_IND),3) IN ('3OA') AND (ISNULL(ASSET_IND,'') <> '') AND LEFT(BANK_COURT,1) <> '9'
			UPDATE #_REC3 SET STATUS1='XXX' WHERE (BANK_COURT = '' OR BANK_COURT IS NULL)
			UPDATE #_REC3 SET OA='DEAD' WHERE CACS_STATE IN ('S37','S06','S07') OR (CACS_STATE IN('N21','Y21','S38','S34') AND LEFT(STATUS1,1)='9' AND (ISNULL(IBD_D2,'') <> '' AND ISNULL(IBD_D2,'') <> ''))
			UPDATE #_REC3 SET OA='G0'+LEFT(LTRIM(ASSET_IND),1)+RIGHT(RTRIM(ASSET_IND),1) WHERE LEFT(LTRIM(ASSET_IND),3) IN ('1OA','2OA') AND (ISNULL(ASSET_IND,'') <>'') AND OA<>'DEAD'
			UPDATE #_REC3 SET OA='G0Z'+RIGHT(RTRIM(ASSET_IND),1) WHERE LEFT(LTRIM(ASSET_IND),3) IN ('3OA') AND ISNULL(ASSET_IND,'') <>'' AND OA <>'DEAD'
			UPDATE #_REC3 SET OA=SUBSTRING(LTRIM(ASSET_IND),2,4) WHERE SUBSTRING(LTRIM(ASSET_IND),2,2)='CO' AND OA<>'DEAD'
			UPDATE #_REC3 SET OA=ASSET_IND WHERE LEFT(LTRIM(ASSET_IND),2)='EO' AND OA<>'DEAD' AND ASSET_IND<>'EORT'
			UPDATE #_REC3 SET OA=UPPER(LEFT(LTRIM(ASSET_IND),4)) WHERE UPPER(ASSET_IND)='CL01' AND OA<>'DEAD'
			UPDATE #_REC3 SET OA='G000' WHERE CACS_STATE='S32' AND ASSET_IND='G000'
			UPDATE #_REC3 SET OA='RPGM', STATUS1='G02' WHERE (SUBSTRING(CACS_STATE,1,2) IN ('G7','G8','Y1','Y6','N8','N6','Y2','N2','Y3','N3','Y4','N4') OR CACS_STATE IN ('G99','N11') ) AND OA<>'DEAD' AND CACS_STATE NOT IN ('Y21','N21')
			UPDATE #_REC3 SET OA='RPGM', STATUS1='G01' WHERE SUBSTRING(CACS_STATE,1,2) IN ('Y5','N5','Y0','N0') AND OA<>'DEAD'
			UPDATE #_REC3 SET OA='RPGM'  WHERE CACS_STATE IN ('Y21','N21') AND LEFT(STATUS1,1)='9' AND OA<>'DEAD'
			UPDATE #_REC3 SET OA='RPGM', STATUS1='G03' WHERE SUBSTRING(CACS_STATE,1,2) IN ('S8') AND OA<>'DEAD'
			UPDATE #_REC3 SET OA='RPGM', STATUS1='G04' WHERE (SUBSTRING(CACS_STATE,1,2) IN ('S7') OR CACS_STATE IN ('S35')) AND OA<>'DEAD'
			UPDATE #_REC3 SET OA='DEAD' WHERE (OA='' OR OA IS NULL) AND CACS_STATE IN ('S04','S10','S34','S36','S38','N21','Y21')
			SET @STEP = '10'
			UPDATE #_REC3 SET ACCTTYPE=A.ACCTTYPE FROM #REC_COMBO_PRE A,#_REC3 B WHERE A.CARDNMBR = B.CARDNMBR AND (A.ACCTTYPE IS NOT NULL AND A.ACCTTYPE !='')
			UPDATE #_REC3 SET ACCTNMBR=A.ACCTNMBR,LASTASGN1 = A.LASTASGN1 FROM #REC_COMBO_PRE A,#_REC3 B WHERE A.CARDNMBR=B.CARDNMBR AND (A.ACCTNMBR IS NOT NULL AND A.ACCTNMBR !='')
			UPDATE #_REC3 SET DOB=A.DOB FROM #REC_COMBO_PRE A,#_REC3 B WHERE A.CARDNMBR=B.CARDNMBR AND ISNULL(A.DOB,'') <> '' AND ISNULL(B.DOB,'') ='' 
			UPDATE #_REC3 SET CUSTID=A.CUSTID FROM #REC_COMBO_PRE A,#_REC3 B WHERE A.CARDNMBR = B.CARDNMBR AND (B.LOCATION='660103' OR B.CARDNMBR IN('72210006340000','97146086000000') OR (B.CUSTID IS NULL OR B.CUSTID =''))
			UPDATE #_REC3 SET BUSINESS=A.BUSINESS,PRODTYPE=A.PRODTYPE FROM #REC_COMBO_PRE A,#_REC3 B WHERE  A.CARDNMBR = B.CARDNMBR AND (SUBSTRING(B.CARDNMBR,1,1)='0' OR B.CARDNMBR IN('72210006340000','97146086000000')) AND (A.BUSINESS IS NOT NULL AND A.BUSINESS !='')
			UPDATE #_REC3 SET LASTASGN1=@V_MIS_DATE FROM #REC_COMBO_PRE A,#_REC3 B WHERE A.CARDNMBR = B.CARDNMBR AND A.OA != B.OA
			SET @STEP = '11'
			UPDATE #_REC3 SET CUSTNAME=A.CUSTNAME FROM #REC_COMBO_PRE A,#_REC3 B WHERE (B.CUSTNAME='' OR B.CUSTID IS NULL) AND A.CARDNMBR=B.CARDNMBR
			UPDATE #_REC3 SET DOB=A.DOB FROM #REC_COMBO_PRE A,#_REC3 B WHERE (B.DOB='' OR B.DOB IS NULL) AND A.CARDNMBR=B.CARDNMBR
			UPDATE #_REC3 SET DOB=A.DOB FROM #REC_COMBO_PRE A,#_REC3 B WHERE (B.DOB='' OR B.DOB IS NULL) AND A.CUSTID=B.CUSTID
			SET @STEP = '12'
			EXEC dbo.UPD_ID_TAG_SP @MAP_DBF = '#_REC3',@COL_ID='CUSTID', @asofdate = @V_MIS_DATE
			SET @STEP = '13'
			if object_id('tempdb..#_REC_WORKABLE1') is not null drop table #_REC_WORKABLE1
			SELECT * INTO #_REC_WORKABLE1 
			FROM #_rec3 WHERE SUBSTRING(STATUS1,1,1) NOT IN ('D','A','9') OR (STATUS1 IN ('D69','A69','969','D67','A67','967','D68','A68','968','D69','A69','969','D70','A70','970','D40','A40','991'))
			if object_id('tempdb..#_REC_WORKABLE') is not null drop table #_REC_WORKABLE
			SELECT * INTO #_REC_WORKABLE 
			FROM #_REC_WORKABLE1 WHERE STATUS1 NOT IN ('L02') AND OA !='XXXX'
			if object_id('tempdb..#_MORTGAGE') is not null drop table #_MORTGAGE
			SELECT ID_TAG,CARDNMBR,NBV INTO #_MORTGAGE 
			FROM #ALSEOM WHERE ID_TAG IN (SELECT ID_TAG FROM #_REC3) AND SEGMENT IN ('ML') AND NBV>0
			if object_id('tempdb..#_SECURE_ML') is not null drop table #_SECURE_ML
			SELECT ID_TAG INTO #_SECURE_ML 
			FROM #_MORTGAGE WHERE CARDNMBR NOT IN (SELECT CARDNMBR FROM #_REC3) GROUP BY ID_TAG
			if object_id('tempdb..#_SFALL_ML') is not null drop table #_SFALL_ML
			SELECT ID_TAG INTO #_SFALL_ML 
			FROM #_REC_WORKABLE WHERE SUBSTRING(CARDNMBR,1,3) IN ('110','150','170','180','190') GROUP BY ID_TAG
			if object_id('tempdb..#_DINERS') is not null drop table #_DINERS
			SELECT CARDNMBR INTO #_DINERS 
			FROM #_REC_WORKABLE WHERE SUBSTRING(CARDNMBR,1,2)='36'
			SET @STEP = '14'
			UPDATE #_REC3 SET ASGN_KEY='SCU_'+LTRIM(B.ID_TAG) FROM #_SECURE_ML A,#_REC3 B WHERE A.ID_TAG=B.ID_TAG AND (LTRIM(B.ASGN_KEY) ='' OR LTRIM(B.ASGN_KEY) IS NULL)
			UPDATE #_REC3 SET ASGN_KEY='SPC_'+LTRIM(B.ID_TAG) FROM #_SFALL_ML A,#_REC3 B WHERE A.ID_TAG=B.ID_TAG AND (LTRIM(B.ASGN_KEY) ='' OR LTRIM(B.ASGN_KEY) IS NULL)
			UPDATE #_REC3 SET ASGN_KEY='SPC_'+LTRIM(B.ID_TAG) FROM #_DINERS A,#_REC3 B WHERE A.CARDNMBR=B.CARDNMBR AND (LTRIM(B.ASGN_KEY) ='' OR LTRIM(B.ASGN_KEY) IS NULL)
			UPDATE #_REC3 SET ASGN_KEY='BAU_'+LTRIM(ID_TAG) FROM #_REC3 WHERE (LTRIM(ASGN_KEY) ='' OR LTRIM(ASGN_KEY) IS NULL)
			UPDATE #_REC3 SET ASGN_KEY='DEA_'+LTRIM(ID_TAG) FROM #_REC3 WHERE SUBSTRING(STATUS1,1,1) IN ('D','A','9') AND STATUS1 NOT IN ('D67','A67','967','D68','A68','968','D69','A69','969','D70','A70','970','D40','A40','991')
			SET @STEP = '15'
			if object_id('tempdb..#_DOUBLE') is not null drop table #_DOUBLE
			SELECT ROW_NUMBER() OVER (ORDER BY CARDNMBR) AS REC_NO,*  INTO #_DOUBLE 
			FROM #_REC3
			if object_id('tempdb..#_DOUBLE_1') is not null drop table #_DOUBLE_1
			SELECT CARDNMBR,MIN(REC_NO) AS MIN_REC INTO #_DOUBLE_1 
			FROM #_DOUBLE 
			GROUP BY CARDNMBR
			if object_id('tempdb..#_REC4') is not null drop table #_REC4
			SELECT * INTO #_REC4 
			FROM #_DOUBLE 
			WHERE REC_NO IN (SELECT MIN_REC FROM #_DOUBLE_1)
			SET @STEP = '16'
			INSERT INTO REC_COMBO_TEMP 
			SELECT MIS_DATE,IN_ALS,DATEAS,ACCTNMBR,CARDNMBR,CUSTID,ID_TAG,CUSTNAME,LOCATION,LOANSTAT,WODATE,WOAMT,BALANCE,STATUS1,CACS_STATE,DATE_STATE,
				OA,WOPRIAMT,WOINTAMT,WOFEEAMT,LASTASGN1,CURR_P,CURR_I,CURR_F,SCOREID,SCORE,RMS_P,RMS_I,RMS_F,RMS_BAL,LASTPYMT,LASTPAY,RECV_P,RECV_I,RECV_F,CACS_CNTCT,
				LAST_OC,DOB,DEALER,ACCTBLOCK1,ACCTBLOCK2,CARDBLOCK,RESPONCOLL,DATE_OPEN,CHAPTERNUM,CASE_NUM,BANK_COURT,ASSET_IND,SEC_DEPOSI,TEXT6,TEXT7,TEXT8,AMT_OUTSID,
				FIELD_DTE,PETITI_DTE,CLAIM_DTE,REAFF_DTE,HEAR_DTE,STIP_DTE,DISCL_DTE,AREA8_DTE,ACT_BANK,ACT_TRUSTE,PHN_BANK,ASGN_KEY,BUSINESS,LOGO,ACCTTYPE,PRODTYPE,IBD_D1,
				IBD_D2,PRG_CODE,INITIATE_D,CRLMIT 
			FROM #_REC4
			SET @STEP = '17'
			if object_id('tempdb..#ADD_REC') is not null drop table #ADD_REC
			SELECT * INTO #ADD_REC 
			FROM #REC_COMBO_PRE WHERE ACCTNMBR NOT IN (SELECT ACCTNMBR FROM REC_COMBO_TEMP)
			SET @STEP = '18'
			UPDATE #ADD_REC SET MIS_DATE=@V_MIS_DATE WHERE MIS_DATE != @V_MIS_DATE
			SET @STEP = '19'
			INSERT INTO REC_COMBO_TEMP SELECT * FROM #ADD_REC
			DECLARE @CNT1 INT
			SELECT @CNT=COUNT(*) FROM #_REC4			--今天
			SELECT @CNT1=COUNT(*) FROM REC_COMBO_TEMP	--含昨天
			IF ((@CNT1-@CNT) > 0)
			BEGIN
				SET @LOGMSG = '昨天有，今天卻沒有的ACCOUNT, 共 ' + CAST((@CNT1-@CNT) AS VARCHAR) + ' 筆，請詳查'
				EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO',@LOGMSG,'Y'
			END
			SET @STEP = '20'
			DELETE FROM REC_COMBO_TEMP WHERE ACCTNMBR IN (SELECT ACCTNMBR FROM CHK_ML)
			if object_id('tempdb..#_DEL_PL') is not null drop table #_DEL_PL
			SELECT * INTO #_DEL_PL FROM CHK_PL WHERE ACCTNMBR IN (SELECT CARDNMBR FROM CACS_CDS_ALS WHERE PRINCIPAL-RESERVE>0 AND MIS_DATE=@V_MIS_DATE)
			DELETE FROM REC_COMBO_TEMP WHERE ACCTNMBR IN (SELECT ACCTNMBR FROM #_DEL_PL)
			DELETE FROM REC_COMBO_TEMP WHERE ACCTNMBR IN (SELECT ACCTNMBR FROM USER_ERR)
			SELECT @CNT=COUNT(*) FROM REC_COMBO_TEMP WHERE WOAMT=0 AND substring(CACS_STATE,1,1) NOT IN ('G','Y','N','S','V','Q')
			IF (@CNT > 0)
			BEGIN
				SET @LOGMSG = '有 ' + CAST(@CNT AS VARCHAR) + ' 筆CHECK AMT，請詳查'
				EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO',@LOGMSG,'Y'
			END
			SET @STEP = '21'
			exec dbo.MAPPING_BUSINESS_SP @tablename = 'REC_COMBO_TEMP',@asofdate = @V_MIS_DATE
			SET @STEP = '22'
			exec dbo.UPD_ID_TAG_SP @MAP_DBF = 'REC_COMBO_TEMP',@COL_ID='CUSTID',@asofdate = @V_MIS_DATE
			SET @STEP = '23'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO','Update REC_COMBO data...','N'
			if object_id('tempdb..#IWO_ADJUST') is not null drop table #IWO_ADJUST
			SELECT '2' AS REASON, B.DATEAS, A.LOCATION, A.ACCTNMBR, A.CARDNMBR, A.WODATE, A.WOPRIAMT, A.WOINTAMT, A.WOFEEAMT, B.CURR_P AS RMS_P
			, B.CURR_I AS RMS_I, B.CURR_F AS RMS_F, A.WOPRIAMT-B.CURR_P AS RECV_P, A.WOINTAMT-B.CURR_I AS RECV_I, A.WOFEEAMT-B.CURR_F AS RECV_F
			INTO #IWO_ADJUST
			FROM #REC_COMBO_PRE A 
			LEFT JOIN REC_COMBO_TEMP B ON A.CARDNMBR=B.CARDNMBR 
			WHERE LEFT(A.CARDNMBR,2)>='36' 
			AND A.CARDNMBR!='4563188417868407'
			SET @STEP = '24'
			UPDATE #IWO_ADJUST SET RECV_P = 0 WHERE WOPRIAMT < RMS_P
			UPDATE #IWO_ADJUST SET RECV_I = 0 WHERE WOINTAMT < RMS_I
			UPDATE #IWO_ADJUST SET RECV_F = 0 WHERE WOFEEAMT < RMS_F
			UPDATE #IWO_ADJUST SET RECV_P =	WOPRIAMT WHERE RMS_P < 0
			UPDATE #IWO_ADJUST SET RECV_I = WOINTAMT WHERE RMS_I < 0
			UPDATE #IWO_ADJUST SET RECV_F = WOFEEAMT WHERE RMS_F < 0
			UPDATE #IWO_ADJUST SET RMS_P=0 WHERE WOPRIAMT=RECV_P AND LEFT(CARDNMBR,2)<'36'
			UPDATE #IWO_ADJUST SET RMS_I=0 WHERE WOINTAMT=RECV_I AND LEFT(CARDNMBR,2)<'36'
			UPDATE #IWO_ADJUST SET RMS_F=0 WHERE WOFEEAMT=RECV_F AND LEFT(CARDNMBR,2)<'36'
			SELECT @CNT=COUNT(*) FROM #IWO_ADJUST WHERE CARDNMBR IN (SELECT CARDNMBR FROM #IWO_ADJUST GROUP BY CARDNMBR HAVING COUNT(*)>1)
			IF (@CNT > 0)
			BEGIN
				SET @LOGMSG = '有 ' + CAST(@CNT AS VARCHAR) + ' 筆重覆的A/C，請詳查'
				EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO',@LOGMSG,'Y'
			END
			SET @STEP = '25'
			if object_id('tempdb..#IWO_ADJUST01') is not null drop table #IWO_ADJUST01
			SELECT A.*,B.STATUS1 
			INTO #IWO_ADJUST01
			FROM #IWO_ADJUST A 
			LEFT JOIN REC_COMBO_TEMP B ON A.CARDNMBR=B.CARDNMBR
			SET @STEP = '26'
			if object_id('tempdb..#IWO_ADJUST1') is not null drop table #IWO_ADJUST1
			SELECT * INTO #IWO_ADJUST1 
			FROM #IWO_ADJUST01 
			WHERE STATUS1 NOT IN ('900','936','945','947','998','999','95A','99A','99B','99D','99F','99G','99K','99O','9FX','9NA','9NB','902') OR WODATE>='2000-01-01'
			SET @STEP = '27'
			UPDATE REC_COMBO_TEMP 
				SET REC_COMBO_TEMP.WOPRIAMT=M.WOPRIAMT
				  , REC_COMBO_TEMP.WOINTAMT=M.WOINTAMT
				  , REC_COMBO_TEMP.WOFEEAMT=M.WOFEEAMT
				  , REC_COMBO_TEMP.WOAMT=M.WOPRIAMT+M.WOINTAMT+M.WOFEEAMT
				  , REC_COMBO_TEMP.RMS_P=M.RMS_P
				  , REC_COMBO_TEMP.RMS_I=M.RMS_I
				  , REC_COMBO_TEMP.RMS_F=M.RMS_F
				  , REC_COMBO_TEMP.RMS_BAL=M.RMS_P+M.RMS_I+M.RMS_F
				  , REC_COMBO_TEMP.RECV_P=M.RECV_P
				  , REC_COMBO_TEMP.RECV_I=M.RECV_I
				  , REC_COMBO_TEMP.RECV_F=M.RECV_F
			FROM #IWO_ADJUST1 M
			WHERE M.CARDNMBR=REC_COMBO_TEMP.CARDNMBR
			AND (ABS(REC_COMBO_TEMP.WOPRIAMT-M.WOPRIAMT)>1 OR ABS(REC_COMBO_TEMP.WOINTAMT-M.WOINTAMT)>1 OR ABS(REC_COMBO_TEMP.WOFEEAMT-M.WOFEEAMT)>1
			  OR ABS(REC_COMBO_TEMP.RMS_P-M.RMS_P)>1 OR ABS(REC_COMBO_TEMP.RMS_I-M.RMS_I)>1 OR ABS(REC_COMBO_TEMP.RMS_F-M.RMS_F)>1
			  OR ABS(REC_COMBO_TEMP.RECV_P-M.RECV_P)>1 OR ABS(REC_COMBO_TEMP.RECV_I-M.RECV_I)>1 OR ABS(REC_COMBO_TEMP.RECV_F-M.RECV_F)>1)
			SELECT @CNT=COUNT(*) FROM REC_COMBO_TEMP WHERE ISNULL(CUSTID,'')=''
			IF (@CNT > 0)
			BEGIN
				SET @LOGMSG = '有 ' + CAST(@CNT AS VARCHAR) + ' 筆空的CUSTID，請詳查'
				EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO',@LOGMSG,'Y'
			END
			SET @STEP = '30'
			DECLARE @TODAY_MM VARCHAR(2)
			SET @TODAY_MM = SUBSTRING(CONVERT(VARCHAR,GETDATE(),112),5,2)
			DECLARE @V_MIS_DATE_MM VARCHAR(2)
			SET @V_MIS_DATE_MM = SUBSTRING(CONVERT(VARCHAR,@D_MIS_DATE,112),5,2)
			DECLARE @TABLE_NAME VARCHAR(50)
			IF (@TODAY_MM = @V_MIS_DATE_MM)
			BEGIN
				SET @TABLE_NAME = 'REC_COMBO'
			END
			ELSE
			BEGIN
				SET @TABLE_NAME = 'REC_COMBO_' + @V_MIS_DATE_MM
			END
			SET @SQLCmd = N'DELETE FROM ' + @TABLE_NAME + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''''
			EXECUTE sp_executesql @SQLCmd
			SET @SQLCmd = N'INSERT INTO ' + @TABLE_NAME + ' SELECT * FROM REC_COMBO_TEMP '
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '31'
			IF (@V_MIS_DATE = CONVERT(VARCHAR,DATEADD(D,-1,GETDATE()),23))
			BEGIN
				Truncate Table REC_COMBO_NEW
				INSERT INTO REC_COMBO_NEW SELECT * FROM REC_COMBO_TEMP
				Truncate Table COGDB2.dbo.DS_RMS
				INSERT INTO COGDB2.DBO.DS_RMS SELECT dateas,wodate,CARDNMBR AS acctnmbr,location,cacs_state,date_state,custname,
					'' AS center1,'' AS group1,oa,status1,'' AS loantype,woamt,balance,lastpay,wopriamt,wofeeamt,wointamt,
					0 AS woothamt,IIF(WOPRIAMT-CURR_P<0,0,WOPRIAMT-CURR_P) as pypriamt,IIF(WOINTAMT-CURR_I<0,0,WOINTAMT-CURR_I)  as pyfeeamt,
					IIF(WOFEEAMT-CURR_F<0,0,WOFEEAMT-CURR_F) as pyintamt,0 as pyothamt,curr_p,curr_i,curr_f,dob,asset_ind,custid,id_tag,
					prodtype,crlmit as crlimit FROM REC_COMBO_NEW
				DECLARE @rec_cnt numeric(10)
				SELECT @rec_cnt =  COUNT(1) FROM COGDB2.DBO.DS_RMS
				Update cogdb2.dbo.control_table set dateas=Convert(date,getdate()-1,111),bat_end=getdate(),bat_begin=getdate(),bat_run=0,records=@rec_cnt where table_name='DS_RMS'
