		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'REC_COMBO',@OUTPUT,'Y'
	END CATCH
	RETURN
END

