ALTER PROCEDURE [dbo].[TRANS_REPORT_ACCTROUT_sp]
	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	BEGIN TRY
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'BUDTRAN',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'CARDS',@YYYYMMDD, @OUTPUT2 output
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S')
		BEGIN
			SET @STEP = '4'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ACCTROUT_sp','ACCTROUT資料處理開始','N'
			DECLARE @BUDTRAN VARCHAR(50)
			SET @BUDTRAN = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @CARDS VARCHAR(50)
			SET @CARDS = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			SET @STEP = '5'
			/* 
			===== ALS =====
			*/
			if object_id('tempdb..#BUDTRAN') is not null drop table #BUDTRAN
			Select * Into #BUDTRAN From BUDTRAN Where 1> 2
			SET @SQLCmd = N'INSERT INTO #BUDTRAN SELECT * FROM ' + @BUDTRAN + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '6'
			if object_id('tempdb..#ALL_ALS') is not null drop table #ALL_ALS
			SELECT DATEAS,CDC,CYCLEID,CACS_STATE,BALANCE/1000 AS BALANCE,LOCATION,BUSINESS,SPACE(6) AS PD 
			INTO #ALL_ALS
			FROM #BUDTRAN
			WHERE PRINCIPAL-RESERVE >0 
			SET @STEP = '7'
			UPDATE #ALL_ALS SET PD='ML' WHERE BUSINESS<>'CTL' AND LOCATION='860103'
			UPDATE #ALL_ALS SET PD='RC'	WHERE BUSINESS<>'CTL' AND LOCATION='860106'
			UPDATE #ALL_ALS SET PD='PL'	WHERE BUSINESS<>'CTL' AND LOCATION='860107'
			UPDATE #ALL_ALS SET PD='AL'	WHERE BUSINESS<>'CTL' AND LOCATION='860102'
			UPDATE #ALL_ALS SET PD='MP'	WHERE BUSINESS<>'CTL' AND LOCATION='860104'
			UPDATE #ALL_ALS SET PD='BOOCML'	WHERE BUSINESS='CTL' AND LOCATION='860103'
			UPDATE #ALL_ALS SET PD='BOOCRC'	WHERE BUSINESS='CTL' AND LOCATION='860106'
			UPDATE #ALL_ALS SET PD='BOOCPL'	WHERE BUSINESS='CTL' AND LOCATION='860107'
			UPDATE #ALL_ALS SET PD='BOOCMP'	WHERE BUSINESS='CTL' AND LOCATION='860104'
			SET @STEP = '8'
			if object_id('tempdb..#ALS') is not null drop table #ALS
			SELECT PD,CDC,CYCLEID,CACS_STATE,COUNT(*) AS ACCT,ROUND(SUM(BALANCE),0) AS AMT
			INTO #ALS
			FROM  #ALL_ALS 
			WHERE isnull(PD,'') <> ''			
			AND ((PD in ('AL','PL','BOOCPL') and CDC <= 4) or (PD not in ('AL','PL','BOOCPL') and CDC<=6))
			GROUP BY PD,CDC,CYCLEID,CACS_STATE
			SET @STEP = '9'
			/* 
			===== ECS =====
			*/
			if object_id('tempdb..#CARDS') is not null drop table #CARDS
			Select * Into #CARDS From CARDS Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CARDS SELECT * FROM ' + @CARDS + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '10'
			if object_id('tempdb..#ALL_ECS') is not null drop table #ALL_ECS
			SELECT  DATEAS,IIF(CDC=0,1,CDC) AS CDC,CYCLEID,CACS_STATE,BALANCE/1000 AS BALANCE,IIF(BUSINESS='CTL','BOOCCA',RIGHT(dbo.ALLTRIM(BUSINESS),2)) AS PD 
			INTO #ALL_ECS
			FROM #CARDS
			WHERE ((CDC BETWEEN 1 AND 3) OR (CDC=0 AND CACS_STATE='F24')) AND WOAMT=0 AND LOCATION<>'020007'
			SET @STEP = '11'
			UPDATE #ALL_ECS SET PD='ASSO' WHERE PD='AC'
			UPDATE #ALL_ECS SET PD='ILCA' WHERE PD='AW'
			UPDATE #ALL_ECS SET PD='DN' WHERE PD='DC'
			UPDATE #ALL_ECS SET PD='CITI' WHERE PD='CT'
			SET @STEP = '12'
			if object_id('tempdb..#ECS') is not null drop table #ECS
			SELECT PD,CDC,CYCLEID,CACS_STATE,COUNT(*) AS ACCT,SUM(BALANCE) AS AMT 
			INTO #ECS
			FROM #ALL_ECS 
			WHERE isnull(PD,'') <> ''
			GROUP BY PD,CDC,CYCLEID,CACS_STATE
			SET @STEP = '13'
			INSERT INTO #ECS
			SELECT 'CARD' AS PD,CDC,CYCLEID AS CYCLEID,CACS_STATE,COUNT(*) AS ACCT,ROUND(SUM(BALANCE),0) AS AMT 
			FROM #ALL_ECS 
			WHERE PD IN ('CITI','ASSO','ILCA') 
			GROUP BY CDC,CYCLEID,CACS_STATE
			SET @STEP = '20'
			/* 
			===== INSERT =====
			*/
			DELETE FROM RPT_ACCTROUT_ACCT WHERE MIS_DATE=@V_MIS_DATE
			DELETE FROM RPT_ACCTROUT_AMT WHERE MIS_DATE=@V_MIS_DATE
			DECLARE @INTLOOP INT
			SET @INTLOOP = 1
			WHILE (@INTLOOP <= 2)
			BEGIN
				DECLARE @PD VARCHAR(10)
				DECLARE @CDC INT
				IF (@INTLOOP=1)
				BEGIN
					SET @STEP = '21'
					DECLARE CURSOR_ALS CURSOR FAST_FORWARD FOR
						SELECT PD FROM #ALS GROUP BY PD
					OPEN CURSOR_ALS
					FETCH NEXT FROM CURSOR_ALS INTO @PD
					WHILE @@FETCH_STATUS = 0
					BEGIN
						SET @STEP = '22'
						IF (@PD IN ('AL','PL','BOPL'))
						BEGIN
							SET @CDC=4
						END
						ELSE
						BEGIN
							SET @CDC=6
						END
						DECLARE @MM INT
						SET @MM = 1
						WHILE (@MM <= @CDC)
						BEGIN
							SET @STEP = '23'
							DECLARE @CACS_STATE VARCHAR(3)
							DECLARE CURSOR_ALS_STATE CURSOR FAST_FORWARD FOR
								SELECT DISTINCT CACS_STATE FROM #ALS WHERE PD=@PD and CDC=@MM ORDER BY CACS_STATE
							OPEN CURSOR_ALS_STATE
							FETCH NEXT FROM CURSOR_ALS_STATE INTO @CACS_STATE
							WHILE @@FETCH_STATUS = 0
							BEGIN
								SET @STEP = '24'
								DECLARE @PD_RESULT VARCHAR(10)
								SET @PD_RESULT = @PD + 'M' + CAST(@MM AS VARCHAR)
								INSERT INTO RPT_ACCTROUT_ACCT(MIS_DATE,PDTYPE,PD_RESULT,STATE,CDC) VALUES(@V_MIS_DATE,'ASSET',@PD_RESULT,@CACS_STATE,@MM)
								INSERT INTO RPT_ACCTROUT_AMT(MIS_DATE,PDTYPE,PD_RESULT,STATE,CDC) VALUES(@V_MIS_DATE,'ASSET',@PD_RESULT,@CACS_STATE,@MM)
								DECLARE @CYCLEID VARCHAR(3)
								DECLARE @ACCT INT
								DECLARE @AMT NUMERIC(12,2)
								DECLARE CURSOR_ALS_UPDATE CURSOR FAST_FORWARD FOR
									SELECT 'D'+CAST(CYCLEID AS VARCHAR) CYC,ACCT,AMT FROM #ALS where PD=@PD and cdc=@MM and CACS_STATE=@CACS_STATE order by CYCLEID
								OPEN CURSOR_ALS_UPDATE
								FETCH NEXT FROM CURSOR_ALS_UPDATE INTO @CYCLEID,@ACCT,@AMT
								WHILE @@FETCH_STATUS = 0
								BEGIN
									SET @STEP = '25'
									SET @SQLCmd = N'UPDATE RPT_ACCTROUT_ACCT SET '+@CYCLEID+'='+CAST(@ACCT AS VARCHAR)+' WHERE MIS_DATE = '''+@V_MIS_DATE+''' AND PDTYPE=''ASSET'' AND PD_RESULT='''+@PD_RESULT+''' AND STATE='''+@CACS_STATE+''' AND CDC='''+CAST(@MM AS VARCHAR)+''' '
									EXECUTE sp_executesql @SQLCmd
									SET @SQLCmd = N'UPDATE RPT_ACCTROUT_AMT SET '+@CYCLEID+'='+CAST(@AMT AS VARCHAR)+' WHERE MIS_DATE = '''+@V_MIS_DATE+''' AND PDTYPE=''ASSET'' AND PD_RESULT='''+@PD_RESULT+''' AND STATE='''+@CACS_STATE+''' AND CDC='''+CAST(@MM AS VARCHAR)+''' '
									EXECUTE sp_executesql @SQLCmd
									FETCH NEXT FROM CURSOR_ALS_UPDATE INTO @CYCLEID,@ACCT,@AMT
								END
								CLOSE CURSOR_ALS_UPDATE
								DEALLOCATE CURSOR_ALS_UPDATE
								FETCH NEXT FROM CURSOR_ALS_STATE INTO @CACS_STATE
							END
							CLOSE CURSOR_ALS_STATE
							DEALLOCATE CURSOR_ALS_STATE
							SET @MM = @MM + 1
						END
						FETCH NEXT FROM CURSOR_ALS INTO @PD
					END
					CLOSE CURSOR_ALS
					DEALLOCATE CURSOR_ALS
				END
				ELSE
				BEGIN
					SET @STEP = '30'
					DECLARE CURSOR_ECS CURSOR FAST_FORWARD FOR
						SELECT PD FROM #ECS GROUP BY PD
					OPEN CURSOR_ECS
					FETCH NEXT FROM CURSOR_ECS INTO @PD
					WHILE @@FETCH_STATUS = 0
					BEGIN
						SET @STEP = '31'
						SET @MM = 1
						WHILE (@MM <= 3)
						BEGIN
							SET @STEP = '32'
							DECLARE CURSOR_ECS_STATE CURSOR FAST_FORWARD FOR
								SELECT DISTINCT CACS_STATE FROM #ECS WHERE PD=@PD and CDC=@MM ORDER BY CACS_STATE
							OPEN CURSOR_ECS_STATE
							FETCH NEXT FROM CURSOR_ECS_STATE INTO @CACS_STATE
							WHILE @@FETCH_STATUS = 0
							BEGIN
								SET @STEP = '33'
								SET @PD_RESULT = @PD + 'M' + CAST(@MM AS VARCHAR)
								INSERT INTO RPT_ACCTROUT_ACCT(MIS_DATE,PDTYPE,PD_RESULT,STATE,CDC) VALUES(@V_MIS_DATE,'CARDS',@PD_RESULT,@CACS_STATE,@MM)
								INSERT INTO RPT_ACCTROUT_AMT(MIS_DATE,PDTYPE,PD_RESULT,STATE,CDC) VALUES(@V_MIS_DATE,'CARDS',@PD_RESULT,@CACS_STATE,@MM)
								DECLARE CURSOR_ECS_UPDATE CURSOR FAST_FORWARD FOR
									SELECT 'D'+CAST(CYCLEID AS VARCHAR) CYC,ACCT,AMT FROM #ECS where PD=@PD and cdc=@MM and CACS_STATE=@CACS_STATE order by CYCLEID
								OPEN CURSOR_ECS_UPDATE
								FETCH NEXT FROM CURSOR_ECS_UPDATE INTO @CYCLEID,@ACCT,@AMT
								WHILE @@FETCH_STATUS = 0
								BEGIN
									SET @STEP = '34'
									SET @SQLCmd = N'UPDATE RPT_ACCTROUT_ACCT SET '+@CYCLEID+'='+CAST(@ACCT AS VARCHAR)+' WHERE MIS_DATE = '''+@V_MIS_DATE+''' AND PDTYPE=''CARDS'' AND PD_RESULT='''+@PD_RESULT+''' AND STATE='''+@CACS_STATE+''' AND CDC='''+CAST(@MM AS VARCHAR)+''' '
									EXECUTE sp_executesql @SQLCmd
									SET @SQLCmd = N'UPDATE RPT_ACCTROUT_AMT SET '+@CYCLEID+'='+CAST(@AMT AS VARCHAR)+' WHERE MIS_DATE = '''+@V_MIS_DATE+''' AND PDTYPE=''CARDS'' AND PD_RESULT='''+@PD_RESULT+''' AND STATE='''+@CACS_STATE+''' AND CDC='''+CAST(@MM AS VARCHAR)+''' '
									EXECUTE sp_executesql @SQLCmd
									FETCH NEXT FROM CURSOR_ECS_UPDATE INTO @CYCLEID,@ACCT,@AMT
								END
								CLOSE CURSOR_ECS_UPDATE
								DEALLOCATE CURSOR_ECS_UPDATE
								FETCH NEXT FROM CURSOR_ECS_STATE INTO @CACS_STATE
							END
							CLOSE CURSOR_ECS_STATE
							DEALLOCATE CURSOR_ECS_STATE
							SET @MM = @MM + 1
						END
						FETCH NEXT FROM CURSOR_ECS INTO @PD
					END
					CLOSE CURSOR_ECS
					DEALLOCATE CURSOR_ECS
				END
				SET @STEP = '35'
				SET @INTLOOP = @INTLOOP + 1
			END
			SET @STEP = '50'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ACCTROUT_sp','ACCTROUT資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', BUDTRAN or CARDS尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ACCTROUT_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

