ALTER PROCEDURE [dbo].[TRANS_REPORT_ASSET_DELQ_SOC_SP]
	@V_MIS_DATE VARCHAR(10)
  , @iCNYGraceDt INT
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	DECLARE @V_DATE_BEF VARCHAR(10)
	SET @V_DATE_BEF = CONVERT(VARCHAR,DATEADD(MS,-3,DATEADD(MM, DATEDIFF(M,0,@D_MIS_DATE), 0)) ,23)
	DECLARE @D_DATE_BEF DATE
	SET @D_DATE_BEF = CAST(@V_DATE_BEF AS DATE)
	PRINT @V_DATE_BEF
	DECLARE @V_MON_E VARCHAR(10)
	SET @V_MON_E = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE)+1, 0)), 23)
	DECLARE @D_MON_E DATE
	SET @D_MON_E = CAST(@V_MON_E AS DATE)
	PRINT @V_MON_E
	DECLARE @M1_CYC TINYINT
	SET @M1_CYC=IIF(DAY(@D_MIS_DATE)-@iCNYGraceDt>0, DAY(@D_MIS_DATE)-@iCNYGraceDt,IIF(DAY(@D_MIS_DATE)=@iCNYGraceDt,31,IIF(DAY(@D_DATE_BEF)=31 AND DAY(@D_MIS_DATE)<@iCNYGraceDt,DAY(@D_MIS_DATE)+27, DAY(@D_DATE_BEF)-@iCNYGraceDt+DAY(@D_MIS_DATE))))
	DECLARE @M2_CYC TINYINT
	IF (@V_MIS_DATE=@V_MON_E)
	BEGIN
		SET @M2_CYC=31
	END
	ELSE
	BEGIN
		SET @M2_CYC=DAY(@D_MIS_DATE)
	END
	PRINT @M1_CYC
	PRINT @M2_CYC
	BEGIN TRY
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		DECLARE @YYYYMMDD_LAST VARCHAR(8)
		SET @YYYYMMDD_LAST = Convert(varchar,@D_DATE_BEF,112)
		PRINT @YYYYMMDD
		PRINT @YYYYMMDD_LAST
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD_LAST, @OUTPUT2 output
		DECLARE @OUTPUT3 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'AS_CIR',@YYYYMMDD, @OUTPUT3 output
		DECLARE @OUTPUT4 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'AS_CIR',@YYYYMMDD_LAST, @OUTPUT4 output
		PRINT @OUTPUT1
		PRINT @OUTPUT2
		PRINT @OUTPUT3
		PRINT @OUTPUT4
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S' and left(@OUTPUT3,1) = 'S' and left(@OUTPUT4,1) = 'S')
		BEGIN
			SET @STEP = '4'
			DELETE FROM LOG_AUTOMATION WHERE MIS_DATE=@V_MIS_DATE AND TABLE_NAME='TRANS_REPORT_ASSET_DELQ_SOC_sp'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_DELQ_SOC_sp','ASSET DELQ SOC資料處理開始','N'
			DELETE FROM RPT_ASSET_DELQ_SOC_CYC WHERE MIS_DATE=@V_MIS_DATE
			DECLARE @ALSEOM VARCHAR(50)
			SET @ALSEOM = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @ALSEOM_LAST VARCHAR(50)
			SET @ALSEOM_LAST = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			DECLARE @AS_CIR VARCHAR(50)
			SET @AS_CIR = RIGHT(@OUTPUT3,LEN(@OUTPUT3)-2)
			DECLARE @AS_CIR_LAST VARCHAR(50)
			SET @AS_CIR_LAST = RIGHT(@OUTPUT4,LEN(@OUTPUT4)-2)
			SET @STEP = '5'
			if object_id('tempdb..#ALSEOM') is not null drop table #ALSEOM
			Select * Into #ALSEOM From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM SELECT * FROM ' + @ALSEOM + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#ALSEOM_LAST') is not null drop table #ALSEOM_LAST
			Select * Into #ALSEOM_LAST From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM_LAST SELECT * FROM ' + @ALSEOM_LAST + ' WHERE MIS_DATE = ''' + @V_DATE_BEF + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#AS_CIR') is not null drop table #AS_CIR
			Select * Into #AS_CIR From AS_CIR Where 1> 2
			ALTER TABLE #AS_CIR DROP COLUMN SEQNO
			ALTER TABLE #AS_CIR ADD SEQNO INT
			SET @SQLCmd = N'INSERT INTO #AS_CIR SELECT * FROM ' + @AS_CIR + ' WHERE MIS_DATE BETWEEN ''' + LEFT(@V_MIS_DATE,8) + '01'' AND ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#AS_CIR_LAST') is not null drop table #AS_CIR_LAST
			Select * Into #AS_CIR_LAST From AS_CIR Where 1> 2
			ALTER TABLE #AS_CIR_LAST DROP COLUMN SEQNO
			ALTER TABLE #AS_CIR_LAST ADD SEQNO INT
			SET @SQLCmd = N'INSERT INTO #AS_CIR_LAST SELECT * FROM ' + @AS_CIR_LAST 
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '6'
			IF (DAY(@D_MIS_DATE)=@iCNYGraceDt)
			BEGIN
				UPDATE #AS_CIR_LAST SET #AS_CIR_LAST.CDC=a.CDC
				FROM #ALSEOM_LAST a
				INNER JOIN #AS_CIR_LAST b on a.CARDNMBR=b.CARDNMBR and (LEFT(a.CACS_STATE,1)='N' OR LEFT(a.CACS_STATE,1)='Y')
				WHERE (b.SEGMENT like '%PLR4%' OR b.SEGMENT like '%PLR6%') 
				AND (LEFT(b.CACS_STATE,1)='N' OR LEFT(b.CACS_STATE,1)='Y')
			END
			/*SELECT * FROM PM INTO DBF PM_ADJ
			IF DAY(DATE4)=4
				LAST_M_END=CTOD(LEFT(DTOC(DATE4),8)+'01')-1
				USE 'M:\DATASET\ALSEOM\ALSEOM'+SUBSTR(DTOS(LAST_M_END),3,6)+'.DBF' IN 0 SHARED ORDER TAG ACCTNMBR ALIAS ALS_END
				SELECT PM_ADJ
				SCAN FOR INLIST(SEGMENT,'PLR4','PLR6') AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
					SCATTER MEMVAR
					SELE ALS_END
						IF SEEK(ALLT(M.CARDNMBR)) AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
							M.CDC=CDC
							SELECT PM_ADJ
							GATHER MEMVAR
						ENDIF
				ENDSCAN
			ENDIF*/
			SET @STEP = '7'
			UPDATE #AS_CIR SET #AS_CIR.CDC=a.CDC
			FROM #ALSEOM a
			INNER JOIN #AS_CIR b on a.CARDNMBR=b.CARDNMBR and (LEFT(a.CACS_STATE,1)='N' OR LEFT(a.CACS_STATE,1)='Y')
			WHERE (b.SEGMENT like '%PLR4%' OR b.SEGMENT like '%PLR6%') 
			AND (LEFT(b.CACS_STATE,1)='N' OR LEFT(b.CACS_STATE,1)='Y')
			/*SELECT * FROM CM INTO DBF CM_ADJ
			*THIS_M_END=CTOD(LEFT(DTOC(GOMONTH(DATE4,1)),8)+'01')-1
			*IF DATE4=THIS_M_END
				SELECT CM_ADJ
				SCAN FOR INLIST(SEGMENT,'PLR4','PLR6') AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
					SCATTER MEMVAR
					SELE ALSEOM
						IF SEEK(ALLT(M.CARDNMBR)) AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
							M.CDC=CDC
							SELECT CM_ADJ
							GATHER MEMVAR
						ENDIF
				ENDSCAN
			*ENDIF*/
			SET @STEP = '8'
			if object_id('tempdb..#TEMP') is not null drop table #TEMP
			CREATE TABLE #TEMP
			(
				SEGMENT VARCHAR(10),
				SEGMENT_O VARCHAR(10),
				ACCTTYPE VARCHAR(3),
				CARDNMBR NUMERIC(16),
				CDC tinyint,
				BILLDAY tinyint,
				NBV NUMERIC(12,2),
				TAG  VARCHAR(8)
			)
			IF (DAY(@D_MIS_DATE)<=@iCNYGraceDt)
			BEGIN
				INSERT INTO #TEMP
				SELECT '' AS SEGMENT,SEGMENT AS SEGMENT_O, ACCTTYPE, CARDNMBR,CDC, ROUND(CAST(BILLDAY AS int),0) AS BILLDAY, NBV ,'M'+CAST(CDC AS VARCHAR)+'SOC' AS TAG
				FROM #AS_CIR_LAST				
				WHERE NBV>0 AND LOANSTAT='AC' AND isnull(SEGMENT,'') != '' AND CDC=1 AND CAST(BILLDAY AS int)<=@M1_CYC 
				UNION
				SELECT '' AS SEGMENT,SEGMENT AS SEGMENT_O, ACCTTYPE, CARDNMBR,CDC+1 AS CDC, ROUND(CAST(BILLDAY AS int),0) AS BILLDAY, NBV ,'M'+CAST(CDC AS VARCHAR)+'EOM' AS TAG 
				FROM #ALSEOM 	
				WHERE NBV>0 AND LOANSTAT='AC' AND isnull(SEGMENT,'') != '' AND CDC=0 AND CAST(BILLDAY AS int)>@M1_CYC 
				UNION
				SELECT '' AS SEGMENT,SEGMENT AS SEGMENT_O, ACCTTYPE, CARDNMBR,CDC AS CDC, ROUND(CAST(BILLDAY AS int),0) AS BILLDAY, NBV ,'M'+CAST(CDC AS VARCHAR)+'SOC' AS TAG 
				FROM #AS_CIR		
				WHERE NBV>0 AND LOANSTAT='AC' AND isnull(SEGMENT,'') != '' AND CDC>1 AND CAST(BILLDAY AS int)<=@M2_CYC 
				UNION
				SELECT '' AS SEGMENT,SEGMENT AS SEGMENT_O, ACCTTYPE, CARDNMBR,CDC+1 AS CDC, ROUND(CAST(BILLDAY AS int),0) AS BILLDAY, NBV ,'M'+CAST(CDC AS VARCHAR)+'EOM' AS TAG 
				FROM #ALSEOM
				WHERE NBV>0 AND LOANSTAT='AC' AND isnull(SEGMENT,'') != '' AND CAST(BILLDAY AS int)>@M2_CYC
				AND (CDC BETWEEN 1 and IIF(SEGMENT in ('ML','MP'),7,6))
			END
			ELSE
			BEGIN
				INSERT INTO #TEMP
				SELECT '' AS SEGMENT,SEGMENT AS SEGMENT_O, ACCTTYPE, CARDNMBR,CDC, ROUND(CAST(BILLDAY AS int),0) AS BILLDAY, NBV ,'M'+CAST(CDC AS VARCHAR)+'SOC' AS TAG 
				FROM #AS_CIR
				WHERE NBV>0 AND LOANSTAT='AC' AND isnull(SEGMENT,'') != '' AND CDC=1 AND CAST(BILLDAY AS int)<=@M1_CYC 
				UNION
				SELECT '' AS SEGMENT,SEGMENT AS SEGMENT_O, ACCTTYPE, CARDNMBR,CDC+1 AS CDC, ROUND(CAST(BILLDAY AS int),0) AS BILLDAY, NBV ,'M'+CAST(CDC AS VARCHAR)+'EOM' AS TAG 
				FROM #ALSEOM
				WHERE NBV>0 AND LOANSTAT='AC' AND isnull(SEGMENT,'') != '' AND CDC=0 AND CAST(BILLDAY AS int)>@M1_CYC 
				UNION
				SELECT '' AS SEGMENT,SEGMENT AS SEGMENT_O, ACCTTYPE, CARDNMBR,CDC AS CDC, ROUND(CAST(BILLDAY AS int),0) AS BILLDAY, NBV ,'M'+CAST(CDC AS VARCHAR)+'SOC' AS TAG 
				FROM #AS_CIR
				WHERE NBV>0 AND LOANSTAT='AC' AND isnull(SEGMENT,'') != '' AND CDC>1 AND CAST(BILLDAY AS int)<=@M2_CYC 
				UNION
				SELECT '' AS SEGMENT,SEGMENT AS SEGMENT_O, ACCTTYPE, CARDNMBR,CDC+1 AS CDC, ROUND(CAST(BILLDAY AS int),0) AS BILLDAY, NBV ,'M'+CAST(CDC AS VARCHAR)+'EOM' AS TAG 
				FROM #ALSEOM
				WHERE NBV>0 AND LOANSTAT='AC' AND isnull(SEGMENT,'') != '' AND CAST(BILLDAY AS int)>@M2_CYC 
				AND (CDC BETWEEN 1 and IIF(SEGMENT in ('ML','MP'),7,6))
			END
			SET @STEP = '9'
			DECLARE @intPDCnt INT
			SET @intPDCnt = 1
			WHILE (@intPDCnt <= 10)
			BEGIN
				SET @STEP = '10'
				DECLARE @PD VARCHAR(10)
				DECLARE @PD_M VARCHAR(500)
				IF (@intPDCnt = 1)
				BEGIN
					SET @PD='ML'
					SET @PD_M='SEGMENT_O=''ML'' '
				END
				ELSE IF (@intPDCnt = 2)
				BEGIN
					SET @PD='MP'
					SET @PD_M='SEGMENT_O=''MP'' AND ACCTTYPE not in (''020'') '
				END
				ELSE IF (@intPDCnt = 3)
				BEGIN
					SET @PD='PLBAU'
					SET @PD_M='(SEGMENT_O=''PL'' OR SEGMENT_O=''PLRE'') AND ACCTTYPE not in (''353'') '
				END
				ELSE IF (@intPDCnt = 4)
				BEGIN
					SET @PD='PLBAURW'
					SET @PD_M='ACCTTYPE in (''308'',''340'',''360'',''365'') '
				END
				ELSE IF (@intPDCnt = 5)
				BEGIN
					SET @PD='PLBKRW'
					SET @PD_M='ACCTTYPE in (''306'',''307'',''336'',''337'',''341'',''342'',''324'',''339'',''363'',''364'') '
				END
				ELSE IF (@intPDCnt = 6)
				BEGIN
					SET @PD='PLPCLRW'
					SET @PD_M='ACCTTYPE in (''371'',''374'') '
				END
				ELSE IF (@intPDCnt = 7)
				BEGIN
					SET @PD='PLRCRW'
					SET @PD_M='ACCTTYPE in (''305'',''315'',''317'',''338'') '
				END
				ELSE IF (@intPDCnt = 8)
				BEGIN
					SET @PD='PLBADRP'
					SET @PD_M='SEGMENT_O=''PLR4'' AND ACCTTYPE not in (''354'') '
				END
				ELSE IF (@intPDCnt = 9)
				BEGIN
					SET @PD='PLIDRP'
					SET @PD_M='SEGMENT_O=''PLR6'' AND ACCTTYPE not in (''372'',''373'') '
				END
				ELSE IF (@intPDCnt = 10)
				BEGIN
					SET @PD='PLIDRPPCL'
					SET @PD_M='ACCTTYPE in (''372'',''373'') '
				END
				SET @STEP = '11'
				SET @SQLCmd = N'UPDATE #TEMP SET SEGMENT=''' + @PD + ''' WHERE ' + @PD_M
				EXECUTE sp_executesql @SQLCmd
				SET @STEP = '12'
				DECLARE @intCDC INT, @intCDC_E INT
				SET @intCDC = 1
				SET @intCDC_E = IIF(@PD in ('ML','MP'),7,5)
				WHILE (@intCDC <= @intCDC_E)
				BEGIN
					DECLARE @intCYCLE INT = 1
					WHILE (@intCYCLE <= 31)
					BEGIN
						SET @STEP = '13'
						INSERT INTO RPT_ASSET_DELQ_SOC_CYC VALUES(@V_MIS_DATE,@PD,@intCDC,@intCYCLE,0,0)
						SET @intCYCLE = @intCYCLE + 1
					END
					SET @intCDC = @intCDC + 1
				END
				SET @intPDCnt = @intPDCnt + 1
			END
			SET @STEP = '14'
			DELETE FROM #TEMP WHERE SEGMENT=''
			SET @STEP = '15'
			if object_id('tempdb..#TEMP2') is not null drop table #TEMP2
			CREATE TABLE #TEMP2
			(
				PD			VARCHAR(10),
				CDC			NUMERIC(1),
				CYCLE		NUMERIC(2),
				ACCT		INT,
				AMT			NUMERIC(18,2)
			)
			INSERT INTO #TEMP2
			SELECT SEGMENT, CAST(CDC AS tinyint), BILLDAY, COUNT(*) AS ACCT, SUM(NBV)/1000 AS AMNT
			FROM #TEMP 
			WHERE CDC<=7 
			GROUP BY SEGMENT,CDC, BILLDAY
			SET @STEP = '16'
			UPDATE RPT_ASSET_DELQ_SOC_CYC SET RPT_ASSET_DELQ_SOC_CYC.ACCT=#TEMP2.ACCT
											, RPT_ASSET_DELQ_SOC_CYC.AMT=#TEMP2.AMT
			FROM #TEMP2
			WHERE RPT_ASSET_DELQ_SOC_CYC.PD=#TEMP2.PD
			AND RPT_ASSET_DELQ_SOC_CYC.CDC=#TEMP2.CDC
			AND RPT_ASSET_DELQ_SOC_CYC.CYCLE=#TEMP2.CYCLE
			AND MIS_DATE=@V_MIS_DATE
			IF (DAY(@D_MIS_DATE)<@iCNYGraceDt)
			BEGIN
				DELETE FROM RPT_ASSET_DELQ_SOC_CYC_PMON WHERE MIS_DATE=@V_DATE_BEF AND CDC=1
				INSERT INTO RPT_ASSET_DELQ_SOC_CYC_PMON	
				SELECT @V_DATE_BEF,PD,CDC,CYCLE,ACCT,AMT FROM RPT_ASSET_DELQ_SOC_CYC WHERE MIS_DATE=@V_MIS_DATE AND CDC=1
			END
			SET @STEP = '20'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_DELQ_SOC_sp','ASSET DELQ SOC資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', ALSEOM及AS_CIR尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()-- + ', ' + @V_MIS_DATE + '-' + @PD + '-' + CAST(@intCDC AS VARCHAR) + '-' + CAST(@intCYCLE AS VARCHAR)
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_DELQ_SOC_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

