ALTER PROCEDURE [dbo].[TRANS_REPORT_ASSET_DELQ_sp]
	@V_MIS_DATE_TMP VARCHAR(10)
  , @iCNYGraceDt INT
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @MSG NVARCHAR(1000)=''
	BEGIN TRY
		DELETE FROM LOG_AUTOMATION WHERE MIS_DATE=@V_MIS_DATE_TMP AND TABLE_NAME='TRANS_REPORT_ASSET_DELQ_sp'
		EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp','ASSET DELQ資料處理開始','N'
		DECLARE @STEP VARCHAR(5)
		DECLARE @SQLCmd NVARCHAR(4000)
		DECLARE @D_MIS_DATE_TMP DATE
		SET @D_MIS_DATE_TMP = CAST(@V_MIS_DATE_TMP AS DATE)
		DECLARE @V_DATE_BEF_TMP VARCHAR(10)
		SET @V_DATE_BEF_TMP = CONVERT(VARCHAR,DATEADD(MS,-3,DATEADD(MM, DATEDIFF(M,0,@D_MIS_DATE_TMP), 0)) ,23)
		DECLARE @D_DATE_BEF_TMP DATE
		SET @D_DATE_BEF_TMP = CAST(@V_DATE_BEF_TMP AS DATE)
		PRINT @V_DATE_BEF_TMP
		DECLARE @V_MON_E_TMP VARCHAR(10)
		SET @V_MON_E_TMP = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE_TMP)+1, 0)), 23)
		DECLARE @D_MON_E_TMP DATE
		SET @D_MON_E_TMP = CAST(@V_MON_E_TMP AS DATE)
		PRINT @V_MON_E_TMP
		DECLARE @D_PMON_DATE DATE = DATEADD(M,-1,@D_MIS_DATE_TMP)	--MIS_DATE的上個月
		IF ((@D_PMON_DATE=@D_MIS_DATE_TMP) OR datepart(weekday,@D_PMON_DATE)=6  OR datepart(weekday,@D_PMON_DATE)=7 )
		BEGIN
			WHILE (datepart(weekday,@D_PMON_DATE) in (6,7))
			BEGIN
				SET @MSG='PMON DATE=' + convert(varchar,@D_PMON_DATE,112) + ' 為週五週六，資料改抓' + convert(varchar,DATEADD(D,1,@D_PMON_DATE),112)
				EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp', @MSG, 'N'
				SET @D_PMON_DATE = DATEADD(D,1,@D_PMON_DATE)
			END
		END
		ELSE
		BEGIN
			DECLARE @V_TMPDT1 VARCHAR(10) = CONVERT(VARCHAR,DATEADD(M,-1,@D_MIS_DATE_TMP),112)
			PRINT @V_TMPDT1
			DECLARE @TABLE_NAME1 VARCHAR(50) = 'ALSEOM_' + SUBSTRING(@V_TMPDT1,5,2)
			DECLARE @V_TMPDT2 VARCHAR(10) = CONVERT(VARCHAR,DATEADD(D,-1,DATEADD(M,-1,@D_MIS_DATE_TMP)),112)
			PRINT @V_TMPDT2
			DECLARE @TABLE_NAME2 VARCHAR(50) = 'ALSEOM_' + SUBSTRING(@V_TMPDT2,5,2)
			if object_id('tempdb..#DIV') is not null drop table #DIV
			CREATE TABLE #DIV(CHK INT)
			SET @SQLCmd = N'INSERT INTO #DIV SELECT distinct A.DPDNOW-B.DPDNOW '
			SET @SQLCmd = @SQLCmd + N'FROM ' + @TABLE_NAME1 + ' a '
			SET @SQLCmd = @SQLCmd + N'LEFT JOIN ' + @TABLE_NAME2 + ' b on a.ACCTNMBR=b.ACCTNMBR and b.MIS_DATE=''' + @V_TMPDT2 + ''' '
			SET @SQLCmd = @SQLCmd + N'WHERE a.CDC=3 AND a.SEGMENT=''ML'' AND a.MIS_DATE=''' + @V_TMPDT1 + ''' '
			SET @SQLCmd = @SQLCmd + N'and A.DPDNOW-B.DPDNOW >= 0 '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			DECLARE @DIV INT
			SELECT @DIV = CHK FROM #DIV
			IF (@DIV=1)
			BEGIN
				SET @MSG='上個月的今天為正常工作天或月底，資料抓['+ CONVERT(varchar,@D_PMON_DATE,23) + ']，請確認是否正確！'
				EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp', @MSG, 'N'
			END
			ELSE
			BEGIN
				IF (DAY(@D_DATE_BEF_TMP)-DAY(@D_MON_E_TMP)>0 AND @V_MIS_DATE_TMP=@V_MON_E_TMP)
				BEGIN
					SET @D_PMON_DATE=@D_DATE_BEF_TMP
					SET @MSG='更正------本月為小月，上個月的資料改抓月底['+ CONVERT(varchar,@D_PMON_DATE,23) + ']，請確認是否正確！'
					EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp', @MSG, 'N'
				END
				ELSE
				BEGIN
					SET @D_PMON_DATE = DATEADD(D,1,@D_PMON_DATE)
					SET @MSG='上個月的今天為假日，或月初為假日，資料改抓['+ CONVERT(varchar,@D_PMON_DATE,23) + ']，請確認是否正確！'
					EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp', @MSG, 'N'
				END
			END
		END
		DECLARE @V_PMON_DATE VARCHAR(10) = convert(varchar,@D_PMON_DATE,23)
		SET @MSG='PMON DATE='+ @V_PMON_DATE
		EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp', @MSG, 'N'
		PRINT @MSG
		DECLARE @M1_CYC TINYINT
		DECLARE @M2_CYC TINYINT
		DECLARE @M1_CYC_PMON TINYINT
		DECLARE @M2_CYC_PMON TINYINT
		DECLARE @iLoop INT = 1
		WHILE (@iLoop <= 2)
		BEGIN
			DECLARE @V_MIS_DATE VARCHAR(10)
			IF (@iLoop=1)
			BEGIN
				SET @V_MIS_DATE=@V_MIS_DATE_TMP
				DELETE FROM RPT_ASSET_DELQ_CYC WHERE MIS_DATE=@V_MIS_DATE_TMP
			END
			IF (@iLoop=2)
			BEGIN
				SET @V_MIS_DATE=@V_PMON_DATE
				DELETE FROM RPT_ASSET_DELQ_CYC_PMON WHERE MIS_DATE=@V_MIS_DATE_TMP
			END
			PRINT '*****iLoop=' + CAST(@iLoop AS VARCHAR) + ' MID_DATE=' + @V_MIS_DATE
			DECLARE @D_MIS_DATE DATE
			SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
			DECLARE @V_DATE_BEF VARCHAR(10)
			SET @V_DATE_BEF = CONVERT(VARCHAR,DATEADD(MS,-3,DATEADD(MM, DATEDIFF(M,0,@D_MIS_DATE), 0)) ,23)
			DECLARE @D_DATE_BEF DATE
			SET @D_DATE_BEF = CAST(@V_DATE_BEF AS DATE)
			PRINT @V_DATE_BEF
			DECLARE @V_MON_E VARCHAR(10)
			SET @V_MON_E = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE)+1, 0)), 23)
			DECLARE @D_MON_E DATE
			SET @D_MON_E = CAST(@V_MON_E AS DATE)
			PRINT @V_MON_E
			SET @M1_CYC=IIF(DAY(@D_MIS_DATE)-@iCNYGraceDt>0, DAY(@D_MIS_DATE)-@iCNYGraceDt,IIF(DAY(@D_MIS_DATE)=@iCNYGraceDt,31,IIF(DAY(@D_DATE_BEF)=31 AND DAY(@D_MIS_DATE)<@iCNYGraceDt,DAY(@D_MIS_DATE)+27, DAY(@D_DATE_BEF)-@iCNYGraceDt+DAY(@D_MIS_DATE))))
			IF (@V_MIS_DATE=@V_MON_E)
			BEGIN
				SET @M1_CYC=31	--如果是月底就讓M0抓到月底現在的樣子，才能跟Monthly平(add by Jure 2015/6)
			END
			SET @M1_CYC_PMON = @M1_CYC
			IF (@V_MIS_DATE=@V_MON_E)
			BEGIN
				SET @M2_CYC=31
			END
			ELSE
			BEGIN
				SET @M2_CYC=DAY(@D_MIS_DATE)
			END
			SET @MSG = 'Current Month 更新M0/1 資料至CYCLE=' + CAST(@M1_CYC AS VARCHAR)
			EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp',@MSG,'N'
			PRINT @MSG
			SET @MSG = 'Current Month 更新M2+ 資料至CYCLE=' + CAST(@M2_CYC AS VARCHAR)
			EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp',@MSG,'N'
			PRINT @MSG
			IF ((DAY(@V_MON_E)-DAY(@D_MIS_DATE) <= 2) AND datepart(weekday,@V_MON_E)=7)
			BEGIN
				SET @M1_CYC_PMON=@M1_CYC_PMON+1
			END
			ELSE IF ((DAY(@V_MON_E)-DAY(@D_MIS_DATE) <= 2) AND datepart(weekday,@V_MON_E)=1)
			BEGIN
				SET @M1_CYC_PMON=@M1_CYC_PMON+2
			END
			IF (@V_MIS_DATE=@V_MON_E)
			BEGIN
				SET @M2_CYC_PMON=31
			END
			ELSE IF ((DAY(@V_MON_E)-DAY(@D_MIS_DATE) <= 2) AND (datepart(weekday,@V_MON_E)=7 OR datepart(weekday,@V_MON_E)=1))
			BEGIN
				SET @M2_CYC_PMON=31
			END
			ELSE
			BEGIN
				SET @M2_CYC_PMON=DAY(@D_MIS_DATE)
			END
			SET @MSG = 'Previous Month 更新M0/1 資料至CYCLE=' + CAST(@M1_CYC_PMON AS VARCHAR)
			EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp',@MSG,'N'
			PRINT @MSG
			SET @MSG = 'Previous Month 更新M2+ 資料至CYCLE=' + CAST(@M2_CYC_PMON AS VARCHAR)
			EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp',@MSG,'N'
			PRINT @MSG
			SET @STEP = '1'
			DECLARE @YYYYMMDD VARCHAR(8)
			SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
			PRINT @YYYYMMDD
			SET @STEP = '2'
			DECLARE @OUTPUT1 VARCHAR(50)
			EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD, @OUTPUT1 output		
			PRINT @OUTPUT1
			SET @STEP = '3'
			IF (left(@OUTPUT1,1) = 'S')
			BEGIN
				SET @STEP = '4'
				DECLARE @ALSEOM VARCHAR(50)
				SET @ALSEOM = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)			
				PRINT @ALSEOM
				SET @STEP = '5'
				if object_id('tempdb..#ALSEOM') is not null drop table #ALSEOM
				Select * Into #ALSEOM From ALSEOM Where 1> 2
				SET @SQLCmd = N'INSERT INTO #ALSEOM SELECT * FROM ' + @ALSEOM + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
				PRINT @SQLCmd
				EXECUTE sp_executesql @SQLCmd
				SET @STEP = '6'
				if object_id('tempdb..#TEMP') is not null drop table #TEMP
				CREATE TABLE #TEMP
				(
					SEGMENT VARCHAR(10),
					SEGMENT_O VARCHAR(10),
					ACCTTYPE VARCHAR(3),
					CARDNMBR NUMERIC(16),
					PMTDUE tinyint,
					BILLDAY tinyint,
					NBV NUMERIC(12,2)
				)
				INSERT INTO #TEMP
				SELECT  '' AS SEGMENT, SEGMENT AS SEGMENT_O, ACCTTYPE, ACCTNMBR, PMTDUE, BILLDAY, NBV
				FROM #ALSEOM 
				WHERE LOANSTAT='AC'AND NBV>0 
				AND isnull(SEGMENT,'') != ''
				SET @STEP = '7'
				DECLARE @intPDCnt INT
				SET @intPDCnt = 1
				WHILE (@intPDCnt <= 10)
				BEGIN
					SET @STEP = '10'
					DECLARE @PD VARCHAR(10)
					DECLARE @PD_M VARCHAR(500)
					IF (@intPDCnt = 1)
					BEGIN
						SET @PD='ML'
						SET @PD_M='SEGMENT_O=''ML'' AND ACCTTYPE not in (''968'',''978'',''988'',''998'') '
					END
					ELSE IF (@intPDCnt = 2)
					BEGIN
						SET @PD='MP'
						SET @PD_M='SEGMENT_O=''MP'' AND ACCTTYPE not in (''020'') '
					END
					ELSE IF (@intPDCnt = 3)
					BEGIN
						SET @PD='PLBAU'
						SET @PD_M='(SEGMENT_O=''PL'' OR SEGMENT_O=''PLRE'') AND ACCTTYPE not in (''213'',''214'',''313'',''314'',''353'') '
					END
					ELSE IF (@intPDCnt = 4)
					BEGIN
						SET @PD='PLBAURW'
						SET @PD_M='ACCTTYPE in (''308'',''340'',''360'',''365'') '
					END
					ELSE IF (@intPDCnt = 5)
					BEGIN
						SET @PD='PLBKRW'
						SET @PD_M='ACCTTYPE in (''306'',''307'',''336'',''337'',''341'',''342'',''324'',''339'',''363'',''364'') '
					END
					ELSE IF (@intPDCnt = 6)
					BEGIN
						SET @PD='PLPCLRW'
						SET @PD_M='ACCTTYPE in (''371'',''374'') '
					END
					ELSE IF (@intPDCnt = 7)
					BEGIN
						SET @PD='PLRCRW'
						SET @PD_M='ACCTTYPE in (''305'',''315'',''317'',''338'') '
					END
					ELSE IF (@intPDCnt = 8)
					BEGIN
						SET @PD='PLBADRP'
						SET @PD_M='SEGMENT_O=''PLR4'' AND ACCTTYPE not in (''354'') '
					END
					ELSE IF (@intPDCnt = 9)
					BEGIN
						SET @PD='PLIDRP'
						SET @PD_M='SEGMENT_O=''PLR6'' AND ACCTTYPE not in (''372'',''373'') '
					END
					ELSE IF (@intPDCnt = 10)
					BEGIN
						SET @PD='PLIDRPPCL'
						SET @PD_M='ACCTTYPE in (''372'',''373'') '
					END
					SET @STEP = '11'
					SET @SQLCmd = N'UPDATE #TEMP SET SEGMENT=''' + @PD + ''' WHERE ' + @PD_M
					EXECUTE sp_executesql @SQLCmd
					SET @STEP = '12'
					DECLARE @intCDC INT, @intCDC_E INT
					SET @intCDC = 0
					SET @intCDC_E = 7
					WHILE (@intCDC <= @intCDC_E)
					BEGIN
						DECLARE @intCYCLE INT = 1
						WHILE (@intCYCLE <= 31)
						BEGIN
							SET @STEP = '13'
							IF (@iLoop=1)
							BEGIN
								INSERT INTO RPT_ASSET_DELQ_CYC VALUES(@V_MIS_DATE_TMP,@PD,@intCDC,@intCYCLE,0,0)
							END
							IF (@iLoop=2)
							BEGIN
								INSERT INTO RPT_ASSET_DELQ_CYC_PMON VALUES(@V_MIS_DATE_TMP,@PD,@intCDC,@intCYCLE,0,0)
							END
							SET @intCYCLE = @intCYCLE + 1
						END
						SET @intCDC = @intCDC + 1
					END
					SET @intPDCnt = @intPDCnt + 1
				END
				SET @STEP = '14'
				DELETE FROM #TEMP WHERE SEGMENT=''
				if object_id('tempdb..#TEMP2') is not null drop table #TEMP2
				CREATE TABLE #TEMP2
				(
					PD			VARCHAR(10),
					PMTDUE		tinyint,
					CYCLE		tinyint,
					ACCT		INT,
					AMT			NUMERIC(18,2)
				)
				INSERT INTO #TEMP2
				SELECT SEGMENT, CAST(PMTDUE AS NUMERIC), BILLDAY, COUNT(*) AS ACCT, SUM(NBV)/1000 AS AMNT
				FROM #TEMP 
				GROUP BY SEGMENT,PMTDUE, BILLDAY
				select * from #TEMP2 where PD='ML' and pmtdue=2order by cycle
				SET @STEP = '15'
				IF (@iLoop=1)
				BEGIN
					SET @STEP = '16'
					UPDATE RPT_ASSET_DELQ_CYC SET RPT_ASSET_DELQ_CYC.ACCT=#TEMP2.ACCT
													, RPT_ASSET_DELQ_CYC.AMT=#TEMP2.AMT
					FROM #TEMP2
					WHERE RPT_ASSET_DELQ_CYC.PD=#TEMP2.PD
					AND RPT_ASSET_DELQ_CYC.CDC=#TEMP2.PMTDUE
					AND RPT_ASSET_DELQ_CYC.CYCLE=#TEMP2.CYCLE
					AND RPT_ASSET_DELQ_CYC.MIS_DATE=@V_MIS_DATE_TMP
					DECLARE @intCDC2 INT
					SET @intCDC2 = 0
					WHILE (@intCDC2 <= 6)
					BEGIN
						DECLARE @WHERE NVARCHAR(500)=''
						IF (@intCDC2=0 and DAY(@D_MIS_DATE_TMP)-@iCNYGraceDt<=0)
						BEGIN
							EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp','M0不更新','N'
							SET @WHERE = N'AND RPT_ASSET_DELQ_CYC.CDC>0 '
						END
						if object_id('tempdb..#TEMP_CDC') is not null drop table #TEMP_CDC
						SELECT * INTO #TEMP_CDC FROM RPT_ASSET_DELQ_CYC WHERE MIS_DATE=@V_MIS_DATE_TMP AND CDC=@intCDC2+1
						DECLARE @CYCLE INT = IIF(@intCDC2=0,@M1_CYC,@M2_CYC)
						PRINT @CYCLE
						SET @STEP = '17-' + CAST(@intCDC2 AS VARCHAR)
						SET @SQLCmd = N'Update RPT_ASSET_DELQ_CYC SET RPT_ASSET_DELQ_CYC.ACCT=b.ACCT, RPT_ASSET_DELQ_CYC.AMT=b.AMT '
						SET @SQLCmd = @SQLCmd + N'FROM #TEMP_CDC b '
						SET @SQLCmd = @SQLCmd + N'WHERE RPT_ASSET_DELQ_CYC.MIS_DATE=b.MIS_DATE '
						SET @SQLCmd = @SQLCmd + N'AND RPT_ASSET_DELQ_CYC.PD=b.PD '
						SET @SQLCmd = @SQLCmd + N'AND RPT_ASSET_DELQ_CYC.CDC= ' + CAST(@intCDC2 AS VARCHAR)
						SET @SQLCmd = @SQLCmd + N' AND RPT_ASSET_DELQ_CYC.CYCLE=b.CYCLE '
						SET @SQLCmd = @SQLCmd + N'AND RPT_ASSET_DELQ_CYC.CYCLE <= '+ CAST(@CYCLE AS VARCHAR)
						SET @SQLCmd = @SQLCmd + N' AND RPT_ASSET_DELQ_CYC.MIS_DATE=''' + @V_MIS_DATE_TMP + ''' '
						SET @SQLCmd = @SQLCmd + @WHERE
						EXECUTE sp_executesql @SQLCmd
						SET @intCDC2 = @intCDC2 + 1
					END
				END
				SET @STEP = '18'
				IF (@iLoop=2)
				BEGIN
					SET @STEP = '19'
					UPDATE RPT_ASSET_DELQ_CYC_PMON SET RPT_ASSET_DELQ_CYC_PMON.ACCT=#TEMP2.ACCT
													, RPT_ASSET_DELQ_CYC_PMON.AMT=#TEMP2.AMT
					FROM #TEMP2
					WHERE RPT_ASSET_DELQ_CYC_PMON.PD=#TEMP2.PD
					AND RPT_ASSET_DELQ_CYC_PMON.CDC=#TEMP2.PMTDUE
					AND RPT_ASSET_DELQ_CYC_PMON.CYCLE=#TEMP2.CYCLE
					AND RPT_ASSET_DELQ_CYC_PMON.MIS_DATE=@V_MIS_DATE_TMP
					DECLARE @intCDC3 INT
					SET @intCDC3 = 7
					WHILE (@intCDC3 >= 1)
					BEGIN
						DECLARE @WHERE2 NVARCHAR(500)=''
						if object_id('tempdb..#TEMP_CDC_PMON') is not null drop table #TEMP_CDC_PMON
						SELECT * INTO #TEMP_CDC_PMON FROM RPT_ASSET_DELQ_CYC_PMON WHERE MIS_DATE=@V_MIS_DATE_TMP AND CDC=@intCDC3-1
						DECLARE @CYCLE2 INT = IIF(@intCDC3=1,@M1_CYC_PMON,@M2_CYC_PMON)
						PRINT @CYCLE2
						SET @STEP = '20-' + CAST(@intCDC3 AS VARCHAR)
						SET @SQLCmd = N'Update RPT_ASSET_DELQ_CYC_PMON SET RPT_ASSET_DELQ_CYC_PMON.ACCT=b.ACCT, RPT_ASSET_DELQ_CYC_PMON.AMT=b.AMT '
						SET @SQLCmd = @SQLCmd + N'FROM #TEMP_CDC_PMON b '
						SET @SQLCmd = @SQLCmd + N'WHERE RPT_ASSET_DELQ_CYC_PMON.MIS_DATE=b.MIS_DATE '
						SET @SQLCmd = @SQLCmd + N'AND RPT_ASSET_DELQ_CYC_PMON.PD=b.PD '
						SET @SQLCmd = @SQLCmd + N'AND RPT_ASSET_DELQ_CYC_PMON.CDC=' + CAST(@intCDC3 AS VARCHAR)
						SET @SQLCmd = @SQLCmd + N' AND RPT_ASSET_DELQ_CYC_PMON.CYCLE=b.CYCLE '
						SET @SQLCmd = @SQLCmd + N'AND RPT_ASSET_DELQ_CYC_PMON.CYCLE > ' + CAST(@CYCLE2 AS VARCHAR)
						SET @SQLCmd = @SQLCmd + N' AND RPT_ASSET_DELQ_CYC_PMON.MIS_DATE=''' + @V_MIS_DATE_TMP + ''' '
						SET @SQLCmd = @SQLCmd + @WHERE2
						EXECUTE sp_executesql @SQLCmd
						SET @intCDC3 = @intCDC3 - 1
					END
				END
				SET @STEP = '25'
			END
			ELSE
			BEGIN
				SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', ALSEOM尚未完成'
			END
			SET @iLoop = @iLoop + 1
		END
		SET @STEP = '30'
		EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp','ASSET DELQ資料處理完成','N'
		SET @OUTPUT = 'S'
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()-- + ', ' + @V_MIS_DATE + '-' + @PD + '-' + CAST(@intCDC AS VARCHAR) + '-' + CAST(@intCYCLE AS VARCHAR)
		EXEC SP_INSERT_LOG @V_MIS_DATE_TMP,'TRANS_REPORT_ASSET_DELQ_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

