ALTER PROCEDURE [dbo].[TRANS_REPORT_ASSET_FLOW_sp]
	@V_MIS_DATE VARCHAR(10)
  , @iCNYGraceDt INT
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(10)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @MSG NVARCHAR(500)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	DECLARE @V_DATE_BEF VARCHAR(10)
	SET @V_DATE_BEF = CONVERT(VARCHAR,DATEADD(MS,-3,DATEADD(MM, DATEDIFF(M,0,@D_MIS_DATE), 0)) ,23)
	DECLARE @D_DATE_BEF DATE
	SET @D_DATE_BEF = CAST(@V_DATE_BEF AS DATE)
	DECLARE @V_MON_E VARCHAR(10)
	SET @V_MON_E = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE)+1, 0)), 23)
	DECLARE @D_MON_E DATE
	SET @D_MON_E = CAST(@V_MON_E AS DATE)
	DECLARE @M1_CYC TINYINT
	SET @M1_CYC=IIF(DAY(@D_MIS_DATE)-@iCNYGraceDt>0, DAY(@D_MIS_DATE)-@iCNYGraceDt,IIF(DAY(@D_DATE_BEF)<>31 AND DAY(@D_MIS_DATE)<@iCNYGraceDt,DAY(@D_MIS_DATE)+26, DAY(@D_MIS_DATE)+27))
	DECLARE @M2_CYC TINYINT
	IF (@V_MIS_DATE=@V_MON_E)
	BEGIN
		SET @M2_CYC=31
	END
	ELSE
	BEGIN
		SET @M2_CYC=DAY(@D_MIS_DATE)
	END
	BEGIN TRY
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		DECLARE @YYYYMMDD_LAST VARCHAR(8)
		SET @YYYYMMDD_LAST = Convert(varchar,@D_DATE_BEF,112)
		PRINT @YYYYMMDD
		PRINT @YYYYMMDD_LAST
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD_LAST, @OUTPUT2 output
		DECLARE @OUTPUT3 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'AS_CIR',@YYYYMMDD, @OUTPUT3 output
		DECLARE @OUTPUT4 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'AS_CIR',@YYYYMMDD_LAST, @OUTPUT4 output
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S' and left(@OUTPUT3,1) = 'S' and left(@OUTPUT4,1) = 'S')
		BEGIN
			SET @STEP = '4'
			DELETE FROM LOG_AUTOMATION WHERE MIS_DATE=@V_MIS_DATE AND TABLE_NAME='TRANS_REPORT_ASSET_FLOW_sp'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_FLOW_sp','ASSET FLOW資料處理開始','N'
			DELETE FROM RPT_ASSET_FLOW_CYC WHERE MIS_DATE=@V_MIS_DATE
			DELETE FROM RPT_ASSET_FLOW_RATE WHERE MIS_DATE=@V_MIS_DATE
			DECLARE @ALSEOM VARCHAR(50)
			SET @ALSEOM = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @ALSEOM_LAST VARCHAR(50)
			SET @ALSEOM_LAST = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			DECLARE @AS_CIR VARCHAR(50)
			SET @AS_CIR = RIGHT(@OUTPUT3,LEN(@OUTPUT3)-2)
			DECLARE @AS_CIR_LAST VARCHAR(50)
			SET @AS_CIR_LAST = RIGHT(@OUTPUT4,LEN(@OUTPUT4)-2)
			SET @STEP = '5'
			DECLARE @CNT INT
			SELECT @CNT=COUNT(*) FROM HOLIDAY WHERE DATE1=LEFT(@V_MIS_DATE,8)+'01'
			IF (@CNT=1)
			BEGIN
				DECLARE @V_WORKDT VARCHAR(10)=LEFT(@V_MIS_DATE,8)+'01'
				WHILE (@CNT=1)
				BEGIN
					SET @V_WORKDT = CONVERT(VARCHAR,DATEADD(D,1,CAST(@V_WORKDT AS DATE)),23)
					PRINT @V_WORKDT
					SELECT @CNT=COUNT(*) FROM HOLIDAY WHERE DATE1=@V_WORKDT
					PRINT 'CNT=' + CAST(@CNT AS VARCHAR)
				END
				PRINT 'FIRST WORKDT=' + @V_WORKDT
			END
			DECLARE @V_CURR_DATE VARCHAR(10)
			IF (@V_MIS_DATE < @V_WORKDT)
			BEGIN
				SET @V_CURR_DATE = @V_WORKDT
			END
			ELSE
			BEGIN
				SET @V_CURR_DATE = @V_MIS_DATE
			END
			SET @MSG = 'ALSEOM 資料日為 ' + @V_CURR_DATE
			PRINT @MSG
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_FLOW_sp',@MSG,'N'
			if object_id('tempdb..#ALSEOM') is not null drop table #ALSEOM
			Select * Into #ALSEOM From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM SELECT * FROM ' + @ALSEOM + ' WHERE MIS_DATE = ''' + @V_CURR_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#ALSEOM_LAST') is not null drop table #ALSEOM_LAST
			Select * Into #ALSEOM_LAST From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM_LAST SELECT * FROM ' + @ALSEOM_LAST + ' WHERE MIS_DATE = ''' + @V_DATE_BEF + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#AS_CIR') is not null drop table #AS_CIR
			Select * Into #AS_CIR From AS_CIR Where 1> 2
			ALTER TABLE #AS_CIR DROP COLUMN SEQNO
			ALTER TABLE #AS_CIR ADD SEQNO INT
			SET @SQLCmd = N'INSERT INTO #AS_CIR SELECT * FROM ' + @AS_CIR + ' WHERE MIS_DATE BETWEEN ''' + LEFT(@V_MIS_DATE,8) + '01'' AND ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#AS_CIR_LAST') is not null drop table #AS_CIR_LAST
			Select * Into #AS_CIR_LAST From AS_CIR Where 1> 2
			ALTER TABLE #AS_CIR_LAST DROP COLUMN SEQNO
			ALTER TABLE #AS_CIR_LAST ADD SEQNO INT
			SET @SQLCmd = N'INSERT INTO #AS_CIR_LAST SELECT * FROM ' + @AS_CIR_LAST 
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '6'
			if object_id('tempdb..#TEMP_EOM') is not null drop table #TEMP_EOM
			SELECT * INTO #TEMP_EOM FROM #ALSEOM WHERE NBV>0 AND LOANSTAT='AC' AND ISNULL(SEGMENT,'') != ''
			UPDATE #TEMP_EOM SET SEGMENT='PLRW' WHERE SEGMENT LIKE '%PLP3%' AND BUSINESS ='CTL'
			UPDATE #AS_CIR SET SEGMENT='PLRW' WHERE BUSINESS ='CTL' AND SEGMENT LIKE '%PLP3%'
			UPDATE #AS_CIR SET SEGMENT='PL' WHERE BUSINESS ='CTL' AND SEGMENT LIKE '%PLRE%'
			UPDATE #AS_CIR_LAST SET SEGMENT='PLRW' WHERE BUSINESS ='CTL' AND SEGMENT LIKE '%PLP3%'
			SET @STEP = '7'
			/*
			#AS_CIR_LAST => TEMP_AS_PM > PM_ADJ
			#AS_CIR => TEMP_AS_CM > CM_ADJ
			*/
			UPDATE #AS_CIR_LAST SET #AS_CIR_LAST.CDC=a.CDC
			FROM #ALSEOM_LAST a
			INNER JOIN #AS_CIR_LAST b on a.CARDNMBR=b.CARDNMBR and (LEFT(a.CACS_STATE,1)='N' OR LEFT(a.CACS_STATE,1)='Y')
			WHERE (b.SEGMENT like '%PLR4%' OR b.SEGMENT like '%PLR6%') 
			AND (LEFT(b.CACS_STATE,1)='N' OR LEFT(b.CACS_STATE,1)='Y')
			/*b
			SELECT * FROM TEMP_AS_PM INTO DBF PM_ADJ
			SELECT PM_ADJ
			SCAN FOR INLIST(SEGMENT,'PLR4','PLR6') AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
				SCATTER MEMVAR
				SELE ALSEOM_P
					IF SEEK(ALLT(M.CARDNMBR)) AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
						M.CDC=CDC
						SELECT PM_ADJ
						GATHER MEMVAR
					ENDIF
			ENDSCAN
			*/
			SET @STEP = '8'
			UPDATE #AS_CIR SET #AS_CIR.CDC=a.CDC
			FROM #ALSEOM a
			INNER JOIN #AS_CIR b on a.CARDNMBR=b.CARDNMBR and (LEFT(a.CACS_STATE,1)='N' OR LEFT(a.CACS_STATE,1)='Y')
			WHERE (b.SEGMENT like '%PLR4%' OR b.SEGMENT like '%PLR6%') 
			AND (LEFT(b.CACS_STATE,1)='N' OR LEFT(b.CACS_STATE,1)='Y')
			/*
			SELECT * FROM TEMP_AS_CM INTO DBF CM_ADJ
			SELECT CM_ADJ
			SCAN FOR INLIST(SEGMENT,'PLR4','PLR6') AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
				SCATTER MEMVAR
				SELE ALSEOM
					IF SEEK(ALLT(M.CARDNMBR)) AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
						M.CDC=CDC
						SELECT CM_ADJ
						GATHER MEMVAR
					ENDIF
			ENDSCAN
			*/
			SET @STEP = '9'
			DECLARE @intPDCnt INT
			SET @intPDCnt = 1
			WHILE (@intPDCnt <= 10)
			BEGIN
				/*
				PDC[1]		='ML'
				PDC[2]		='MP'
				PDC[3]		='PLBAU'
				PDC[4]		='PLBAURW'
				PDC[5]		='PLBKRW'
				PDC[6]		='PLPCLRW'
				PDC[7]		='PLRCRW'
				PDC[8]		='PLBADRP'
				PDC[9]		='PLIDRP'
				PDC[10]		='PLIDRPPCL'
				P_M[1]		="SEGMENT='ML'"
				P_M[2]		="SEGMENT='MP' AND !INLIST(ACCTTYPE,'020')"
				P_M[3]		="(SEGMENT=='PL' OR SEGMENT='PLRE') AND !INLIST(ACCTTYPE,'353')"
				P_M[4]		="INLIST(ACCTTYPE,'308','340','360','365')"
				P_M[5]		="INLIST(ACCTTYPE,'306','307','336','337','341','342','324','339','363','364')"
				P_M[6]		="INLIST(ACCTTYPE,'371','374')"
				P_M[7]		="INLIST(ACCTTYPE,'305','315','317','338')"
				P_M[8]		="SEGMENT='PLR4' AND !INLIST(ACCTTYPE,'354')"
				P_M[9]		="SEGMENT='PLR6' AND !INLIST(ACCTTYPE,'372','373')"
				P_M[10]	="INLIST(ACCTTYPE,'372','373')"
				*/
				SET @STEP = '10'
				DECLARE @PD VARCHAR(10)
				DECLARE @PD_M VARCHAR(500)
				IF (@intPDCnt = 1)
				BEGIN
					SET @PD='ML'
					SET @PD_M='SEGMENT=''ML'' '
				END
				ELSE IF (@intPDCnt = 2)
				BEGIN
					SET @PD='MP'
					SET @PD_M='SEGMENT=''MP'' AND ACCTTYPE not in (''020'') '
				END
				ELSE IF (@intPDCnt = 3)
				BEGIN
					SET @PD='PLBAU'
					SET @PD_M='(SEGMENT=''PL'' OR SEGMENT=''PLRE'') AND ACCTTYPE not in (''353'') '
				END
				ELSE IF (@intPDCnt = 4)
				BEGIN
					SET @PD='PLBAURW'
					SET @PD_M='ACCTTYPE in (''308'',''340'',''360'',''365'') '
				END
				ELSE IF (@intPDCnt = 5)
				BEGIN
					SET @PD='PLBKRW'
					SET @PD_M='ACCTTYPE in (''306'',''307'',''336'',''337'',''341'',''342'',''324'',''339'',''363'',''364'') '
				END
				ELSE IF (@intPDCnt = 6)
				BEGIN
					SET @PD='PLPCLRW'
					SET @PD_M='ACCTTYPE in (''371'',''374'') '
				END
				ELSE IF (@intPDCnt = 7)
				BEGIN
					SET @PD='PLRCRW'
					SET @PD_M='ACCTTYPE in (''305'',''315'',''317'',''338'') '
				END
				ELSE IF (@intPDCnt = 8)
				BEGIN
					SET @PD='PLBADRP'
					SET @PD_M='SEGMENT=''PLR4'' AND ACCTTYPE not in (''354'') '
				END
				ELSE IF (@intPDCnt = 9)
				BEGIN
					SET @PD='PLIDRP'
					SET @PD_M='SEGMENT=''PLR6'' AND ACCTTYPE not in (''372'',''373'') '
				END
				ELSE IF (@intPDCnt = 10)
				BEGIN
					SET @PD='PLIDRPPCL'
					SET @PD_M='ACCTTYPE in (''372'',''373'') '
				END
				SET @STEP = '11'
				DECLARE @INTCDC INT = 0
				WHILE @INTCDC <= 7
				BEGIN
					SET @STEP = '12-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					if object_id('tempdb..#TEMP_PD') is not null drop table #TEMP_PD
					CREATE TABLE dbo.#TEMP_PD
					(
						PD			VARCHAR(10),
						CDC			NUMERIC(1),
						CYCLE		NUMERIC(2),
						AC_BOP		NUMERIC(16),
						AC_EOP		NUMERIC(10),
						NBV_BOP		NUMERIC(18,4),
						NBV_EOP		NUMERIC(18,4),
					)
					if object_id('tempdb..#BP1') is not null drop table #BP1
					CREATE TABLE #BP1
					(
						BILLDAY tinyint
					  , AC_BOP NUMERIC(10)
					  , NBV_BOP NUMERIC(18,4)
					)
					SET @SQLCmd = N'INSERT INTO #BP1 SELECT CAST(BILLDAY AS VARCHAR),COUNT(*) AS AC_BOP,SUM(NBV)/1000 AS NBV_BOP'					
					SET @SQLCmd = @SQLCmd + N' FROM #AS_CIR_LAST'
					SET @SQLCmd = @SQLCmd + N' WHERE ' + @PD_M + ' AND CDC=' + CAST(@INTCDC AS VARCHAR) + ' AND LOANSTAT!=''CL'''
					SET @SQLCmd = @SQLCmd + N' GROUP BY BILLDAY '
					PRINT @SQLCmd
					EXECUTE sp_executesql @SQLCmd
					if object_id('tempdb..#EP1') is not null drop table #EP1
					CREATE TABLE #EP1
					(
						BILLDAY tinyint
					  , AC_EOP NUMERIC(10)
					  , NBV_EOP NUMERIC(18,4)
					)
					SET @STEP = '13-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					IF (@INTCDC=0)
					BEGIN
						IF (DAY(@D_MIS_DATE)<=@iCNYGraceDt)
						BEGIN
							SET @STEP = '14-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
							SET @SQLCmd = N'INSERT INTO #EP1 SELECT CAST(BILLDAY AS VARCHAR),COUNT(*) AS AC_EOP,SUM(NBV)/1000 AS NBV_EOP'					
							SET @SQLCmd = @SQLCmd + N' FROM #TEMP_EOM '
							SET @SQLCmd = @SQLCmd + N' WHERE ' + @PD_M + ' AND CDC=' + CAST(@INTCDC AS VARCHAR)
							SET @SQLCmd = @SQLCmd + N' GROUP BY BILLDAY '
							PRINT @SQLCmd
							EXECUTE sp_executesql @SQLCmd			
						END
						ELSE
						BEGIN
							SET @STEP = '15-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
							SET @SQLCmd = N'INSERT INTO #EP1 SELECT CAST(BILLDAY AS VARCHAR),COUNT(*) AS AC_EOP,SUM(NBV)/1000 AS NBV_EOP'
							SET @SQLCmd = @SQLCmd + N' FROM #AS_CIR WHERE ' + @PD_M + ' AND CDC=' + CAST(@INTCDC AS VARCHAR) + '+1 AND CAST(BILLDAY AS INT)<=' + CAST(@M1_CYC AS VARCHAR) + ' AND LOANSTAT!=''CL'' '
							SET @SQLCmd = @SQLCmd + N' GROUP BY BILLDAY'
							SET @SQLCmd = @SQLCmd + N' UNION'
							SET @SQLCmd = @SQLCmd + N' SELECT CAST(BILLDAY AS VARCHAR),COUNT(*) AS AC_EOP,SUM(NBV)/1000 AS NBV_EOP'
							SET @SQLCmd = @SQLCmd + N' FROM #TEMP_EOM'
							SET @SQLCmd = @SQLCmd + N' WHERE ' + @PD_M + ' AND CDC=' + CAST(@INTCDC AS VARCHAR) + ' AND CAST(BILLDAY AS INT)>' + CAST(@M1_CYC AS VARCHAR)
							SET @SQLCmd = @SQLCmd + N' GROUP BY BILLDAY'
							PRINT @SQLCmd
							EXECUTE sp_executesql @SQLCmd			
						END
					END
					ELSE
					BEGIN
						SET @STEP = '16-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
						SET @SQLCmd = N'INSERT INTO #EP1 SELECT CAST(BILLDAY AS VARCHAR),COUNT(*) AS AC_EOP,SUM(NBV)/1000 AS NBV_EOP'
						SET @SQLCmd = @SQLCmd + N' FROM #AS_CIR'
						SET @SQLCmd = @SQLCmd + N' WHERE ' + @PD_M + ' AND CDC=' + CAST(@INTCDC AS VARCHAR) + '+1 AND CAST(BILLDAY AS INT)<=' + CAST(@M2_CYC AS VARCHAR) + ' AND LOANSTAT!=''CL'' '
						SET @SQLCmd = @SQLCmd + N' GROUP BY BILLDAY'
						SET @SQLCmd = @SQLCmd + N' UNION'
						SET @SQLCmd = @SQLCmd + N' SELECT CAST(BILLDAY AS VARCHAR),COUNT(*) AS AC_EOP,SUM(NBV)/1000 AS NBV_EOP'
						SET @SQLCmd = @SQLCmd + N' FROM #TEMP_EOM'
						SET @SQLCmd = @SQLCmd + N' WHERE ' + @PD_M + ' AND CDC=' + CAST(@INTCDC AS VARCHAR) + ' AND CAST(BILLDAY AS INT)>' + CAST(@M2_CYC AS VARCHAR) + ''
						SET @SQLCmd = @SQLCmd + N' GROUP BY BILLDAY'
						PRINT @SQLCmd
						EXECUTE sp_executesql @SQLCmd
					END
					SET @STEP = '17-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					if object_id('tempdb..#DATA_CYC') is not null drop table #DATA_CYC
					CREATE TABLE #DATA_CYC
					(
						BILLDAY tinyint
					  , AC_BOP NUMERIC(10)
					  , NBV_BOP NUMERIC(18,4)
					  , AC_EOP NUMERIC(10)
					  , NBV_EOP NUMERIC(18, 4)
					)
					if object_id('tempdb..#DATA_RATE') is not null drop table #DATA_RATE
					CREATE TABLE #DATA_RATE
					(
						DAY1 tinyint
					  ,	FLOW NUMERIC(7,4)
					)
					DECLARE @CX INT = 1
					WHILE @CX <= 31
					BEGIN
						INSERT INTO #DATA_CYC(BILLDAY, AC_BOP, NBV_BOP, AC_EOP, NBV_EOP) VALUES(@CX,0,0,0,0)
						INSERT INTO #DATA_RATE(DAY1) VALUES(@CX)
						SET @CX = @CX + 1
					END
					SET @STEP = '18-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					UPDATE #DATA_RATE SET #DATA_RATE.FLOW=A.FLOW
					FROM RPT_ASSET_FLOW_RATE A
					WHERE A.BUCKET=@INTCDC AND A.DAY=#DATA_RATE.DAY1
					AND A.MIS_DATE = (SELECT MAX(MIS_DATE) FROM RPT_ASSET_FLOW_RATE WHERE MIS_DATE LIKE SUBSTRING(@V_MIS_DATE,1,8)+'%' AND BUCKET=@INTCDC AND PD=@PD AND PD_RESULT='CURR' AND MIS_DATE < @V_MIS_DATE) 
					AND A.PD=@PD AND A.PD_RESULT='CURR' AND A.BUCKET=@INTCDC
					SET @STEP = '19-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					UPDATE #DATA_CYC
						SET #DATA_CYC.AC_BOP = #BP1.AC_BOP
						  , #DATA_CYC.NBV_BOP = #BP1.NBV_BOP
					FROM #BP1
					WHERE #DATA_CYC.BILLDAY = #BP1.BILLDAY
					SET @STEP = '20-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					UPDATE #DATA_CYC
						SET #DATA_CYC.AC_EOP = #EP1.AC_EOP
						  , #DATA_CYC.NBV_EOP = #EP1.NBV_EOP
					FROM #EP1
					WHERE #DATA_CYC.BILLDAY = #EP1.BILLDAY
					SET @STEP = '21-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					INSERT INTO RPT_ASSET_FLOW_CYC
					select @D_MIS_DATE, @PD, 'CURR', @INTCDC, BILLDAY, isnull(AC_BOP,0), isnull(AC_EOP,0), isnull(NBV_BOP,0), isnull(NBV_EOP,0), 0, 0, 0, 0
					From #DATA_CYC
					SET @STEP = '22-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					IF (@INTCDC>0)
					BEGIN
						if object_id('tempdb..#_FR1') is not null drop table #_FR1
						SELECT DAY(@D_MIS_DATE) AS DAY1, @PD AS PD, @INTCDC AS CDC ,SUM(NBV_EOP) AS SUM_EOP, SUM(NBV_BOP) AS SUM_BOP
						INTO #_FR1
						FROM #DATA_CYC 
						SET @STEP = '23-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
						if object_id('tempdb..#FR1') is not null drop table #FR1
						SELECT DAY1, PD, CDC, IIF(SUM_BOP=0, 0, ROUND(SUM_EOP/SUM_BOP,4)) AS FRATE 
						INTO #FR1
						FROM #_FR1
						SET @STEP = '24-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
						UPDATE #DATA_RATE
							SET #DATA_RATE.FLOW = #FR1.FRATE
						FROM #FR1
						WHERE #DATA_RATE.DAY1 = #FR1.DAY1
						SET @STEP = '25-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
						INSERT INTO RPT_ASSET_FLOW_RATE
						Select @D_MIS_DATE, @PD, 'CURR', DAY1, @INTCDC, IIF(FLOW>1,1.0000,FLOW)
						From #DATA_RATE
						ORDER BY DAY1
					END
					SET @STEP = '26-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					TRUNCATE TABLE #BP1
					TRUNCATE TABLE #EP1
					TRUNCATE TABLE #DATA_CYC
					TRUNCATE TABLE #DATA_RATE
					SET @CX = 1
					WHILE @CX <= 31
					BEGIN
						INSERT INTO #DATA_CYC(BILLDAY, AC_BOP, NBV_BOP, AC_EOP, NBV_EOP) VALUES(@CX,0,0,0,0)
						INSERT INTO #DATA_RATE(DAY1) VALUES(@CX)
						SET @CX = @CX + 1
					END
					SET @STEP = '27-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					UPDATE #DATA_RATE SET #DATA_RATE.FLOW=A.FLOW
					FROM RPT_ASSET_FLOW_RATE A
					WHERE A.BUCKET=@INTCDC AND A.DAY=#DATA_RATE.DAY1
					AND A.MIS_DATE = (SELECT MAX(MIS_DATE) FROM RPT_ASSET_FLOW_RATE WHERE MIS_DATE LIKE SUBSTRING(@V_MIS_DATE,1,8)+'%' AND BUCKET=@INTCDC AND PD=@PD AND PD_RESULT='NEXT') 
					AND A.PD=@PD AND A.PD_RESULT='NEXT' AND A.BUCKET=@INTCDC
					SET @STEP = '28-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					SET @SQLCmd = N'INSERT INTO #BP1 SELECT CAST(BILLDAY AS VARCHAR),COUNT(*) AS AC_BOP,SUM(NBV)/1000 AS NBV_BOP'					
					SET @SQLCmd = @SQLCmd + N' FROM #AS_CIR'
					SET @SQLCmd = @SQLCmd + N' WHERE ' + @PD_M + ' AND CDC=' + CAST(@INTCDC AS VARCHAR) + ' AND CAST(BILLDAY AS VARCHAR)<=' + CAST(@M2_CYC AS VARCHAR)
					SET @SQLCmd = @SQLCmd + N' GROUP BY BILLDAY '
					PRINT @SQLCmd
					EXECUTE sp_executesql @SQLCmd
					SET @STEP = '29-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					SET @SQLCmd = N'INSERT INTO #EP1 SELECT CAST(BILLDAY AS VARCHAR),COUNT(*) AS AC_EOP,SUM(NBV)/1000 AS NBV_EOP'					
					SET @SQLCmd = @SQLCmd + N' FROM #TEMP_EOM '
					SET @SQLCmd = @SQLCmd + N' WHERE ' + @PD_M + ' AND CDC=' + CAST(@INTCDC AS VARCHAR) + ' AND CAST(BILLDAY AS VARCHAR)<=' + CAST(@M2_CYC AS VARCHAR)
					SET @SQLCmd = @SQLCmd + N' GROUP BY BILLDAY '
					PRINT @SQLCmd
					EXECUTE sp_executesql @SQLCmd
					SET @STEP = '30-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					UPDATE #DATA_CYC
						SET #DATA_CYC.AC_BOP = #BP1.AC_BOP
						  , #DATA_CYC.NBV_BOP = #BP1.NBV_BOP
					FROM #BP1
					WHERE #DATA_CYC.BILLDAY = #BP1.BILLDAY
					SET @STEP = '31-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					UPDATE #DATA_CYC
						SET #DATA_CYC.AC_EOP = #EP1.AC_EOP
						  , #DATA_CYC.NBV_EOP = #EP1.NBV_EOP
					FROM #EP1
					WHERE #DATA_CYC.BILLDAY = #EP1.BILLDAY
					SET @STEP = '32-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					INSERT INTO RPT_ASSET_FLOW_CYC
					select @D_MIS_DATE, @PD, 'NEXT', @INTCDC, BILLDAY, isnull(AC_BOP,0), isnull(AC_EOP,0), isnull(NBV_BOP,0), isnull(NBV_EOP,0), 0, 0, 0, 0
					From #DATA_CYC
					SET @STEP = '33-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					IF (@INTCDC>0)
					BEGIN
						if object_id('tempdb..#_FR2') is not null drop table #_FR2
						SELECT DAY(@D_MIS_DATE) AS DAY1, @PD AS PD, @INTCDC AS CDC ,SUM(NBV_EOP) AS SUM_EOP, SUM(NBV_BOP) AS SUM_BOP
						INTO #_FR2
						FROM #DATA_CYC
						SET @STEP = '34-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
						if object_id('tempdb..#FR2') is not null drop table #FR2
						SELECT DAY1, PD, CDC, IIF(SUM_BOP=0, 0, ROUND(SUM_EOP/SUM_BOP,4)) AS FRATE 
						INTO #FR2
						FROM #_FR2
						SET @STEP = '35-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
						UPDATE #DATA_RATE
							SET #DATA_RATE.FLOW = #FR2.FRATE
						FROM #FR2
						WHERE #DATA_RATE.DAY1 = #FR2.DAY1
						SET @STEP = '36-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
						INSERT INTO RPT_ASSET_FLOW_RATE
						Select @D_MIS_DATE, @PD, 'NEXT', DAY1, @INTCDC, IIF(FLOW>1,1.0000,FLOW)
						From #DATA_RATE
						ORDER BY DAY1
					END
					SET @STEP = '37-' + CAST(@intPDCnt AS VARCHAR) + '-' + CAST(@INTCDC AS VARCHAR)
					SET @INTCDC = @INTCDC + 1
				END
				SET @STEP = '38' + CAST(@intPDCnt AS VARCHAR)
				SET @intPDCnt = @intPDCnt + 1
			END
			/*
			計算 RPT_ASSET_FLOW_CYC - FLOW_R_AC_CUM 及 FLOW_R_AMT_CUM 累計值
			*/
			SET @STEP = '50'
			DECLARE @PD_RESULT		VARCHAR(4)
			DECLARE @CDC			NUMERIC(1)
			DECLARE FLOW_ITEM CURSOR FAST_FORWARD FOR
			SELECT DISTINCT PD, PD_RESULT, CDC
				FROM RPT_ASSET_FLOW_CYC 
				WHERE MIS_DATE = @V_MIS_DATE 
			OPEN FLOW_ITEM
			FETCH NEXT FROM FLOW_ITEM INTO @PD, @PD_RESULT, @CDC
			WHILE @@FETCH_STATUS = 0
			BEGIN
					DECLARE @MIS_DATE		DATE
					DECLARE @CYCLE			NUMERIC(2)
					DECLARE @AC_BOP			NUMERIC(16)
					DECLARE @AC_EOP			NUMERIC(10)
					DECLARE @NBV_BOP		NUMERIC(18,4)
					DECLARE @NBV_EOP		NUMERIC(18,4)
					DECLARE @AC_BOP_SUM		NUMERIC(16) = 0
					DECLARE @AC_EOP_SUM		NUMERIC(16) = 0
					DECLARE @NBV_BOP_SUM	NUMERIC(18,4) = 0
					DECLARE @NBV_EOP_SUM	NUMERIC(18,4) = 0
					DECLARE @FLOW_R_AC		NUMERIC(10,4)
					DECLARE @FLOW_R_AC_CUM	NUMERIC(10,4)
					DECLARE @FLOW_R_AMT		NUMERIC(10,4)
					DECLARE @FLOW_R_AMT_CUM	NUMERIC(10,4)
					DECLARE FLOW_CYC CURSOR FAST_FORWARD FOR
						SELECT MIS_DATE, PD, PD_RESULT, CDC, CYCLE, AC_BOP, AC_EOP, NBV_BOP, NBV_EOP 
						FROM RPT_ASSET_FLOW_CYC 
						WHERE MIS_DATE=@V_MIS_DATE 
						AND PD = @PD 
						AND PD_RESULT = @PD_RESULT 
						AND CDC = @CDC
						ORDER BY CYCLE
					OPEN FLOW_CYC
					FETCH NEXT FROM FLOW_CYC INTO @MIS_DATE, @PD, @PD_RESULT, @CDC, @CYCLE, @AC_BOP, @AC_EOP, @NBV_BOP, @NBV_EOP
					WHILE @@FETCH_STATUS = 0
					BEGIN
						SET @STEP = '51'
						SET @FLOW_R_AC = IIF(@AC_BOP = 0, 0, @AC_EOP / @AC_BOP)
						SET @FLOW_R_AMT = IIF(@NBV_BOP = 0, 0, @NBV_EOP / @NBV_BOP)
						SET @AC_BOP_SUM = @AC_BOP_SUM + @AC_BOP
						SET @AC_EOP_SUM = @AC_EOP_SUM + @AC_EOP
						SET @NBV_BOP_SUM = @NBV_BOP_SUM + @NBV_BOP
						SET @NBV_EOP_SUM = @NBV_EOP_SUM + @NBV_EOP
						SET @FLOW_R_AC_CUM = IIF(@AC_BOP_SUM = 0, 0, IIF((@AC_EOP_SUM / @AC_BOP_SUM) > 1,1,(@AC_EOP_SUM / @AC_BOP_SUM)))
						SET @FLOW_R_AMT_CUM = IIF(@NBV_BOP_SUM = 0, 0, IIF((@NBV_EOP_SUM / @NBV_BOP_SUM) > 1,1,(@NBV_EOP_SUM / @NBV_BOP_SUM)))
						SET @STEP = '52'
						Update RPT_ASSET_FLOW_CYC 
						Set FLOW_R_AC=@FLOW_R_AC,
							FLOW_R_AMT=@FLOW_R_AMT,
							FLOW_R_AC_CUM=@FLOW_R_AC_CUM,
							FLOW_R_AMT_CUM=@FLOW_R_AMT_CUM
						Where MIS_DATE=@MIS_DATE and PD=@PD and PD_RESULT=@PD_RESULT and CDC=@CDC and CYCLE=@CYCLE
						FETCH NEXT FROM FLOW_CYC INTO @MIS_DATE, @PD, @PD_RESULT, @CDC, @CYCLE, @AC_BOP, @AC_EOP, @NBV_BOP, @NBV_EOP
					END
					CLOSE FLOW_CYC
					DEALLOCATE FLOW_CYC
					FETCH NEXT FROM FLOW_ITEM INTO @PD, @PD_RESULT, @CDC
			END
			CLOSE FLOW_ITEM
			DEALLOCATE FLOW_ITEM
			SET @STEP = '70'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_FLOW_sp','ASSET FLOW資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', ALSEOM或AS_CIR尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_FLOW_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

