ALTER PROCEDURE [dbo].[TRANS_REPORT_ASSET_LEADING_sp]
	@V_MIS_DATE VARCHAR(10)
  , @iCNYGraceDt INT
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(10)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	DECLARE @V_DATE_BEF VARCHAR(10)
	SET @V_DATE_BEF = CONVERT(VARCHAR,DATEADD(MS,-3,DATEADD(MM, DATEDIFF(M,0,@D_MIS_DATE), 0)) ,23)
	DECLARE @D_DATE_BEF DATE
	SET @D_DATE_BEF = CAST(@V_DATE_BEF AS DATE)
	DECLARE @V_MON_E VARCHAR(10)
	SET @V_MON_E = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE)+1, 0)), 23)
	DECLARE @D_MON_E DATE
	SET @D_MON_E = CAST(@V_MON_E AS DATE)
	DECLARE @M1_CYC int
	SET @M1_CYC=DAY(@D_MIS_DATE)-@iCNYGraceDt
	DECLARE @M2_CYC int
	IF (@V_MIS_DATE=@V_MON_E)
	BEGIN
		SET @M2_CYC=31
	END
	ELSE
	BEGIN
		SET @M2_CYC=DAY(@D_MIS_DATE)
	END
	PRINT @M1_CYC
	PRINT @M2_CYC
	BEGIN TRY
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		DECLARE @YYYYMMDD_LAST VARCHAR(8)
		SET @YYYYMMDD_LAST = Convert(varchar,@D_DATE_BEF,112)
		PRINT @YYYYMMDD
		PRINT @YYYYMMDD_LAST
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'ALSEOM',@YYYYMMDD_LAST, @OUTPUT2 output
		DECLARE @OUTPUT3 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'AS_CIR',@YYYYMMDD, @OUTPUT3 output
		DECLARE @OUTPUT4 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'AS_CIR',@YYYYMMDD_LAST, @OUTPUT4 output
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S' and left(@OUTPUT3,1) = 'S' and left(@OUTPUT4,1) = 'S')
		BEGIN
			SET @STEP = '4'
			DELETE FROM LOG_AUTOMATION WHERE MIS_DATE=@V_MIS_DATE AND TABLE_NAME='TRANS_REPORT_ASSET_LEADING_sp'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_LEADING_sp','ASSET LEADING資料處理開始','N'
			DELETE FROM RPT_ASSET_LEADING_CYC WHERE MIS_DATE=@V_MIS_DATE
			DECLARE @ALSEOM VARCHAR(50)
			SET @ALSEOM = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @ALSEOM_LAST VARCHAR(50)
			SET @ALSEOM_LAST = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			DECLARE @AS_CIR VARCHAR(50)
			SET @AS_CIR = RIGHT(@OUTPUT3,LEN(@OUTPUT3)-2)
			DECLARE @AS_CIR_LAST VARCHAR(50)
			SET @AS_CIR_LAST = RIGHT(@OUTPUT4,LEN(@OUTPUT4)-2)
			SET @STEP = '5'
			if object_id('tempdb..#ALSEOM') is not null drop table #ALSEOM
			Select * Into #ALSEOM From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM SELECT * FROM ' + @ALSEOM + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#ALSEOM_LAST') is not null drop table #ALSEOM_LAST
			Select * Into #ALSEOM_LAST From ALSEOM Where 1> 2
			SET @SQLCmd = N'INSERT INTO #ALSEOM_LAST SELECT * FROM ' + @ALSEOM_LAST + ' WHERE MIS_DATE = ''' + @V_DATE_BEF + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#AS_CIR') is not null drop table #AS_CIR
			Select * Into #AS_CIR From AS_CIR Where 1> 2
			ALTER TABLE #AS_CIR DROP COLUMN SEQNO
			ALTER TABLE #AS_CIR ADD SEQNO INT
			SET @SQLCmd = N'INSERT INTO #AS_CIR SELECT * FROM ' + @AS_CIR + ' WHERE MIS_DATE BETWEEN ''' + LEFT(@V_MIS_DATE,8) + '01'' AND ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#AS_CIR_LAST') is not null drop table #AS_CIR_LAST
			Select * Into #AS_CIR_LAST From AS_CIR Where 1> 2
			ALTER TABLE #AS_CIR_LAST DROP COLUMN SEQNO
			ALTER TABLE #AS_CIR_LAST ADD SEQNO INT
			SET @SQLCmd = N'INSERT INTO #AS_CIR_LAST SELECT * FROM ' + @AS_CIR_LAST 
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '6'
			if object_id('tempdb..#TEMP') is not null drop table #TEMP
			CREATE TABLE #TEMP
			(
				SEGMENT VARCHAR(10),
				SEGMENT_O VARCHAR(10),
				CARDNMBR NUMERIC(16),
				LOANSTAT VARCHAR(2),
				ACCTTYPE VARCHAR(3),
				CDC int,
				BILLDAY int,
				NBV NUMERIC(12,2)
			)
			INSERT INTO #TEMP SELECT '',SEGMENT, CARDNMBR, LOANSTAT, ACCTTYPE, CDC, BILLDAY, NBV FROM #ALSEOM
			SET @STEP = '7'
			UPDATE #AS_CIR_LAST SET #AS_CIR_LAST.CDC=a.CDC
			FROM #ALSEOM_LAST a
			INNER JOIN #AS_CIR_LAST b on a.ACCTNMBR=b.CARDNMBR and (LEFT(a.CACS_STATE,1)='N' OR LEFT(a.CACS_STATE,1)='Y')
			WHERE (b.SEGMENT like '%PLR4%' OR b.SEGMENT like '%PLR6%') 
			AND (LEFT(b.CACS_STATE,1)='N' OR LEFT(b.CACS_STATE,1)='Y')
			SET @STEP = '8'
			if object_id('tempdb..#TEMPBOP') is not null drop table #TEMPBOP
			CREATE TABLE #TEMPBOP
			(
				SEGMENT VARCHAR(10),
				SEGMENT_O VARCHAR(10),
				CARDNMBR NUMERIC(16),
				ACCTTYPE VARCHAR(3),
				CDC int,
				BILLDAY int,
				NBV NUMERIC(12,2)
			)
			INSERT INTO #TEMPBOP SELECT '',SEGMENT,CARDNMBR,ACCTTYPE, CDC, BILLDAY, NBV FROM #AS_CIR_LAST
			/*
			SELECT * FROM PM_AS INTO DBF PM_ADJ
			USE 'M:\DATASET\ALSEOM\ALSEOM'+SUBSTR(DTOS(PM_END),3,6)+'.DBF' IN 0 SHARED ORDER TAG ACCTNMBR ALIAS ALS_END
			SELECT PM_ADJ
			SCAN FOR INLIST(SEGMENT,'PLR4','PLR6') AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
				SCATTER MEMVAR
				SELE ALS_END
					IF SEEK(ALLT(M.CARDNMBR)) AND INLIST(SUBS(CACS_STATE,1,1),'N','Y')
						M.CDC=CDC
						SELECT PM_ADJ
						GATHER MEMVAR
					ENDIF
			ENDSCAN
			*/
			SET @STEP = '9'
			DECLARE @intPDCnt INT
			SET @intPDCnt = 1
			WHILE (@intPDCnt <= 10)
			BEGIN
				SET @STEP = '10'
				DECLARE @PD VARCHAR(10)
				DECLARE @PD_M VARCHAR(500)
				IF (@intPDCnt = 1)
				BEGIN
					SET @PD='ML'
					SET @PD_M='SEGMENT_O=''ML'' '
				END
				ELSE IF (@intPDCnt = 2)
				BEGIN
					SET @PD='MP'
					SET @PD_M='SEGMENT_O=''MP'' AND ACCTTYPE not in (''020'') '
				END
				ELSE IF (@intPDCnt = 3)
				BEGIN
					SET @PD='PLBAU'
					SET @PD_M='(SEGMENT_O=''PL'' OR SEGMENT_O=''PLRE'') AND ACCTTYPE not in (''353'') '
				END
				ELSE IF (@intPDCnt = 4)
				BEGIN
					SET @PD='PLBAURW'
					SET @PD_M='ACCTTYPE in (''308'',''340'',''360'',''365'') '
				END
				ELSE IF (@intPDCnt = 5)
				BEGIN
					SET @PD='PLBKRW'
					SET @PD_M='ACCTTYPE in (''306'',''307'',''336'',''337'',''341'',''342'',''324'',''339'',''363'',''364'') '
				END
				ELSE IF (@intPDCnt = 6)
				BEGIN
					SET @PD='PLPCLRW'
					SET @PD_M='ACCTTYPE in (''371'',''374'') '
				END
				ELSE IF (@intPDCnt = 7)
				BEGIN
					SET @PD='PLRCRW'
					SET @PD_M='ACCTTYPE in (''305'',''315'',''317'',''338'') '
				END
				ELSE IF (@intPDCnt = 8)
				BEGIN
					SET @PD='PLBADRP'
					SET @PD_M='SEGMENT_O=''PLR4'' AND ACCTTYPE not in (''354'') '
				END
				ELSE IF (@intPDCnt = 9)
				BEGIN
					SET @PD='PLIDRP'
					SET @PD_M='SEGMENT_O=''PLR6'' AND ACCTTYPE not in (''372'',''373'') '
				END
				ELSE IF (@intPDCnt = 10)
				BEGIN
					SET @PD='PLIDRPPCL'
					SET @PD_M='ACCTTYPE in (''372'',''373'') '
				END
				SET @STEP = '11'
				SET @SQLCmd = N'UPDATE #TEMP SET SEGMENT=''' + @PD + ''' WHERE ' + @PD_M
				EXECUTE sp_executesql @SQLCmd
				SET @STEP = '12'
				SET @SQLCmd = N'UPDATE #TEMPBOP SET SEGMENT=''' + @PD + ''' WHERE ' + @PD_M
				EXECUTE sp_executesql @SQLCmd
				SET @STEP = '13'
				DECLARE @intCDC INT, @intCDC_E INT
				SET @intCDC = 0
				SET @intCDC_E = IIF(@PD in ('ML','MP'),6,4)
				WHILE (@intCDC <= @intCDC_E)
				BEGIN
					DECLARE @intCYCLE INT = 1
					WHILE (@intCYCLE <= 31)
					BEGIN
						SET @STEP = '14'
						INSERT INTO RPT_ASSET_LEADING_CYC VALUES(@V_MIS_DATE,@PD,@intCDC,@intCYCLE,0,0,0,0,0,0,0,0)
						SET @intCYCLE = @intCYCLE + 1
					END
					SET @intCDC = @intCDC + 1
				END
				SET @intPDCnt = @intPDCnt + 1
			END
			SET @STEP = '15'
			if object_id('tempdb..#BOP') is not null drop table #BOP
			CREATE TABLE #BOP
			(
				CARDNMBR NUMERIC(16),
				SEGMENT VARCHAR(10),
				TAG VARCHAR(2),
				ACCTTYPE VARCHAR(3),
				BILLDAY int,
				BOP_CDC int,
				NBV NUMERIC(12,2)
			)
			INSERT INTO #BOP
			SELECT CARDNMBR, SEGMENT, SPACE(2) AS TAG, ACCTTYPE, BILLDAY, CDC AS BOP_CDC,NBV 
			FROM #TEMPBOP 
			UNION
			SELECT CARDNMBR, SEGMENT, SPACE(2) AS TAG, ACCTTYPE, BILLDAY, CDC AS BOP_CDC,NBV 
			FROM #TEMP WHERE CARDNMBR IN (SELECT CARDNMBR FROM #AS_CIR_LAST) AND CARDNMBR NOT IN (SELECT CARDNMBR FROM #TEMPBOP) 
			SET @STEP = '16'
			if object_id('tempdb..#BOP_EOP') is not null drop table #BOP_EOP
			CREATE TABLE #BOP_EOP
			(
				CARDNMBR NUMERIC(16),
				SEGMENT VARCHAR(10),
				TAG VARCHAR(2),
				ACCTTYPE VARCHAR(3),
				BILLDAY int,
				BOP_CDC int,
				NBV NUMERIC(12,2),
				EOP_CDC int
			)
			INSERT INTO #BOP_EOP
			SELECT A.*, B.CDC AS EOP_CDC
			FROM #BOP A 
			LEFT JOIN #AS_CIR B ON A.CARDNMBR=B.CARDNMBR
			SET @STEP = '17'
			UPDATE #BOP_EOP SET #BOP_EOP.EOP_CDC=#TEMP.CDC
			FROM #TEMP
			WHERE #TEMP.CARDNMBR=#BOP_EOP.CARDNMBR
			AND #BOP_EOP.EOP_CDC is null
			SET @STEP = '18'
			UPDATE #BOP_EOP SET EOP_CDC = 5 WHERE dbo.ALLTRIM(SEGMENT) not in ('ML','MP') AND EOP_CDC>= 5 
			UPDATE #BOP_EOP SET EOP_CDC = 8 WHERE dbo.ALLTRIM(SEGMENT) in ('ML','MP') AND EOP_CDC>= 8 
			UPDATE #BOP_EOP SET BOP_CDC = 5 WHERE dbo.ALLTRIM(SEGMENT) not in ('ML','MP') AND BOP_CDC>= 5 
			UPDATE #BOP_EOP SET BOP_CDC = 8 WHERE dbo.ALLTRIM(SEGMENT) in ('ML','MP') AND BOP_CDC>= 8 
			UPDATE #BOP_EOP SET TAG='FW' WHERE BOP_CDC=0 AND EOP_CDC=0 AND BILLDAY>@M1_CYC AND isnull(TAG,'')=''
			UPDATE #BOP_EOP SET TAG='FW' WHERE EOP_CDC>BOP_CDC AND isnull(TAG,'')=''
			UPDATE #BOP_EOP SET TAG='ST' WHERE  EOP_CDC=BOP_CDC AND isnull(TAG,'')=''
			UPDATE #BOP_EOP SET TAG='BW' WHERE  EOP_CDC<BOP_CDC AND isnull(TAG,'')=''
			SET @STEP = '19'
			if object_id('tempdb..#LEADDB') is not null drop table #LEADDB
			CREATE TABLE #LEADDB
			(
				PD			VARCHAR(10),
				CDC			NUMERIC(1),
				CYCLE		NUMERIC(2),
				BOP_ACCT	NUMERIC(10),
				BOP_AMT		NUMERIC(18,4),
				ACCT_FWD	NUMERIC(10),
				AMT_FWD		NUMERIC(18,4),
				ACCT_STY	NUMERIC(10),
				AMT_STY		NUMERIC(18,4),
				ACCT_BWD	NUMERIC(10),
				AMT_BWD		NUMERIC(18,4)
			)
			INSERT INTO #LEADDB
			SELECT SEGMENT,BOP_CDC, BILLDAY, COUNT(*) AS ACCT,SUM(NBV)/1000 AS AMNT, SUM(IIF(TAG='FW',1,0)) AS ACCT_FWD,SUM(IIF(TAG='FW',NBV,0))/1000 AS AMNT_FWD, SUM(IIF(TAG='ST',1,0)) AS ACCT_STY,SUM(IIF(TAG='ST',NBV,0))/1000 AS AMNT_STY, SUM(IIF(TAG='BW',1,0)) AS ACCT_BWD,SUM(IIF(TAG='BW',NBV,0))/1000 AS AMNT_BWD
			FROM #BOP_EOP 
			GROUP BY SEGMENT, BOP_CDC, BILLDAY 
			SET @STEP = '20'
			UPDATE RPT_ASSET_LEADING_CYC SET RPT_ASSET_LEADING_CYC.BOP_ACCT=#LEADDB.BOP_ACCT
										   , RPT_ASSET_LEADING_CYC.BOP_AMT=#LEADDB.BOP_AMT
										   , RPT_ASSET_LEADING_CYC.ACCT_FWD=#LEADDB.ACCT_FWD
										   , RPT_ASSET_LEADING_CYC.AMT_FWD=#LEADDB.AMT_FWD
										   , RPT_ASSET_LEADING_CYC.ACCT_STY=#LEADDB.ACCT_STY
										   , RPT_ASSET_LEADING_CYC.AMT_STY=#LEADDB.AMT_STY
										   , RPT_ASSET_LEADING_CYC.ACCT_BWD=#LEADDB.ACCT_BWD
										   , RPT_ASSET_LEADING_CYC.AMT_BWD=#LEADDB.AMT_BWD
			FROM #LEADDB
			WHERE #LEADDB.PD=RPT_ASSET_LEADING_CYC.PD
			AND #LEADDB.CDC=RPT_ASSET_LEADING_CYC.CDC
			AND #LEADDB.CYCLE=RPT_ASSET_LEADING_CYC.CYCLE
			AND RPT_ASSET_LEADING_CYC.MIS_DATE=@V_MIS_DATE
			SET @STEP = '30'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_LEADING_sp','ASSET LEADING資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', ALSEOM或AS_CIR尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_ASSET_LEADING_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

