ALTER PROCEDURE [dbo].[TRANS_REPORT_CARDS_DELQ_sp]
	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	DECLARE @V_MISDATE_FIRSTDAY VARCHAR(10)
	SET @V_MISDATE_FIRSTDAY = LEFT(@V_MIS_DATE,8) + '01'
	BEGIN TRY
		/*DECLARE @STEP VARCHAR(5)
		DECLARE @V_MIS_DATE VARCHAR(10)
		SET @V_MIS_DATE = '2018-09-13'
		DECLARE @D_MIS_DATE DATE
		SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
		*/
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'GEN_WO',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'CP030D',@YYYYMMDD, @OUTPUT2 output
		PRINT @OUTPUT1
		PRINT @OUTPUT2
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S')
		BEGIN
			SET @STEP = '4'
			DELETE FROM LOG_AUTOMATION WHERE MIS_DATE=@V_MIS_DATE AND TABLE_NAME='TRANS_REPORT_CARDS_LEADING_sp'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_DELQ_sp','CARDS DELQ資料處理開始','N'
			DELETE FROM RPT_CARDS_DELQ_CYC WHERE MIS_DATE=@V_MIS_DATE
			DECLARE @GEN_WO VARCHAR(50)
			SET @GEN_WO = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @CP030D VARCHAR(50)
			SET @CP030D = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			PRINT @GEN_WO
			PRINT @CP030D
			SET @STEP = '5'
			if object_id('tempdb..#GEN_WO') is not null drop table #GEN_WO
			Select * Into #GEN_WO From GEN_WO Where 1> 2
			SET @SQLCmd = N'INSERT INTO #GEN_WO SELECT * FROM ' + @GEN_WO + ' WHERE MIS_DATE BETWEEN ''' + @V_MISDATE_FIRSTDAY + ''' AND ''' + @V_MIS_DATE + ''' ' 
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '6'
			if object_id('tempdb..#CP030D') is not null drop table #CP030D
			Select * Into #CP030D From CP030D Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CP030D SELECT * FROM ' + @CP030D + ' WHERE MIS_DATE BETWEEN ''' + @V_MISDATE_FIRSTDAY + ''' AND ''' + @V_MIS_DATE + ''' ' 
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '7'
			if object_id('tempdb..#WOLIST') is not null drop table #WOLIST
			SELECT IIF(BUSINESS='CTL','BO',RIGHT(dbo.ALLTRIM(BUSINESS),2)) AS PD, * 
			INTO #WOLIST 
			FROM #GEN_WO
			WHERE LEFT(CONVERT(VARCHAR,WODATE,23),7) = LEFT(@V_MIS_DATE,7)  
			AND (BUSINESS LIKE '%NA%' OR BUSINESS LIKE '%DC%' OR BUSINESS LIKE '%CTL%')
			SET @STEP = '8'
			if object_id('tempdb..#CPLIST') is not null drop table #CPLIST
			SELECT DATEAS,CYCLE,CYCDUE,ACCT,AMT,PRODTYPE,BUSINESS,'BB' AS PD 
			INTO #CPLIST 
			FROM #CP030D WHERE TYPE='BADRP' AND CYCDUE < 6
			ALTER TABLE #CPLIST ALTER COLUMN PD VARCHAR(5)
			INSERT INTO #CPLIST
			SELECT DATEAS,CYCLE,CYCDUE,ACCT,AMT,PRODTYPE,BUSINESS,IIF(BUSINESS='CTL','BO',RIGHT(dbo.ALLTRIM(BUSINESS),2)) AS PD 
			FROM #CP030D WHERE TYPE='BAU'
			INSERT INTO #CPLIST
			SELECT DATEAS,CYCLE,CYCDUE,ACCT,AMT,PRODTYPE,BUSINESS,'BB' AS PD 
			FROM #CP030D WHERE TYPE='RW'
			INSERT INTO #CPLIST
			SELECT DATEAS,CYCLE,CYCDUE,ACCT,AMT,PRODTYPE,BUSINESS,RIGHT(dbo.ALLTRIM(BUSINESS),2) AS PD 
			FROM #CP030D WHERE TYPE = 'CP030D'
			AND (BUSINESS LIKE '%NA%' OR BUSINESS LIKE '%DC%' OR PRODTYPE='PCL') 
			INSERT INTO #CPLIST
			SELECT CAST((SUBSTRING(CONVERT(VARCHAR,WODATE,23),1,8)+CAST(CYCLEID AS VARCHAR)) AS DATE) AS DATEAS 
			, IIF(CYCLEID<10,'0' + CAST(CYCLEID AS VARCHAR), CAST(CYCLEID AS VARCHAR)) AS CYCLE, EW_CDC+1 AS CYCDUE,COUNT(CARDNMBR)*(-1) AS ACCT,SUM(WOAMT)*(-1) AS AMT,PRODTYPE,BUSINESS,PD 
			FROM #WOLIST 
			WHERE (isnull(PGM_CODE,'') LIKE '%DRP%' OR isnull(PGM_CODE,'') LIKE '%RW%') 
			AND DAY(WODATE) <> CYCLEID 
			AND ISNULL(EW_STUS,'') = '' 
			GROUP BY WODATE,LOGO,CYCLEID,EW_CDC,PRODTYPE,BUSINESS,PD
			SET @STEP = '9'
			UPDATE #CPLIST SET PD='CL' WHERE PRODTYPE='PCL'
			UPDATE #CPLIST SET PD='PIL' WHERE PRODTYPE='PIL'
			delete From #CPLIST where CYCLE <= DAY(@D_MIS_DATE) AND PD = 'CL' AND CYCDUE > 5
			delete From #CPLIST where CYCLE > DAY(@D_MIS_DATE) AND PD = 'CL' AND CYCDUE > 4
			SET @STEP = '10'
			if object_id('tempdb..#ALL_CYC') is not null drop table #ALL_CYC
			CREATE TABLE #ALL_CYC
			(
				CDC NUMERIC(1),
				CYCLE VARCHAR(3),
				AC_EOP NUMERIC(10),
				BAL_EOP NUMERIC(18,4),
			)
			SET @STEP = '11'
			DECLARE @intLoop INT
			SET @intLoop = 1
			WHILE (@intLoop <= 3)
			BEGIN
				SET @STEP = '12'
				if object_id('tempdb..#CP1') is not null drop table #CP1
				SELECT * INTO #CP1 FROM #CPLIST WHERE 1>2
				DECLARE @PD VARCHAR(10)
				IF (@intLoop = 1)
				BEGIN
					SET @PD='TT'
					SET @STEP = '13'
					INSERT INTO #CP1 SELECT * FROM #CPLIST WHERE PD in ('CT','AC','AW','BO','BB')
				END
				ELSE IF (@intLoop = 2)
				BEGIN
					SET @PD='DC'
					SET @STEP = '14'
					INSERT INTO #CP1 SELECT * FROM #CPLIST WHERE PD='DC'
				END
				ELSE
				BEGIN
					SET @PD='CL'
					SET @STEP = '14'
					INSERT INTO #CP1 SELECT * FROM #CPLIST WHERE PD='CL'
				END
				DECLARE @INTLOOP2 INT = 0
				WHILE @INTLOOP2 <= 6
				BEGIN
					IF (@INTLOOP2<6)
					BEGIN
						SET @STEP = '15'
						if object_id('tempdb..#CURR_M_D') is not null drop table #CURR_M_D
						Create table #CURR_M_D
						(
							MON			CHAR(6),
							CDC			NUMERIC(1),
							CYCLE		NUMERIC(2),
							AC_EOP		NUMERIC(10),
							BAL_EOP		NUMERIC(18,4)
						)
						IF (@PD='DC' AND DAY(DATEADD(D,1,@D_MIS_DATE))=1 AND DAY(@D_MIS_DATE)<>31)
						BEGIN
							INSERT INTO #CURR_M_D
							SELECT LEFT(Convert(varchar,DATEAS,112),6) AS MON,CYCDUE-2 AS CDC,CYCLE,SUM(ACCT) AS AC_EOP,SUM(AMT)/1000 AS BAL_EOP 
							FROM #CP1
							WHERE DAY(DATEAS)=CYCLE
							AND CYCLE<=DAY(@D_MIS_DATE) 
							AND LEFT(Convert(varchar,DATEAS,112),6) = LEFT(Convert(varchar,@D_MIS_DATE,112),6) 
							AND CYCDUE-2=@INTLOOP2
							GROUP BY LEFT(Convert(varchar,DATEAS,112),6),CYCDUE,CYCLE 
							UNION
							SELECT LEFT(Convert(varchar,DATEAS,112),6) AS MON,CYCDUE-1 AS CDC,CYCLE,SUM(ACCT) AS AC_EOP,SUM(AMT)/1000 AS BAL_EOP 
							FROM #CP1
							WHERE DATEAS=@V_MIS_DATE 
							AND CYCLE>DAY(@D_MIS_DATE) 
							AND LEFT(Convert(varchar,DATEAS,112),6) = LEFT(Convert(varchar,@D_MIS_DATE,112),6) 
							AND CYCDUE-2=@INTLOOP2
							GROUP BY LEFT(Convert(varchar,DATEAS,112),6),CYCDUE,CYCLE
						END
						ELSE
						BEGIN
							INSERT INTO #CURR_M_D
							SELECT LEFT(Convert(varchar,DATEAS,112),6) AS MON,CYCDUE-2 AS CDC,CYCLE,SUM(ACCT) AS AC_EOP,SUM(AMT)/1000 AS BAL_EOP 
							FROM #CP1
							WHERE CYCLE<=DAY(@D_MIS_DATE)
							AND DAY(DATEAS)=CYCLE 
							AND LEFT(Convert(varchar,DATEAS,112),6) = LEFT(Convert(varchar,@D_MIS_DATE,112),6) 
							AND CYCDUE-2 = @INTLOOP2
							GROUP BY LEFT(Convert(varchar,DATEAS,112),6),CYCDUE,CYCLE 
							UNION
							SELECT LEFT(Convert(varchar,DATEAS,112),6) AS MON,CYCDUE-1 AS CDC,CYCLE,SUM(ACCT) AS AC_EOP,SUM(AMT)/1000 AS BAL_EOP 
							FROM #CP1
							WHERE DATEAS=@V_MIS_DATE 
							AND CYCLE>DAY(@D_MIS_DATE) 
							AND LEFT(Convert(varchar,DATEAS,112),6) = LEFT(Convert(varchar,@D_MIS_DATE,112),6) 
							AND CYCDUE-1 = @INTLOOP2
							GROUP BY LEFT(Convert(varchar,DATEAS,112),6),CYCDUE,CYCLE
						END
						SET @STEP = '16'
						TRUNCATE TABLE  #ALL_CYC
						DECLARE @CX INT = 1
						WHILE @CX <= 31
						BEGIN
							INSERT INTO #ALL_CYC (CDC, CYCLE) VALUES(@INTLOOP2,@CX)
							SET @CX = @CX + 1
						END
						SET @STEP = '16-1'
						Update #ALL_CYC Set #ALL_CYC.AC_EOP=a.AC_EOP, #ALL_CYC.BAL_EOP=a.BAL_EOP
						From RPT_CARDS_DELQ_CYC a
						Where a.CDC=#ALL_CYC.CDC and a.CYCLE=#ALL_CYC.CYCLE
						and a.mis_date = (Select max(mis_date) from RPT_CARDS_DELQ_CYC where mis_date like SUBSTRING(@V_MIS_DATE,1,8)+'%' and pd=@PD and CDC=@INTLOOP2) 
						and a.pd=@PD and a.CDC=@INTLOOP2 and a.CYCLE < DAY(@D_MIS_DATE)
						SET @STEP = '17'
						UPDATE #ALL_CYC
							SET #ALL_CYC.CDC = #CURR_M_D.CDC
								, #ALL_CYC.AC_EOP = #CURR_M_D.AC_EOP
								, #ALL_CYC.BAL_EOP = #CURR_M_D.BAL_EOP
						FROM #CURR_M_D
						WHERE #CURR_M_D.CYCLE = #ALL_CYC.CYCLE
						AND #CURR_M_D.CYCLE >=DAY(@D_MIS_DATE)
						SET @STEP = '18'
						INSERT INTO RPT_CARDS_DELQ_CYC
						select @D_MIS_DATE, @PD, @INTLOOP2, CYCLE, isnull(AC_EOP,0), isnull(BAL_EOP,0)
						From #ALL_CYC
					END
					ELSE
					BEGIN
					BEGIN
						INSERT INTO RPT_CARDS_DELQ_CYC
						SELECT MIS_DATE,PD,6,CYCLE,SUM(AC_EOP) AC_EOP_SUM, SUM(BAL_EOP) BAL_EOP_SUM
						FROM RPT_CARDS_DELQ_CYC
						WHERE PD=@PD AND CDC > 0 and cdc <6
						AND MIS_DATE = @V_MIS_DATE
						And CYCLE <= 27 
						GROUP BY MIS_DATE,PD,CYCLE ORDER BY CYCLE
					END
					END
					SET @INTLOOP2 = @INTLOOP2 + 1
				END
				SET @STEP = '19'
				SET @intLoop = @intLoop + 1
			END
			SET @STEP = '20'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_DELQ_sp','CARDS DELQ資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', GNE_WO及CP030D尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_DELQ_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

