								WHERE DATEAS = @D_MIS_DATE 
								AND CYCLE > DAY(@D_MIS_DATE) 
								AND LEFT(CONVERT(VARCHAR,DATEAS,112),6) = LEFT(CONVERT(VARCHAR,@D_MIS_DATE,112),6) 
								AND CYCDUE-1 = @INTLOOP2
								GROUP BY LEFT(CONVERT(VARCHAR,DATEAS,112),6), CYCDUE-1, ISNULL(CYCLE,'')
								INSERT INTO #CURR_M_D
								SELECT MON,CDC,CYCLE,SUM(AC_EOP) AS AC_EOP,SUM(BAL_EOP) AS BAL_EOP 
								FROM #CURR_M_TEMP 
								GROUP BY MON,CDC,CYCLE
							END
						END
					END
					SET @STEP = '24'
					TRUNCATE TABLE  #ALL_CYC
					DECLARE @CX INT = 1
					WHILE @CX <= 31
					BEGIN
						INSERT INTO #ALL_CYC (CYCLE) VALUES(@CX)
						SET @CX = @CX + 1
					END
					SET @STEP = '25'
					UPDATE #ALL_CYC
						SET #ALL_CYC.CDC = #PRE_M_D.CDC
							, #ALL_CYC.AC_BOP = #PRE_M_D.AC_BOP
							, #ALL_CYC.BAL_BOP = #PRE_M_D.BAL_BOP
					FROM #PRE_M_D
					WHERE #PRE_M_D.CYCLE = #ALL_CYC.CYCLE
					UPDATE #ALL_CYC
						SET #ALL_CYC.CDC = #CURR_M_D.CDC
							, #ALL_CYC.AC_EOP = #CURR_M_D.AC_EOP
							, #ALL_CYC.BAL_EOP = #CURR_M_D.BAL_EOP
					FROM #CURR_M_D
					WHERE #CURR_M_D.CYCLE = #ALL_CYC.CYCLE
					UPDATE #ALL_CYC 
					SET AC_P = AC_EOP/AC_BOP
						, BAL_P =	BAL_EOP/BAL_BOP
					WHERE ((CDC = 0 AND CYCLE <= DAY(@D_MIS_DATE)) OR CDC <> 0) 
					AND (AC_EOP > 0 AND AC_BOP > 0)
					SET @STEP = '26'
					truncate table #RR1
					INSERT INTO #RR1
					SELECT * FROM #ALL_CYC
					SET @STEP = '27'
					INSERT INTO RPT_CARDS_FLOW_CYC
					select @D_MIS_DATE, @PD, 'CURR', @INTLOOP2, CYCLE, isnull(AC_BOP,0), isnull(AC_EOP,0), isnull(BAL_BOP,0), isnull(BAL_EOP,0), isnull(AC_P,0), isnull(BAL_P,0), 0, 0, 0, 0
					From #ALL_CYC
					SET @STEP = '28'
					TRUNCATE TABLE  #NEXT_M_D
					INSERT INTO #NEXT_M_D
					SELECT LEFT(CONVERT(VARCHAR,DATEAS,112),6) AS MON, CYCDUE-1 AS CDC, CYCLE, SUM(ACCT) AS AC_EOP, SUM(AMT)/1000 AS BAL_EOP 
					FROM #CP1
					WHERE  DATEAS = @D_MIS_DATE 
					AND CYCLE <= DAY(@D_MIS_DATE) 
					AND LEFT(CONVERT(VARCHAR,DATEAS,112),6) = LEFT(CONVERT(VARCHAR,@D_MIS_DATE,112),6) 
					AND CYCDUE-1 = @INTLOOP2
					GROUP BY LEFT(CONVERT(VARCHAR,DATEAS,112),6), CYCDUE-1, CYCLE
					SET @STEP = '29'
					TRUNCATE TABLE  #CURR_M_D1
					INSERT INTO #CURR_M_D1
					SELECT LEFT(CONVERT(VARCHAR,DATEAS,112),6) AS MON, CYCDUE-1 AS CDC, CYCLE, SUM(ACCT) AS AC_BOP, SUM(AMT)/1000 AS BAL_BOP 
					FROM #CP1
					WHERE DAY(DATEAS) = CYCLE 
					AND CYCLE <= DAY(@D_MIS_DATE) 
					AND LEFT(CONVERT(VARCHAR,DATEAS,112),6) = LEFT(CONVERT(VARCHAR,@D_MIS_DATE,112),6) 
					AND CYCDUE-1 = @INTLOOP2
					GROUP BY LEFT(CONVERT(VARCHAR,DATEAS,112),6), CYCDUE-1, CYCLE
					SET @STEP = '30'
					TRUNCATE TABLE  #ALL_CYC
					SET @CX = 1
					WHILE @CX <= 31
					BEGIN
						INSERT INTO #ALL_CYC (CYCLE) VALUES(@CX)
						SET @CX = @CX + 1
					END
					SET @STEP = '31'
					UPDATE #ALL_CYC
						SET #ALL_CYC.CDC = #CURR_M_D1.CDC
							, #ALL_CYC.AC_BOP = #CURR_M_D1.AC_BOP
							, #ALL_CYC.BAL_BOP = #CURR_M_D1.BAL_BOP
					FROM #CURR_M_D1
					WHERE #CURR_M_D1.CYCLE = #ALL_CYC.CYCLE
					SET @STEP = '31-11'
					UPDATE #ALL_CYC
						SET #ALL_CYC.CDC = #NEXT_M_D.CDC
							, #ALL_CYC.AC_EOP = #NEXT_M_D.AC_EOP
							, #ALL_CYC.BAL_EOP = #NEXT_M_D.BAL_EOP
					FROM #NEXT_M_D
					WHERE #NEXT_M_D.CYCLE = #ALL_CYC.CYCLE
					SET @STEP = '31-2'
					UPDATE #ALL_CYC 
					SET AC_P = AC_EOP/AC_BOP
						, BAL_P =	BAL_EOP/BAL_BOP
					WHERE ((CDC = 0 AND CYCLE <= DAY(@D_MIS_DATE)) OR CDC <> 0) 
					AND (AC_EOP > 0 AND AC_BOP > 0)
					SET @STEP = '32'
					truncate table #RR2
					INSERT INTO #RR2
					SELECT * FROM #ALL_CYC
					INSERT INTO RPT_CARDS_FLOW_CYC
					select @D_MIS_DATE, @PD, 'NEXT', @INTLOOP2, CYCLE, isnull(AC_BOP,0), isnull(AC_EOP,0), isnull(BAL_BOP,0), isnull(BAL_EOP,0), isnull(AC_P,0), isnull(BAL_P,0), 0, 0, 0, 0
					From #ALL_CYC
					SET @STEP = '33'
					TRUNCATE TABLE #CURR_RATE
					TRUNCATE TABLE #NEXT_RATE
					DECLARE @CX2 INT
					SET @CX2 = 1
					WHILE @CX2 <= 31
					BEGIN
						INSERT INTO #CURR_RATE (DAY,BUCKET) VALUES(@CX2,@INTLOOP2)
						SET @CX2 = @CX2 + 1
					END
					Update #CURR_RATE Set #CURR_RATE.FLOW=a.FLOW
					From RPT_CARDS_FLOW_RATE a
					Where a.BUCKET=#CURR_RATE.BUCKET and a.DAY=#CURR_RATE.DAY
					and a.mis_date = (Select max(mis_date) from RPT_CARDS_FLOW_RATE where mis_date like SUBSTRING(@V_MIS_DATE,1,8)+'%' and bucket=@INTLOOP2 and pd=@PD and pd_result='CURR') 
					and a.pd=@PD and a.pd_result='CURR' and a.BUCKET=@INTLOOP2
					SET @STEP = '34'
					SET @CX2 = 1
					WHILE @CX2 <= 31
					BEGIN
						INSERT INTO #NEXT_RATE (DAY,BUCKET) VALUES(@CX2,@INTLOOP2)
						SET @CX2 = @CX2 + 1
					END
					Update #NEXT_RATE Set #NEXT_RATE.FLOW=a.FLOW
					From RPT_CARDS_FLOW_RATE a
					Where a.BUCKET=#NEXT_RATE.BUCKET and a.DAY=#NEXT_RATE.DAY
					and a.mis_date = (Select max(mis_date) from RPT_CARDS_FLOW_RATE where mis_date like SUBSTRING(@V_MIS_DATE,1,8)+'%' and bucket=@INTLOOP2 and pd=@PD and pd_result='NEXT') 
					and a.pd=@PD and a.pd_result='NEXT' and a.BUCKET=@INTLOOP2
					SET @STEP = '48'
					IF ((@INTLOOP2=0 AND (@D_MIS_DATE < @D_MON_E)) OR (@INTLOOP2=0 AND @PD='DC'))
					BEGIN
						SET @STEP = '49'
						INSERT INTO #RR3
						SELECT @INTLOOP2 AS CDC, IIF(SUM(isnull(BAL_BOP,0))=0,0.0000,ROUND(SUM(isnull(BAL_EOP,0))/SUM(isnull(BAL_BOP,0)),4)) AS FLOW
						FROM #RR1 
						WHERE CYCLE <= DAY(@D_MIS_DATE)
						INSERT INTO #RR4
						SELECT @INTLOOP2 AS CDC,IIF(SUM(isnull(BAL_BOP,0))=0,0.0000,ROUND(SUM(isnull(BAL_EOP,0))/SUM(isnull(BAL_BOP,0)),4)) AS FLOW
						FROM #RR1
					END
					ELSE
					BEGIN
						SET @STEP = '50'
						INSERT INTO #RR3
						SELECT @INTLOOP2 AS CDC, IIF(SUM(isnull(BAL_BOP,0))=0,0.0000,ROUND(SUM(isnull(BAL_EOP,0))/SUM(isnull(BAL_BOP,0)),4)) AS FLOW
						FROM #RR1
						INSERT INTO #RR4
						SELECT @INTLOOP2 AS CDC, IIF(SUM(isnull(BAL_BOP,0))=0,0.0000,ROUND(SUM(isnull(BAL_EOP,0))/SUM(isnull(BAL_BOP,0)),4)) AS FLOW
						FROM #RR2
					END
					SET @STEP = '51'
					UPDATE #CURR_RATE
						SET #CURR_RATE.FLOW = #RR3.FLOW
					FROM #RR3
					WHERE CAST(DAY(@D_MIS_DATE) AS VARCHAR)+'M'+CAST(#RR3.CDC AS VARCHAR) = CAST(#CURR_RATE.[DAY] AS VARCHAR)+'M'+ CAST(#CURR_RATE.BUCKET AS VARCHAR)
					UPDATE #NEXT_RATE
						SET #NEXT_RATE.FLOW = #RR4.FLOW
					FROM #RR4
					WHERE CAST(DAY(@D_MIS_DATE) AS VARCHAR)+'M'+CAST(#RR4.CDC AS VARCHAR) = CAST(#NEXT_RATE.[DAY] AS VARCHAR)+'M'+ CAST(#NEXT_RATE.BUCKET AS VARCHAR)
					INSERT INTO RPT_CARDS_FLOW_RATE
					Select @D_MIS_DATE, @PD, 'CURR', [DAY], BUCKET, isnull(FLOW,0)
					From #CURR_RATE
					INSERT INTO RPT_CARDS_FLOW_RATE
					Select @D_MIS_DATE, @PD, 'NEXT', [DAY], BUCKET, isnull(FLOW,0)
					From #NEXT_RATE
					SET @INTLOOP2 = @INTLOOP2 + 1
				END
				SET @intLoop = @intLoop + 1
			END
			/*
			TT CURR M6 要把CYCLE 27~31的總值更新至CYC27
			*/
			DECLARE @AC_BOP_SUM1 NUMERIC(16,0)
			DECLARE @AC_EOP_SUM1 NUMERIC(10,0)
			DECLARE @BAL_BOP_SUM1 NUMERIC(18,4)
			DECLARE @BAL_EOP_SUM1 NUMERIC(18,4)
			SELECT @AC_BOP_SUM1=SUM(AC_BOP), @AC_EOP_SUM1=SUM(AC_EOP), @BAL_BOP_SUM1=SUM(BAL_BOP), @BAL_EOP_SUM1=SUM(BAL_EOP)
			FROM RPT_CARDS_FLOW_CYC
			WHERE MIS_DATE=@V_MIS_DATE AND PD='TT' AND PD_RESULT='CURR' AND CDC=6 AND CYCLE >=27
			Update RPT_CARDS_FLOW_CYC set AC_BOP=@AC_BOP_SUM1
									    , AC_EOP=@AC_EOP_SUM1
										, BAL_BOP=@BAL_BOP_SUM1
										, BAL_EOP=@BAL_EOP_SUM1
			WHERE MIS_DATE=@V_MIS_DATE 
			AND PD='TT' AND PD_RESULT='CURR' AND CDC=6 AND CYCLE=27
			Update RPT_CARDS_FLOW_CYC set AC_BOP=0
									    , AC_EOP=0
										, BAL_BOP=0
										, BAL_EOP=0
			WHERE MIS_DATE=@V_MIS_DATE 
			AND PD='TT' AND PD_RESULT='CURR' AND CDC=6 AND CYCLE>27
			/*
			CL CURR M4 要把CYCLE 27~31的總值更新至CYC27
			*/
			SELECT @AC_BOP_SUM1=SUM(AC_BOP), @AC_EOP_SUM1=SUM(AC_EOP), @BAL_BOP_SUM1=SUM(BAL_BOP), @BAL_EOP_SUM1=SUM(BAL_EOP)
			FROM RPT_CARDS_FLOW_CYC
			WHERE MIS_DATE=@V_MIS_DATE AND PD='CL' AND PD_RESULT='CURR' AND CDC=4 AND CYCLE >=27
			Update RPT_CARDS_FLOW_CYC set AC_BOP=@AC_BOP_SUM1
									    , AC_EOP=@AC_EOP_SUM1
										, BAL_BOP=@BAL_BOP_SUM1
										, BAL_EOP=@BAL_EOP_SUM1
			WHERE MIS_DATE=@V_MIS_DATE 
			AND PD='CL' AND PD_RESULT='CURR' AND CDC=4 AND CYCLE=27
			Update RPT_CARDS_FLOW_CYC set AC_BOP=0
									    , AC_EOP=0
										, BAL_BOP=0
										, BAL_EOP=0
			WHERE MIS_DATE=@V_MIS_DATE 
			AND PD='CL' AND PD_RESULT='CURR' AND CDC=4 AND CYCLE>27
			/*
			計算 RPT_CARDS_FLOW_CYC - FLOW_R_AC_CUM 及 FLOW_R_AMT_CUM 累計值
			*/
			SET @STEP = '60'
			DECLARE @PD_RESULT		VARCHAR(4)
			DECLARE @CDC			NUMERIC(1)
			DECLARE FLOW_ITEM CURSOR FAST_FORWARD FOR
			SELECT DISTINCT PD, PD_RESULT, CDC
				FROM RPT_CARDS_FLOW_CYC 
				WHERE MIS_DATE = @V_MIS_DATE 
				AND PD in ('TT','CL','DC')
			OPEN FLOW_ITEM
			FETCH NEXT FROM FLOW_ITEM INTO @PD, @PD_RESULT, @CDC
			WHILE @@FETCH_STATUS = 0
			BEGIN
					DECLARE @MIS_DATE		DATE
					DECLARE @CYCLE			NUMERIC(2)
					DECLARE @AC_BOP			NUMERIC(16)
					DECLARE @AC_EOP			NUMERIC(10)
					DECLARE @BAL_BOP		NUMERIC(18,4)
					DECLARE @BAL_EOP		NUMERIC(18,4)
					DECLARE @AC_BOP_SUM		NUMERIC(16) = 0
					DECLARE @AC_EOP_SUM		NUMERIC(16) = 0
					DECLARE @BAL_BOP_SUM	NUMERIC(18,4) = 0
					DECLARE @BAL_EOP_SUM	NUMERIC(18,4) = 0
					DECLARE @FLOW_R_AC		NUMERIC(10,4)
					DECLARE @FLOW_R_AC_CUM	NUMERIC(10,4)
					DECLARE @FLOW_R_AMT		NUMERIC(10,4)
					DECLARE @FLOW_R_AMT_CUM	NUMERIC(10,4)
					DECLARE FLOW_CYC CURSOR FAST_FORWARD FOR
						SELECT MIS_DATE, PD, PD_RESULT, CDC, CYCLE, AC_BOP, AC_EOP, BAL_BOP, BAL_EOP 
						FROM RPT_CARDS_FLOW_CYC 
						WHERE MIS_DATE=@V_MIS_DATE 
						AND PD = @PD 
						AND PD_RESULT = @PD_RESULT 
						AND CDC = @CDC 
						ORDER BY CYCLE
					OPEN FLOW_CYC
					FETCH NEXT FROM FLOW_CYC INTO @MIS_DATE, @PD, @PD_RESULT, @CDC, @CYCLE, @AC_BOP, @AC_EOP, @BAL_BOP, @BAL_EOP
					WHILE @@FETCH_STATUS = 0
					BEGIN
						SET @FLOW_R_AC = IIF(@AC_BOP = 0, 0, @AC_EOP / @AC_BOP)
						SET @FLOW_R_AMT = IIF(@BAL_BOP = 0, 0, @BAL_EOP / @BAL_BOP)
						SET @AC_EOP_SUM = @AC_EOP_SUM + @AC_EOP
						SET @AC_BOP_SUM = @AC_BOP_SUM + @AC_BOP
						SET @BAL_EOP_SUM = @BAL_EOP_SUM + @BAL_EOP
						SET @BAL_BOP_SUM = @BAL_BOP_SUM + @BAL_BOP
						SET @FLOW_R_AC_CUM = IIF(@AC_BOP_SUM = 0, 0, @AC_EOP_SUM / @AC_BOP_SUM)
						SET @FLOW_R_AMT_CUM = IIF(@BAL_BOP_SUM = 0, 0, @BAL_EOP_SUM / @BAL_BOP_SUM)
						Update RPT_CARDS_FLOW_CYC 
						Set FLOW_R_AC=@FLOW_R_AC,
							FLOW_R_AMT=@FLOW_R_AMT,
							FLOW_R_AC_CUM=@FLOW_R_AC_CUM,
							FLOW_R_AMT_CUM=@FLOW_R_AMT_CUM
						Where MIS_DATE=@MIS_DATE and PD=@PD and PD_RESULT=@PD_RESULT and CDC=@CDC and CYCLE=@CYCLE
						FETCH NEXT FROM FLOW_CYC INTO @MIS_DATE, @PD, @PD_RESULT, @CDC, @CYCLE, @AC_BOP, @AC_EOP, @BAL_BOP, @BAL_EOP
					END
					CLOSE FLOW_CYC
					DEALLOCATE FLOW_CYC
					FETCH NEXT FROM FLOW_ITEM INTO @PD, @PD_RESULT, @CDC
			END
			CLOSE FLOW_ITEM
			DEALLOCATE FLOW_ITEM
			SET @STEP = '61'
			/*
			TT CURR M6 要把CYC CYCLE 27的總值更新至 RATE CYC27
			*/
			UPDATE RPT_CARDS_FLOW_RATE SET FLOW=(SELECT FLOW_R_AMT_CUM FROM RPT_CARDS_FLOW_CYC WHERE MIS_DATE=@V_MIS_DATE AND PD='TT' AND PD_RESULT='CURR' AND CDC=6 AND CYCLE=27)
			WHERE MIS_DATE=@V_MIS_DATE AND PD='TT' AND PD_RESULT='CURR' AND BUCKET=6 AND DAY=27
			/*
			CL CURR M4 要把CYC CYCLE 27的總值更新至 RATE CYC27
			*/
			UPDATE RPT_CARDS_FLOW_RATE SET FLOW=(SELECT FLOW_R_AMT_CUM FROM RPT_CARDS_FLOW_CYC WHERE MIS_DATE=@V_MIS_DATE AND PD='CL' AND PD_RESULT='CURR' AND CDC=4 AND CYCLE=27)
			WHERE MIS_DATE=@V_MIS_DATE AND PD='CL' AND PD_RESULT='CURR' AND BUCKET=4 AND DAY=27
			SET @STEP = '70'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_FLOW_sp','CARDS FLOW資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', GNE_WO及CP030D尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_FLOW_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

