ALTER PROCEDURE [dbo].[TRANS_REPORT_CARDS_GWO_sp]
	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	DECLARE @V_MISDATE_FIRSTDAY VARCHAR(10)
	SET @V_MISDATE_FIRSTDAY = LEFT(@V_MIS_DATE,8) + '01'
	BEGIN TRY
		/*DECLARE @STEP VARCHAR(5)
		DECLARE @SQLCmd NVARCHAR(4000)
		DECLARE @V_MIS_DATE VARCHAR(10)
		SET @V_MIS_DATE = '2018-09-18'
		DECLARE @D_MIS_DATE DATE
		SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)		
		DECLARE @V_MISDATE_FIRSTDAY VARCHAR(10)
		SET @V_MISDATE_FIRSTDAY = LEFT(@V_MIS_DATE,8) + '01'*/
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'GEN_WO',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'CP030D',@YYYYMMDD, @OUTPUT2 output
		DECLARE @OUTPUT3 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'CARDS',@YYYYMMDD, @OUTPUT3 output
		PRINT @OUTPUT1
		PRINT @OUTPUT2
		PRINT @OUTPUT3
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S' and left(@OUTPUT3,1) = 'S')
		BEGIN
			SET @STEP = '4'
			DELETE FROM LOG_AUTOMATION WHERE MIS_DATE=@V_MIS_DATE AND TABLE_NAME='TRANS_REPORT_CARDS_GWO_sp'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_GWO_sp','CARDS GWO資料處理開始','N'
			DELETE FROM RPT_CARDS_GWO_CYC WHERE MIS_DATE=@V_MIS_DATE
			DELETE FROM RPT_CARDS_GWO_BAL WHERE MIS_DATE=@V_MIS_DATE
			DECLARE @GEN_WO VARCHAR(50)
			SET @GEN_WO = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @CP030D VARCHAR(50)
			SET @CP030D = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			DECLARE @CARDS VARCHAR(50)
			SET @CARDS = RIGHT(@OUTPUT3,LEN(@OUTPUT3)-2)
			PRINT @GEN_WO
			PRINT @CP030D
			PRINT @CARDS
			SET @STEP = '5'
			if object_id('tempdb..#GEN_WO') is not null drop table #GEN_WO
			Select * Into #GEN_WO From GEN_WO Where 1> 2
			SET @SQLCmd = N'INSERT INTO #GEN_WO SELECT * FROM ' + @GEN_WO + ' WHERE MIS_DATE BETWEEN ''' + @V_MISDATE_FIRSTDAY + ''' AND ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '6'
			if object_id('tempdb..#CP030D') is not null drop table #CP030D
			Select * Into #CP030D From CP030D Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CP030D SELECT * FROM ' + @CP030D + ' WHERE MIS_DATE BETWEEN ''' + @V_MISDATE_FIRSTDAY + ''' AND ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '7'
			if object_id('tempdb..#CARDS') is not null drop table #CARDS
			Select * Into #CARDS From CARDS Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CARDS SELECT * FROM ' + @CARDS + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '8'
			if object_id('tempdb..#CALIST') is not null drop table #CALIST
			SELECT IIF(BUSINESS='CTL','BO',RIGHT(dbo.ALLTRIM(BUSINESS),2)) AS PD,DATEAS,CARDNMBR,CYCLEID AS CYCLEID,CDC,BALANCE,PGM_CODE 
			INTO #CALIST
			FROM #CARDS
			WHERE DAY(DATEAS) < CYCLEID AND CDC=6 AND BALANCE<=6500 AND SUBSTRING(AGE_HIS,1,1)='7' AND WOAMT=0 AND (BUSINESS LIKE '%NA%' OR BUSINESS LIKE '%DC%' OR BUSINESS LIKE '%CTL%') AND PRODTYPE!='PCL' 
			UNION
			SELECT 'CL' AS PD,DATEAS,CARDNMBR,CYCLEID AS CYCLEID,CDC,BALANCE,PGM_CODE 
			FROM #CARDS
			WHERE DAY(DATEAS) < CYCLEID AND CDC=4 AND BALANCE<5000 AND SUBSTRING(AGE_HIS,1,1)='5' AND WOAMT=0 AND BUSINESS LIKE '%CTL%' AND PRODTYPE='PCL' 
			UNION
			SELECT 'CL' AS PD,DATEAS,CARDNMBR,CYCLEID AS CYCLEID,CDC,BALANCE,PGM_CODE 
			FROM #CARDS
			WHERE DAY(DATEAS) = CYCLEID AND CDC=5 AND BALANCE<5000 AND SUBSTRING(AGE_HIS,1,1)='6' AND WOAMT=0 AND BUSINESS LIKE '%CTL%' AND PRODTYPE='PCL' 
			UNION
			SELECT 'CL' AS PD,DATEAS,CARDNMBR,CYCLEID AS CYCLEID,CDC,BALANCE,PGM_CODE 
			FROM #CARDS
			WHERE DAY(DATEAS) < CYCLEID AND CDC=4 AND BALANCE<5000 AND SUBSTRING(AGE_HIS,1,1)='5' AND WOAMT=0 AND BUSINESS LIKE '%NA%' AND PRODTYPE='PCL' 
			UNION
			SELECT 'CL' AS PD,DATEAS,CARDNMBR,CYCLEID AS CYCLEID,CDC,BALANCE,PGM_CODE 
			FROM #CARDS
			WHERE DAY(DATEAS) = CYCLEID AND CDC=5 AND BALANCE<5000 AND SUBSTRING(AGE_HIS,1,1)='6' AND WOAMT=0 AND BUSINESS LIKE '%NA%' AND PRODTYPE='PCL' 
			SET @STEP = '9'
			if object_id('tempdb..#CPLIST') is not null drop table #CPLIST
			SELECT DATEAS,CYCLE,CYCDUE,ACCT,AMT,PRODTYPE,BUSINESS,IIF(BUSINESS='CTL','BO',RIGHT(dbo.ALLTRIM(BUSINESS),2)) AS PD 
