					SELECT COUNT(*) AS TOTAL, WRITEDATE, SUBPROD, SUM(WOPRIAMT) AS AMT, SUM(WOAMT) AS INTAMT
					FROM #EWACCT1 
					GROUP BY WRITEDATE,SUBPROD
				OPEN CURSOR_EWACCT3
				FETCH NEXT FROM CURSOR_EWACCT3 INTO @TOTAL, @WRITEDATE, @SUBPROD,  @WOPRIAMT, @WOAMT
				WHILE @@FETCH_STATUS = 0
				BEGIN
					SET @intLoop2 = 0
					WHILE (@intLoop2 <= 6)
					BEGIN
						IF (@SUBPROD <> 'RMS')
						BEGIN	
							SET @STEP = '62'
							SET @SQLCmd = N'UPDATE #GWO_BAL_TEMP '
							SET @SQLCmd = @SQLCmd + N'SET TTL_A = ' + CAST(@TOTAL AS VARCHAR)
							SET @SQLCmd = @SQLCmd + N'  , TTL_P = CAST(' + CAST(@WOPRIAMT AS VARCHAR) + '/1000 AS NUMERIC(15,3)) '
							SET @SQLCmd = @SQLCmd + N'  , TTL_PI = CAST(' + CAST(@WOAMT AS VARCHAR) + '/1000 AS NUMERIC(15,3)) '
							SET @SQLCmd = @SQLCmd + N'WHERE CYCLE=' + CAST(DAY(@WRITEDATE) AS VARCHAR) + ' AND PD=''' + @TYPE + @SUBPROD + ''' '				
							PRINT @SQLCmd
							EXECUTE sp_executesql @SQLCmd
						END
						Set @intLoop2 = @intLoop2 + 1
					END
					FETCH NEXT FROM CURSOR_EWACCT3 INTO @TOTAL, @WRITEDATE, @SUBPROD,  @WOPRIAMT, @WOAMT
				END
				CLOSE CURSOR_EWACCT3
				DEALLOCATE CURSOR_EWACCT3
				SET @STEP = '63'
				DECLARE CURSOR_EWACCT4 CURSOR FAST_FORWARD FOR
					SELECT COUNT(*) AS TOTAL, WRITEDATE, SUM(WOPRIAMT) AS AMT, SUM(WOAMT) AS INTAMT
					FROM #EWACCT1 WHERE SUBPROD IN ('CT','AR','AW') 
					GROUP BY WRITEDATE
				OPEN CURSOR_EWACCT4
				FETCH NEXT FROM CURSOR_EWACCT4 INTO @TOTAL, @WRITEDATE, @WOPRIAMT, @WOAMT
				WHILE @@FETCH_STATUS = 0
				BEGIN
					SET @intLoop2 = 1
					WHILE (@intLoop2 <= 31)
					BEGIN
						IF (DAY(@WRITEDATE) = @intLoop2)
						BEGIN
							SET @STEP = '64'
							SET @SQLCmd = N'UPDATE #GWO_BAL_TEMP '
							SET @SQLCmd = @SQLCmd + N'SET TTL_A = ' + CAST(@TOTAL AS VARCHAR)
							SET @SQLCmd = @SQLCmd + N'  , TTL_P = CAST(' + CAST(@WOPRIAMT AS VARCHAR) + '/1000 AS NUMERIC(15,3)) '
							SET @SQLCmd = @SQLCmd + N'  , TTL_PI = CAST(' + CAST(@WOAMT AS VARCHAR) + '/1000 AS NUMERIC(15,3)) '
							SET @SQLCmd = @SQLCmd + N'WHERE CYCLE=' + CAST(@intLoop2 AS VARCHAR) + ' AND PD=''' + @TYPE + 'RMS'' '				
							PRINT @SQLCmd
							EXECUTE sp_executesql @SQLCmd
						END
						Set @intLoop2 = @intLoop2 + 1
					END
					FETCH NEXT FROM CURSOR_EWACCT4 INTO @TOTAL, @WRITEDATE,  @WOPRIAMT, @WOAMT
				END
				CLOSE CURSOR_EWACCT4
				DEALLOCATE CURSOR_EWACCT4
				SET @STEP = '65'
				SET @intTypeCnt = @intTypeCnt + 1
			END
			SET @STEP = '70'
			INSERT INTO RPT_CARDS_GWO_BAL
			SELECT MIS_DATE,'TT','A_LOSS',CYCLE,SUM(CDC0_A),SUM(CDC0_P),SUM(CDC0_PI),SUM(CDC1_A),SUM(CDC1_P),SUM(CDC1_PI),SUM(CDC2_A),SUM(CDC2_P),SUM(CDC2_PI),SUM(CDC3_A),SUM(CDC3_P),SUM(CDC3_PI),SUM(CDC4_A),SUM(CDC4_P),SUM(CDC4_PI),SUM(CDC5_A),SUM(CDC5_P),SUM(CDC5_PI),SUM(CDC6_A),SUM(CDC6_P),SUM(CDC6_PI),SUM(TTL_A),SUM(TTL_P),SUM(TTL_PI)
			FROM #GWO_BAL_TEMP WHERE PD IN ('RMS','DC','BOCA')
			GROUP BY MIS_DATE,CYCLE
			INSERT INTO RPT_CARDS_GWO_BAL
			SELECT MIS_DATE,'TT','B_LOSS',CYCLE,SUM(CDC0_A),SUM(CDC0_P),SUM(CDC0_PI),SUM(CDC1_A),SUM(CDC1_P),SUM(CDC1_PI),SUM(CDC2_A),SUM(CDC2_P),SUM(CDC2_PI),SUM(CDC3_A),SUM(CDC3_P),SUM(CDC3_PI),SUM(CDC4_A),SUM(CDC4_P),SUM(CDC4_PI),SUM(CDC5_A),SUM(CDC5_P),SUM(CDC5_PI),SUM(CDC6_A),SUM(CDC6_P),SUM(CDC6_PI),SUM(TTL_A),SUM(TTL_P),SUM(TTL_PI)
			FROM #GWO_BAL_TEMP WHERE PD IN ('B_RMS','B_DC','B_BOCA')
			GROUP BY MIS_DATE,CYCLE
			INSERT INTO RPT_CARDS_GWO_BAL
			SELECT MIS_DATE,'TT','S_LOSS',CYCLE,SUM(CDC0_A),SUM(CDC0_P),SUM(CDC0_PI),SUM(CDC1_A),SUM(CDC1_P),SUM(CDC1_PI),SUM(CDC2_A),SUM(CDC2_P),SUM(CDC2_PI),SUM(CDC3_A),SUM(CDC3_P),SUM(CDC3_PI),SUM(CDC4_A),SUM(CDC4_P),SUM(CDC4_PI),SUM(CDC5_A),SUM(CDC5_P),SUM(CDC5_PI),SUM(CDC6_A),SUM(CDC6_P),SUM(CDC6_PI),SUM(TTL_A),SUM(TTL_P),SUM(TTL_PI)
			FROM #GWO_BAL_TEMP WHERE PD IN ('S_RMS','S_DC','S_BOCA')
			GROUP BY MIS_DATE,CYCLE
			INSERT INTO RPT_CARDS_GWO_BAL
			SELECT MIS_DATE,'TT','D_LOSS',CYCLE,SUM(CDC0_A),SUM(CDC0_P),SUM(CDC0_PI),SUM(CDC1_A),SUM(CDC1_P),SUM(CDC1_PI),SUM(CDC2_A),SUM(CDC2_P),SUM(CDC2_PI),SUM(CDC3_A),SUM(CDC3_P),SUM(CDC3_PI),SUM(CDC4_A),SUM(CDC4_P),SUM(CDC4_PI),SUM(CDC5_A),SUM(CDC5_P),SUM(CDC5_PI),SUM(CDC6_A),SUM(CDC6_P),SUM(CDC6_PI),SUM(TTL_A),SUM(TTL_P),SUM(TTL_PI)
			FROM #GWO_BAL_TEMP WHERE PD IN ('D_RMS','D_DC','D_BOCA')
			GROUP BY MIS_DATE,CYCLE
			INSERT INTO RPT_CARDS_GWO_BAL
			SELECT MIS_DATE,'CL','A_LOSS',CYCLE,SUM(CDC0_A),SUM(CDC0_P),SUM(CDC0_PI),SUM(CDC1_A),SUM(CDC1_P),SUM(CDC1_PI),SUM(CDC2_A),SUM(CDC2_P),SUM(CDC2_PI),SUM(CDC3_A),SUM(CDC3_P),SUM(CDC3_PI),SUM(CDC4_A),SUM(CDC4_P),SUM(CDC4_PI),SUM(CDC5_A),SUM(CDC5_P),SUM(CDC5_PI),SUM(CDC6_A),SUM(CDC6_P),SUM(CDC6_PI),SUM(TTL_A),SUM(TTL_P),SUM(TTL_PI)
			FROM #GWO_BAL_TEMP WHERE PD='CL'
			GROUP BY MIS_DATE,CYCLE
			INSERT INTO RPT_CARDS_GWO_BAL
			SELECT MIS_DATE,'CL','B_LOSS',CYCLE,SUM(CDC0_A),SUM(CDC0_P),SUM(CDC0_PI),SUM(CDC1_A),SUM(CDC1_P),SUM(CDC1_PI),SUM(CDC2_A),SUM(CDC2_P),SUM(CDC2_PI),SUM(CDC3_A),SUM(CDC3_P),SUM(CDC3_PI),SUM(CDC4_A),SUM(CDC4_P),SUM(CDC4_PI),SUM(CDC5_A),SUM(CDC5_P),SUM(CDC5_PI),SUM(CDC6_A),SUM(CDC6_P),SUM(CDC6_PI),SUM(TTL_A),SUM(TTL_P),SUM(TTL_PI)
			FROM #GWO_BAL_TEMP WHERE PD='B_CL'
			GROUP BY MIS_DATE,CYCLE
			INSERT INTO RPT_CARDS_GWO_BAL
			SELECT MIS_DATE,'DC','A_LOSS',CYCLE,SUM(CDC0_A),SUM(CDC0_P),SUM(CDC0_PI),SUM(CDC1_A),SUM(CDC1_P),SUM(CDC1_PI),SUM(CDC2_A),SUM(CDC2_P),SUM(CDC2_PI),SUM(CDC3_A),SUM(CDC3_P),SUM(CDC3_PI),SUM(CDC4_A),SUM(CDC4_P),SUM(CDC4_PI),SUM(CDC5_A),SUM(CDC5_P),SUM(CDC5_PI),SUM(CDC6_A),SUM(CDC6_P),SUM(CDC6_PI),SUM(TTL_A),SUM(TTL_P),SUM(TTL_PI)
			FROM #GWO_BAL_TEMP WHERE PD='DC'
			GROUP BY MIS_DATE,CYCLE
			INSERT INTO RPT_CARDS_GWO_BAL
			SELECT MIS_DATE,'DC','B_LOSS',CYCLE,SUM(CDC0_A),SUM(CDC0_P),SUM(CDC0_PI),SUM(CDC1_A),SUM(CDC1_P),SUM(CDC1_PI),SUM(CDC2_A),SUM(CDC2_P),SUM(CDC2_PI),SUM(CDC3_A),SUM(CDC3_P),SUM(CDC3_PI),SUM(CDC4_A),SUM(CDC4_P),SUM(CDC4_PI),SUM(CDC5_A),SUM(CDC5_P),SUM(CDC5_PI),SUM(CDC6_A),SUM(CDC6_P),SUM(CDC6_PI),SUM(TTL_A),SUM(TTL_P),SUM(TTL_PI)
			FROM #GWO_BAL_TEMP WHERE PD='B_DC'
			GROUP BY MIS_DATE,CYCLE
			SET @STEP = '71'
			SET @intLoop = 1
			WHILE (@intLoop <= 3)
			BEGIN
				SET @STEP = '72'
				IF (@intLoop = 1)
				BEGIN
					SET @PD='TT'
				END
				ELSE IF (@intLoop = 2)
				BEGIN
					SET @PD='DC'
				END
				ELSE
				BEGIN
					SET @PD='CL'
				END
				SET @STEP = '73'
				INSERT INTO RPT_CARDS_GWO_CYC
				select a.mis_date,a.PD,'C_LOSS',6,a.CYCLE
				, a.AC_EOP-b.TTL_A-c.TTL_A C_LOSS_AC_EOP
				, a.BAL_EOP-b.TTL_PI-c.TTL_PI C_LOSS_BAL_EOP
				from RPT_CARDS_GWO_CYC a
				left join RPT_CARDS_GWO_BAL b on b.MIS_DATE=a.MIS_DATE and a.pd=b.pd and b.CYCLE=a.CYCLE and b.PD_RESULT='A_LOSS'
				left join RPT_CARDS_GWO_BAL c on c.MIS_DATE=a.MIS_DATE and a.pd=c.pd and c.CYCLE=a.CYCLE and c.PD_RESULT='B_LOSS'
				where a.pd=@PD and a.mis_date=@V_MIS_DATE and a.PD_RESULT='GWO'
				order by cycle
				SET @STEP = '74'
				INSERT INTO RPT_CARDS_GWO_CYC
				select a.mis_date,a.PD,'C_LOSS_P',6,a.CYCLE
				, a.AC_EOP-b.TTL_A-c.TTL_A C_LOSS_P_AC_EOP
				, a.BAL_EOP-b.TTL_P-c.TTL_P C_LOSS_P_BAL_EOP
				from RPT_CARDS_GWO_CYC a
				left join RPT_CARDS_GWO_BAL b on b.MIS_DATE=a.MIS_DATE and a.pd=b.pd and b.CYCLE=a.CYCLE and b.PD_RESULT='A_LOSS'
				left join RPT_CARDS_GWO_BAL c on c.MIS_DATE=a.MIS_DATE and a.pd=c.pd and c.CYCLE=a.CYCLE and c.PD_RESULT='B_LOSS'
				where a.pd=@PD and a.mis_date=@V_MIS_DATE and a.PD_RESULT='GWO_P'
				order by cycle
				SET @intLoop = @intLoop + 1
			END
			SET @STEP = '80'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_GWO_sp','CARDS GWO資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', CARDS,GNE_WO及CP030D尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_GWO_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

