ALTER PROCEDURE [dbo].[TRANS_REPORT_CARDS_LEADING_sp]
	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	DECLARE @V_MISDATE_FIRSTDAY VARCHAR(10)
	SET @V_MISDATE_FIRSTDAY = LEFT(@V_MIS_DATE,8) + '01'
	DECLARE @V_DATE_BEF VARCHAR(10)
	SET @V_DATE_BEF = CONVERT(VARCHAR,DATEADD(MS,-3,DATEADD(MM, DATEDIFF(M,0,@D_MIS_DATE), 0)) ,23)
	DECLARE @D_DATE_BEF DATE
	SET @D_DATE_BEF = CAST(@V_DATE_BEF AS DATE)
	DECLARE @V_MON_E VARCHAR(10)
	SET @V_MON_E = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE)+1, 0)), 23)
	DECLARE @D_MON_E DATE
	SET @D_MON_E = CAST(@V_MON_E AS DATE)
	BEGIN TRY
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		DECLARE @YYYYMMDD_BEF VARCHAR(8)
		SET @YYYYMMDD_BEF = Convert(varchar,@D_DATE_BEF,112)
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'BC',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'BC',@YYYYMMDD_BEF, @OUTPUT2 output
		DECLARE @OUTPUT3 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'CARDS',@YYYYMMDD, @OUTPUT3 output
		PRINT @OUTPUT1
		PRINT @OUTPUT2
		PRINT @OUTPUT3
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S' and left(@OUTPUT3,1) = 'S')-- and left(@OUTPUT4,1) = 'S')
		BEGIN
			SET @STEP = '4'
			DELETE FROM LOG_AUTOMATION WHERE MIS_DATE=@V_MIS_DATE AND TABLE_NAME='TRANS_REPORT_CARDS_LEADING_sp'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_LEADING_sp','CARDS LEADING資料處理開始','N'
			DELETE FROM RPT_CARDS_LEADING_CYC WHERE MIS_DATE=@V_MIS_DATE
			DECLARE @BC VARCHAR(50)
			SET @BC = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @BC_PRE VARCHAR(50)
			SET @BC_PRE = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			DECLARE @CARDS VARCHAR(50)
			SET @CARDS = RIGHT(@OUTPUT3,LEN(@OUTPUT3)-2)
			PRINT @BC
			PRINT @BC_PRE
			PRINT @CARDS
			if object_id('tempdb..#BC_PRE') is not null drop table #BC_PRE
			Select * Into #BC_PRE From BC Where 1> 2
			SET @SQLCmd = N'INSERT INTO #BC_PRE SELECT * FROM ' + @BC_PRE-- + ' WHERE MIS_DATE BETWEEN ''' + @V_MISDATE_FIRSTDAY + ''' AND ''' + @V_DATE_BEF + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#BC') is not null drop table #BC
			Select * Into #BC From BC Where 1> 2
			SET @SQLCmd = N'INSERT INTO #BC SELECT * FROM ' + @BC + ' WHERE MIS_DATE BETWEEN ''' + @V_MISDATE_FIRSTDAY + ''' AND ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			if object_id('tempdb..#CARDS') is not null drop table #CARDS
			Select * Into #CARDS From CARDS Where 1> 2
			SET @SQLCmd = N'INSERT INTO #CARDS SELECT * FROM ' + @CARDS + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '5'
			if object_id('tempdb..#WAS_A1') is not null drop table #WAS_A1
			SELECT PRODTYPE, BUSINESS, CYCLEID, CARDNMBR, CDC, BALANCE
			INTO #WAS_A1 FROM #BC_PRE WHERE 1>2
			ALTER TABLE #WAS_A1 ADD FW_AC INT default 0
			ALTER TABLE #WAS_A1 ADD FW_BAL NUMERIC(15,2) default 0
			ALTER TABLE #WAS_A1 ADD ST_AC INT default 0
			ALTER TABLE #WAS_A1 ADD ST_BAL NUMERIC(15,2) default 0
			INSERT INTO #WAS_A1
			SELECT PRODTYPE, BUSINESS, CYCLEID, CARDNMBR, CDC, BALANCE, 0,0,0,0
			FROM #BC_PRE WHERE CDC>0 AND WOAMT=0 
			AND CARDNMBR NOT IN (SELECT CARDNMBR FROM #BC_PRE WHERE CDC>0 AND WOAMT=0 GROUP BY CARDNMBR HAVING COUNT(*)>1)	--先排掉重複的CARDNMBR
			ORDER BY CARDNMBR, CDC desc
			INSERT INTO #WAS_A1
			Select PRODTYPE, BUSINESS, CYCLEID, CARDNMBR, CDC, BALANCE, 0,0,0,0
			from #BC_PRE 
			Where CDC>0 AND WOAMT=0 
			AND CARDNMBR+CONVERT(VARCHAR,MIS_DATE,112) IN (SELECT CARDNMBR+CONVERT(VARCHAR,MAX(MIS_DATE),112) FROM #BC_PRE WHERE CDC>0 AND WOAMT=0 GROUP BY CARDNMBR HAVING COUNT(*)>1)
			ORDER BY CARDNMBR, CDC desc
			SET @STEP = '6'
			if object_id('tempdb..#IS_A1') is not null drop table #IS_A1
			SELECT CARDNMBR,CDC 
			INTO #IS_A1
			FROM #BC 
			WHERE CARDNMBR NOT IN (SELECT CARDNMBR FROM #BC GROUP BY CARDNMBR HAVING COUNT(*)>1)	--先排掉重複的CARDNMBR
			ORDER BY CARDNMBR,CDC DESC 
			INSERT INTO #IS_A1
			Select CARDNMBR,CDC 
			from #BC
			Where CARDNMBR+CONVERT(VARCHAR,MIS_DATE,112) IN (SELECT CARDNMBR+CONVERT(VARCHAR,MAX(MIS_DATE),112) FROM #BC GROUP BY CARDNMBR HAVING COUNT(*)>1)
			ORDER BY CARDNMBR,CDC DESC 
			SET @STEP = '7'
			DECLARE @DAY2 INT
			IF DAY(DATEADD(dd,1,@D_MIS_DATE))=1
			BEGIN
				SET @DAY2 = 31
			END
			ELSE
			BEGIN
				SET @DAY2 = DAY(@D_MIS_DATE)
			END
			if object_id('tempdb..#LE_A1') is not null drop table #LE_A1
			SELECT A.*,B.CDC AS IS_CDC 
			INTO #LE_A1
			FROM #WAS_A1 A 
			LEFT JOIN #IS_A1 B ON A.CARDNMBR=B.CARDNMBR 
			WHERE A.CYCLEID <= @DAY2 AND A.CDC BETWEEN 1 AND 6 
			UNION
			SELECT A.*,B.CDC AS IS_CDC 
			FROM #WAS_A1 A 
			LEFT JOIN #CARDS B ON A.CARDNMBR=B.CARDNMBR 
			WHERE A.CYCLEID > @DAY2 
			SET @STEP = '8'
			UPDATE #LE_A1 SET IS_CDC=0 WHERE IS_CDC is null
			UPDATE #LE_A1 SET BUSINESS='C230' WHERE PRODTYPE='PCL'
			UPDATE #LE_A1 SET BUSINESS='PIL' WHERE left(CARDNMBR,6)='516888'
			UPDATE #LE_A1 Set FW_AC=1, FW_BAL=BALANCE Where CDC<IS_CDC AND CYCLEID<=@DAY2
			UPDATE #LE_A1 Set FW_AC=1, FW_BAL=BALANCE Where CDC=IS_CDC AND CYCLEID>@DAY2
			UPDATE #LE_A1 SET ST_AC=1, ST_BAL=BALANCE Where IS_CDC=CDC AND CYCLEID<=@DAY2
			UPDATE #LE_A1 SET ST_AC=1, ST_BAL=BALANCE Where IS_CDC=CDC-1 AND CYCLEID> @DAY2
			/*
			REPL FW_AC WITH 1, FW_BAL WITH BALANCE FOR CDC<IS_CDC AND VAL(CYCLEID)<=DAY2
			REPL FW_AC WITH 1, FW_BAL WITH BALANCE FOR CDC=IS_CDC AND VAL(CYCLEID)>DAY2
			REPL ST_AC WITH 1, ST_BAL WITH BALANCE FOR IS_CDC=CDC AND VAL(CYCLEID)<=DAY2
			REPL ST_AC WITH 1, ST_BAL WITH BALANCE FOR IS_CDC=CDC-1 AND VAL(CYCLEID)>DAY2
			*/
			SET @STEP = '9'
			if object_id('tempdb..#LE_A2') is not null drop table #LE_A2
			SELECT BUSINESS,CDC,CYCLEID, COUNT(*) AS WAS_AC, SUM(BALANCE)/1000 AS WAS_BAL, SUM(FW_AC) AS FW_AC, SUM(FW_BAL)/1000 AS FW_BAL, SUM(ST_AC) AS ST_AC,SUM(ST_BAL)/1000 AS ST_BAL
			INTO #LE_A2
			FROM #LE_A1 
			GROUP BY BUSINESS, CDC, CYCLEID 
			ORDER BY BUSINESS, CDC, CYCLEID
			SET @STEP = '10'
			if object_id('tempdb..#LE_A3') is not null drop table #LE_A3
			Select CASE RIGHT(dbo.ALLTRIM(BUSINESS),2) WHEN 'TL' THEN 'TT' WHEN 'AC' THEN 'TT' WHEN 'DC' THEN 'TT' WHEN 'CT' THEN 'TT'  WHEN 'AW' THEN 'TT' ELSE 'CL' END PD,
				BUSINESS,CDC,CYCLEID,WAS_AC,WAS_BAL,FW_AC,FW_BAL,ST_AC,ST_BAL
			Into #LE_A3 
			From #LE_A2
			WHERE RIGHT(dbo.ALLTRIM(BUSINESS),2) IN ('30','TL','DC','AC','CT','AW')
			/*
			if object_id('tempdb..#LE_A11') is not null drop table #LE_A11
			SELECT A.*,B.CDC AS IS_CDC 
			into #LE_A11
			FROM #WAS_A1 A 
			LEFT JOIN #IS_A1 B ON A.CARDNMBR=B.CARDNMBR 
			WHERE A.CYCLEID=1 AND A.CDC=1  and a.business='NACT'
			UNION
			SELECT A.*,B.CDC AS IS_CDC 
			FROM #WAS_A1 A 
			LEFT JOIN #CARDS B ON A.CARDNMBR=B.CARDNMBR 
			WHERE A.CYCLEID=1 and a.business='NACT'
			UPDATE #LE_A11 SET IS_CDC=0 WHERE IS_CDC is null
			UPDATE #LE_A11 SET BUSINESS='C230' WHERE PRODTYPE='PCL'
			UPDATE #LE_A11 SET BUSINESS='PIL' WHERE left(CARDNMBR,6)='516888'
			UPDATE #LE_A11 Set FW_AC=1, FW_BAL=BALANCE Where CDC<IS_CDC AND CYCLEID<=13
			UPDATE #LE_A11 Set FW_AC=1, FW_BAL=BALANCE Where CDC=IS_CDC AND CYCLEID>13
			UPDATE #LE_A11 SET ST_AC=1, ST_BAL=BALANCE Where IS_CDC=CDC AND CYCLEID<=13
			UPDATE #LE_A11 SET ST_AC=1, ST_BAL=BALANCE Where IS_CDC=CDC-1 AND CYCLEID> 13
			select  * from #LE_A1 where business='NACT' and cdc=1 and cycleid=1
			SELECT pd,business,cdc, CYCLEID, WAS_AC,WAS_BAL,FW_AC,FW_BAL,ST_AC,ST_BAL
			FROM #LE_A3
			WHERE PD = 'TT' and CDC=1 and cycleid=1
			order by cycleid
			SELECT pd,cdc, CYCLEID, sum(WAS_AC),sum(WAS_BAL),sum(FW_AC),sum(FW_BAL),sum(ST_AC),sum(ST_BAL)
			FROM #LE_A3
			WHERE PD = 'TT' and CDC=1 and cycleid=1
			group by pd,cdc,CYCLEID
			order by cycleid
			*/
			DECLARE @PD VARCHAR(5)
			DECLARE CURSOR_TEMP CURSOR FAST_FORWARD FOR
				SELECT PD FROM #LE_A3 GROUP BY PD
			OPEN CURSOR_TEMP
			FETCH NEXT FROM CURSOR_TEMP INTO @PD
			WHILE @@FETCH_STATUS = 0
			BEGIN
				DECLARE @intLoop INT
				SET @intLoop = 1
				WHILE (@intLoop <= 6)
				BEGIN					
					SET @STEP = '11'
					INSERT INTO RPT_CARDS_LEADING_CYC
					SELECT @V_MIS_DATE, PD, CDC, CYCLEID, SUM(WAS_AC),SUM(WAS_BAL),SUM(FW_AC),SUM(FW_BAL),SUM(ST_AC),SUM(ST_BAL)
					FROM #LE_A3
					WHERE PD = @PD and CDC=@intLoop
					GROUP BY PD, CDC, CYCLEID
					Set @intLoop = @intLoop + 1
				END
				FETCH NEXT FROM CURSOR_TEMP INTO @PD
			END
			CLOSE CURSOR_TEMP
			DEALLOCATE CURSOR_TEMP
			SET @STEP = '70'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_LEADING_sp','CARDS LEADING資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = ''
			IF (left(@OUTPUT1,1)='F')
			BEGIN
				SET @OUTPUT = @OUTPUT + ',BC'
			END
			IF (left(@OUTPUT2,1)='F')
			BEGIN
				SET @OUTPUT = @OUTPUT + ',' + @YYYYMMDD_BEF + ' BC'
			END
			IF (left(@OUTPUT3,1)='F')
			BEGIN
				SET @OUTPUT = @OUTPUT + ',CARDS_DBF'
			END
			IF (LEN(@OUTPUT) >0)
			BEGIN
				SET @OUTPUT = SUBSTRING(@OUTPUT,2,LEN(@OUTPUT)-1)
			END
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', ' + @OUTPUT + '尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_CARDS_LEADING_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

