ALTER PROCEDURE [dbo].[TRANS_REPORT_DAILY_NCNP_sp]
	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @SQLCmd NVARCHAR(4000)
	DECLARE @D_MIS_DATE DATE
	SET @D_MIS_DATE = CAST(@V_MIS_DATE AS DATE)
	DECLARE @V_MISDATE_FIRSTDAY VARCHAR(10)
	SET @V_MISDATE_FIRSTDAY = LEFT(@V_MIS_DATE,8) + '01'
	DECLARE @V_MON_E VARCHAR(10)
	SET @V_MON_E = CONVERT(VARCHAR,DATEADD(ms,-3,DATEADD(mm, DATEDIFF(m,0,@D_MIS_DATE)+1, 0)), 23)
	DECLARE @D_MON_E DATE
	SET @D_MON_E = CAST(@V_MON_E AS DATE)
	BEGIN TRY
		SET @STEP = '1'
		DECLARE @YYYYMMDD VARCHAR(8)
		SET @YYYYMMDD = Convert(varchar,@D_MIS_DATE,112)
		SET @STEP = '2'
		DECLARE @OUTPUT1 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'BUDTRAN',@YYYYMMDD, @OUTPUT1 output
		DECLARE @OUTPUT2 VARCHAR(50)
		EXEC dbo.SP_GetTableName 'BC',@YYYYMMDD, @OUTPUT2 output
		PRINT @OUTPUT1
		PRINT @OUTPUT2
		SET @STEP = '3'
		IF (left(@OUTPUT1,1) = 'S' and left(@OUTPUT2,1) = 'S')
		BEGIN
			SET @STEP = '4'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_DAILY_NCNP_sp','DAILY NCNP資料處理開始','N'
			DELETE FROM RPT_DAILY_NCNP WHERE MIS_DATE=@V_MIS_DATE
			DECLARE @BUDTRAN VARCHAR(50)
			SET @BUDTRAN = RIGHT(@OUTPUT1,LEN(@OUTPUT1)-2)
			DECLARE @BC VARCHAR(50)
			SET @BC = RIGHT(@OUTPUT2,LEN(@OUTPUT2)-2)
			PRINT @BUDTRAN
			PRINT @BC
			SET @STEP = '5'
			/* 
			===== ALS =====
			*/
			if object_id('tempdb..#BUDTRAN') is not null drop table #BUDTRAN
			Select * Into #BUDTRAN From BUDTRAN Where 1> 2
			SET @SQLCmd = N'INSERT INTO #BUDTRAN SELECT * FROM ' + @BUDTRAN + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '6'
			if object_id('tempdb..#DEL1') is not null drop table #DEL1
			SELECT *
			INTO #DEL1 
			FROM #BUDTRAN 
			WHERE PDF>1 AND PRINCIPAL>0 AND PRINCIPAL-RESERVE>0 AND LOCATION<>'860109'
			SET @STEP = '7'
			if object_id('tempdb..#DEL2') is not null drop table #DEL2
			SELECT * 
			INTO #DEL2
			FROM #DEL1 WHERE SUBSTRING(CACS_STATE,1,1)='T' AND CAST(SUBSTRING(CACS_STATE,2,2) AS INT)>=50
			SET @STEP = '8'
			UPDATE #DEL1 SET PDF=7 WHERE PDF>7
			SET @STEP = '9'
			if object_id('tempdb..#TEMPNCNP001') is not null drop table #TEMPNCNP001
			SELECT * 
			INTO #TEMPNCNP001
			FROM #DEL1 WHERE CACS_STATE<>'T11' AND SUBSTRING(CACS_STATE,1,1) NOT IN ('S','R','Q','V','G','N','Y') AND CARDNMBR NOT IN (SELECT CARDNMBR FROM #DEL2)
			SET @STEP = '10'
			if object_id('tempdb..#NA_TEMPNCNP001') is not null drop table #NA_TEMPNCNP001
			SELECT * INTO #NA_TEMPNCNP001 FROM #TEMPNCNP001 WHERE BUSINESS='NA'
			if object_id('tempdb..#BOOC_TEMPNCNP001') is not null drop table #BOOC_TEMPNCNP001
			SELECT * INTO #BOOC_TEMPNCNP001 FROM #TEMPNCNP001 WHERE BUSINESS<>'NA'
			SET @STEP = '11'
			TRUNCATE TABLE #DEL2
			INSERT INTO #DEL2
			SELECT * FROM #TEMPNCNP001 WHERE isnull(DATE_CNTCT,'')='' AND isnull(DATE_PMT,'')=''			
			UNION
			SELECT * FROM #TEMPNCNP001 WHERE isnull(DATE_CNTCT,'')='' AND DATEDIFF(DD,DATE_PMT,@D_MON_E)>30 
			UNION
			SELECT * FROM #TEMPNCNP001 WHERE DATEDIFF(DD,DATE_CNTCT,@D_MON_E)>30 AND isnull(DATE_PMT,'')=''
			UNION
			SELECT * FROM #TEMPNCNP001 WHERE DATEDIFF(DD,DATE_CNTCT,@D_MON_E)>30 AND DATEDIFF(DD,DATE_PMT,@D_MON_E)>30 
			SET @STEP = '12'
			if object_id('tempdb..#TEMPNCNP002') is not null drop table #TEMPNCNP002
			SELECT * INTO #TEMPNCNP002 FROM #DEL2 --GROUP BY CARDNMBR			
			if object_id('tempdb..#NA_TEMPNCNP002') is not null drop table #NA_TEMPNCNP002
			SELECT * INTO #NA_TEMPNCNP002 FROM #TEMPNCNP002 WHERE BUSINESS='NA'
			if object_id('tempdb..#BOOC_TEMPNCNP002') is not null drop table #BOOC_TEMPNCNP002
			SELECT * INTO #BOOC_TEMPNCNP002 FROM #TEMPNCNP002 WHERE BUSINESS<>'NA'
			/* 
			===== ECS =====
			*/
			SET @STEP = '20'
			if object_id('tempdb..#BC') is not null drop table #BC
			Select * Into #BC From BC Where 1> 2
			SET @SQLCmd = N'INSERT INTO #BC SELECT * FROM ' + @BC + ' WHERE MIS_DATE = ''' + @V_MIS_DATE + ''' '
			PRINT @SQLCmd
			EXECUTE sp_executesql @SQLCmd
			SET @STEP = '21'
			if object_id('tempdb..#BC_DEL1') is not null drop table #BC_DEL1
			SELECT convert(varchar, DATEAS, 111) AS CYCDATE, *
			INTO #BC_DEL1
			FROM #BC WHERE (CDC>1 AND CDC<=6) AND WOAMT=0 AND LOCATION<>'020109'
			SET @STEP = '22'
			if object_id('tempdb..#BC_DEL2') is not null drop table #BC_DEL2
			SELECT * 
			INTO #BC_DEL2
			FROM #BC_DEL1 WHERE CACS_STATE <> 'T11' AND SUBSTRING(CACS_STATE,2,2) NOT IN ('02','62') 
			AND (LEFT(CACS_STATE,1) NOT IN ('G','N','Y','S') OR CACS_STATE IN ('G30','G31','G32','G34','G35'))
			SET @STEP = '23'
			if object_id('tempdb..#BC_DEL3') is not null drop table #BC_DEL3
			SELECT * 
			INTO #BC_DEL3
			FROM #BC_DEL1 WHERE LEFT(CACS_STATE,1)='T' AND CAST(SUBSTRING(CACS_STATE,2,2) AS INT) >= 50
			SET @STEP = '24'
			if object_id('tempdb..#BC_DEL4') is not null drop table #BC_DEL4
			SELECT * 
			INTO #BC_DEL4
			FROM #BC_DEL2
			WHERE CARDNMBR NOT IN (SELECT CARDNMBR FROM #BC_DEL3)
			SET @STEP = '25'
			if object_id('tempdb..#BC_TEMPNCNP001') is not null drop table #BC_TEMPNCNP001
			SELECT * 
			INTO #BC_TEMPNCNP001
			FROM #BC_DEL4 --GROUP BY CARDNMBR
			SET @STEP = '26'
			SET @STEP = '27'
			if object_id('tempdb..#BC_NA_TEMPNCNP001') is not null drop table #BC_NA_TEMPNCNP001
			SELECT * 
			INTO #BC_NA_TEMPNCNP001
			FROM #BC_TEMPNCNP001 WHERE BUSINESS<>'CTL'
			SET @STEP = '28'
			if object_id('tempdb..#BC_BOOC_TEMPNCNP001') is not null drop table #BC_BOOC_TEMPNCNP001
			SELECT * 
			INTO #BC_BOOC_TEMPNCNP001
			FROM #BC_TEMPNCNP001 WHERE BUSINESS='CTL'
			SET @STEP = '29'
			if object_id('tempdb..#BC_DEL5') is not null drop table #BC_DEL5
			SELECT * 
			INTO #BC_DEL5
			FROM #BC_TEMPNCNP001 WHERE isnull(DATE_CNTCT,'')='' AND isnull(DATE_PMT,'')=''
			UNION
			SELECT * FROM #BC_TEMPNCNP001 WHERE isnull(DATE_CNTCT,'')='' AND DATEDIFF(DD,DATE_PMT,CYCDATE)>30
			UNION
			SELECT * FROM #BC_TEMPNCNP001 WHERE DATEDIFF(DD,DATE_CNTCT,CYCDATE)>30 AND isnull(DATE_PMT,'')=''
			UNION
			SELECT * FROM #BC_TEMPNCNP001 WHERE DATEDIFF(DD,DATE_CNTCT,CYCDATE)>30 AND DATEDIFF(DD,DATE_PMT,CYCDATE)>30
			SET @STEP = '30'
			if object_id('tempdb..#BC_TEMPNCNP002') is not null drop table #BC_TEMPNCNP002
			SELECT *
			INTO #BC_TEMPNCNP002
			FROM #BC_DEL5 --GROUP BY CARDNMBR
			if object_id('tempdb..#BC_NA_TEMPNCNP002') is not null drop table #BC_NA_TEMPNCNP002
			if object_id('tempdb..#BC_BOOC_TEMPNCNP002') is not null drop table #BC_BOOC_TEMPNCNP002
			SELECT * INTO #BC_NA_TEMPNCNP002 FROM #BC_TEMPNCNP002 WHERE BUSINESS<>'CTL' 
			SELECT * INTO #BC_BOOC_TEMPNCNP002 FROM #BC_TEMPNCNP002 WHERE BUSINESS='CTL' 
			SET @STEP = '31'
			DECLARE @INTLOOP_PDTYPE INT
			DECLARE @PDTYPE VARCHAR(3)
			SET @INTLOOP_PDTYPE = 1
			WHILE (@INTLOOP_PDTYPE <= 2)
			BEGIN
				SET @STEP = '32'
				IF (@INTLOOP_PDTYPE=1)
				BEGIN
					SET @PDTYPE='ALS'
				END
				ELSE
				BEGIN
					SET @PDTYPE='ECS'
				END
				DECLARE @INTLOOP_PD INT
				DECLARE @PD VARCHAR(8)
				DECLARE @PD_ITEM VARCHAR(8)
				SET @INTLOOP_PD = 1
				WHILE (@INTLOOP_PD <= 2)
				BEGIN
					SET @STEP = '33'
					IF (@INTLOOP_PD=1)
					BEGIN
						SET @PD = @PDTYPE
					END
					ELSE
					BEGIN
						SET @PD = @PDTYPE + '_BOOC'
					END
					DECLARE @INTLOOP_PD_ITEM INT
					SET @INTLOOP_PD_ITEM = 1
					WHILE (@INTLOOP_PD_ITEM <= 2)
					BEGIN
						SET @STEP = '34'
						IF (@INTLOOP_PD_ITEM=1)
						BEGIN
							SET @PD_ITEM='TTL_ACCT'
						END
						ELSE
						BEGIN
							SET @PD_ITEM='NCNP'
						END
						if object_id('tempdb..#TEMPNCNP_ALS') is not null drop table #TEMPNCNP_ALS
						if object_id('tempdb..#TEMPNCNP_ECS') is not null drop table #TEMPNCNP_ECS
						DECLARE @CYCLEID VARCHAR(3)
						DECLARE @ACCTS INT
						DECLARE @CDC INT
						DECLARE @intLoop INT
						IF (@PDTYPE='ALS')
						BEGIN
							SET @STEP = '35'
							Select * Into #TEMPNCNP_ALS From #TEMPNCNP001 Where 1> 2
							IF (@PD='ALS' AND @PD_ITEM='TTL_ACCT')
							BEGIN
								INSERT iNTO #TEMPNCNP_ALS Select * From #NA_TEMPNCNP001
							END
							IF (@PD='ALS' AND @PD_ITEM='NCNP')
							BEGIN						
								INSERT iNTO #TEMPNCNP_ALS Select * From #NA_TEMPNCNP002
							END
							IF (@PD='ALS_BOOC' AND @PD_ITEM='TTL_ACCT')
							BEGIN
								INSERT iNTO #TEMPNCNP_ALS Select * FROM #BOOC_TEMPNCNP001
							END
							IF (@PD='ALS_BOOC' AND @PD_ITEM='NCNP')
							BEGIN
								INSERT iNTO #TEMPNCNP_ALS Select * FROM #BOOC_TEMPNCNP002
							END
							SET @intLoop = 2
							WHILE (@intLoop <= 6)
							BEGIN
								SET @STEP = '36'
								INSERT INTO RPT_DAILY_NCNP(MIS_DATE,PD,PD_RESULT,CDC) VALUES(@V_MIS_DATE,@PD,@PD_ITEM,@intLoop)
								DECLARE CURSOR_TEMP CURSOR FAST_FORWARD FOR
									SELECT 'D'+dbo.ALLTRIM(CYCLEID) AS CYCLEID, PDF AS CDC, COUNT(*) AS NO_ACCTS FROM #TEMPNCNP_ALS WHERE PDF=@intLoop GROUP BY CYCLEID, PDF 
									ORDER BY CAST(CYCLEID AS INT)
								OPEN CURSOR_TEMP
								FETCH NEXT FROM CURSOR_TEMP INTO @CYCLEID, @CDC, @ACCTS
								WHILE @@FETCH_STATUS = 0
								BEGIN
									SET @SQLCmd = N'UPDATE RPT_DAILY_NCNP SET ' + @CYCLEID + '=' + CAST(@ACCTS AS VARCHAR) + ' WHERE MIS_DATE=''' + @V_MIS_DATE + ''' AND CDC=' + CAST(@CDC AS VARCHAR) + ' AND PD=''' + @PD + ''' AND PD_RESULT=''' + @PD_ITEM + ''' '
									EXECUTE sp_executesql @SQLCmd
									FETCH NEXT FROM CURSOR_TEMP INTO @CYCLEID, @CDC, @ACCTS
								END
								CLOSE CURSOR_TEMP
								DEALLOCATE CURSOR_TEMP
								SET @intLoop = @intLoop + 1
							END
						END
						SET @STEP = '37'
						IF (@PDTYPE='ECS')
						BEGIN
							SET @STEP = '38'
							Select * Into #TEMPNCNP_ECS From #BC_TEMPNCNP001 Where 1> 2
							IF (@PD='ECS' AND @PD_ITEM='TTL_ACCT')
							BEGIN
								INSERT iNTO #TEMPNCNP_ECS Select * From #BC_NA_TEMPNCNP001
							END
							IF (@PD='ECS' AND @PD_ITEM='NCNP')
							BEGIN						
								INSERT iNTO #TEMPNCNP_ECS Select * From #BC_NA_TEMPNCNP002
							END
							IF (@PD='ECS_BOOC' AND @PD_ITEM='TTL_ACCT')
							BEGIN
								INSERT iNTO #TEMPNCNP_ECS Select * FROM #BC_BOOC_TEMPNCNP001
							END
							IF (@PD='ECS_BOOC' AND @PD_ITEM='NCNP')
							BEGIN
								INSERT iNTO #TEMPNCNP_ECS Select * FROM #BC_BOOC_TEMPNCNP002
							END
							SET @intLoop = 2
							WHILE (@intLoop <= 6)
							BEGIN
								SET @STEP = '39'
								DECLARE @CNT INT
								Select @CNT = Count(*) FROM RPT_DAILY_NCNP WHERE MIS_DATE=DATEADD(DD,-1,@D_MIS_DATE) AND PD=@PD AND PD_RESULT=@PD_ITEM AND CDC=@intLoop
								IF (@CNT>0 AND DAY(@D_MIS_DATE) <> 1)
								BEGIN
									INSERT INTO RPT_DAILY_NCNP
									SELECT @V_MIS_DATE,PD,PD_RESULT,CDC,D1,D2,D3,D4,D5,D6,D7,D8,D9,D10,D11,D12,D13,D14,D15,D16,D17,D18,D19,D20,D21,D22,D23,D24,D25,D26,D27,D28,D29,D30,D31								
									FROM RPT_DAILY_NCNP WHERE MIS_DATE=DATEADD(DD,-1,@D_MIS_DATE) AND PD=@PD AND PD_RESULT=@PD_ITEM AND CDC=@intLoop
								END
								ELSE
								BEGIN
									INSERT INTO RPT_DAILY_NCNP(MIS_DATE,PD,PD_RESULT,CDC) VALUES(@V_MIS_DATE,@PD,@PD_ITEM,@intLoop)
								END
								DECLARE CURSOR_TEMP_ECS CURSOR FAST_FORWARD FOR
									SELECT 'D'+CAST(DAY(@D_MIS_DATE) AS VARCHAR) AS CYCLEID, CDC, COUNT(*) AS NO_ACCTS FROM #TEMPNCNP_ECS WHERE CDC=@intLoop GROUP BY CYCLEID, CDC 
									ORDER BY CAST(CYCLEID AS INT)
								OPEN CURSOR_TEMP_ECS
								FETCH NEXT FROM CURSOR_TEMP_ECS INTO @CYCLEID, @CDC, @ACCTS
								WHILE @@FETCH_STATUS = 0
								BEGIN
									SET @SQLCmd = N'UPDATE RPT_DAILY_NCNP SET ' + @CYCLEID + '=' + CAST(@ACCTS AS VARCHAR) + ' WHERE MIS_DATE=''' + @V_MIS_DATE + ''' AND CDC=' + CAST(@CDC AS VARCHAR) + ' AND PD=''' + @PD + ''' AND PD_RESULT=''' + @PD_ITEM + ''' '	
									PRINT @SQLCmd
									EXECUTE sp_executesql @SQLCmd
									FETCH NEXT FROM CURSOR_TEMP_ECS INTO @CYCLEID, @CDC, @ACCTS
								END
								CLOSE CURSOR_TEMP_ECS
								DEALLOCATE CURSOR_TEMP_ECS
								SET @STEP = '40'
								SET @intLoop = @intLoop + 1
							END
						END
						SET @STEP = '41'
						SET @INTLOOP_PD_ITEM = @INTLOOP_PD_ITEM + 1
					END	
					SET @STEP = '42'
					SET @INTLOOP_PD = @INTLOOP_PD + 1
				END
				SET @STEP = '43'
				SET @INTLOOP_PDTYPE = @INTLOOP_PDTYPE + 1
			END
			SET @STEP = '50'
			EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_DAILY_NCNP_sp','DAILY NCNP資料處理完成','N'
			SET @OUTPUT = 'S'
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'F|MIS_DATE=' + @V_MIS_DATE + ', BUDTRAN or BC 其一尚未完成'
		END
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'TRANS_REPORT_DAILY_NCNP_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

