ALTER PROCEDURE [dbo].[TRANS_SKIP_sp]
	@DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(2)
	BEGIN TRY
		SET @STEP = '1'
		if object_id('tempdb..#CC1_N') is not null drop table #CC1_N
		Create table #CC1_N
		(
			DATE_CCH	DATE,
			LOCATION	VARCHAR(6),
			CARDNMBR	VARCHAR(16),
			RTE_CCH		VARCHAR(3),
			CDC			NUMERIC(2),
			BALANCE		NUMERIC(14),
			OPENDATE	DATE,
			NONSTART	VARCHAR(1),
			DATE_PMT	DATE,
			PMT_CCH		NUMERIC(14,2),
			LTR_CCH		VARCHAR(12)
		)
		Insert into #CC1_N
		Select distinct DATE_CCH , LOCATION, CARDNMBR, RTE_CCH, 0 AS CDC, 0 AS BALANCE ,null AS OPENDATE, '' AS NONSTART, null AS DATE_PMT, PMT_CCH, LTR_CCH 
		From CCH 
		Where MIS_DATE = @DATE
		And RTE_CCH in (Select BizCodeValue from ReportPara_Code where RptType='SKIP' and BizType='001' and IsValid=1) 
		And DATE_CCH = @DATE
		SET @STEP = '2'
		if object_id('tempdb..#CC2_N') is not null drop table #CC2_N
		Create table #CC2_N
		(
			DATE_CCH	DATE,
			LOCATION	VARCHAR(6),
			CARDNMBR	VARCHAR(16),
			RTE_CCH		VARCHAR(3),
			CDC			NUMERIC(2),
			BALANCE		NUMERIC(14),
			OPENDATE	DATE,
			NONSTART	VARCHAR(1),
			DATE_PMT	DATE,
			PMT_CCH		NUMERIC(14,2),
			LTR_CCH		VARCHAR(12)
		)
		Insert into #CC2_N
		Select distinct DATE_CCH , LOCATION, CARDNMBR, RTE_CCH, 0 AS CDC, 0 AS BALANCE , null AS OPENDATE, '' AS NONSTART, null AS DATE_PMT, PMT_CCH, LTR_CCH 
		From CCH  
		Where MIS_DATE = @DATE
		And RTE_CCH IN (Select BizCodeValue from ReportPara_Code where RptType='SKIP' and BizType='001' and IsValid=1) 
		And DATE_CCH = @DATE
		And substring(LTR_CCH,1,2) = 'S3'
		And LTR_CCH <> 'S3' --!INLIST(SUBSTR(LTR_CCH,1,3),'S3 ') 不要LTR_CCH='S3'
		SET @STEP = '3'
		if object_id('tempdb..#ALS_CHK') is not null drop table #ALS_CHK
		Create table #ALS_CHK
		(
			DATE_CCH	DATE,
			LOCATION	VARCHAR(6),
			CARDNMBR	VARCHAR(16),
			RTE_CCH		VARCHAR(3),
			CDC			NUMERIC(2),
			BALANCE		NUMERIC(14),
			OPENDATE	DATE,
			NONSTART	VARCHAR(1),
			DATE_PMT	DATE,
			PMT_CCH		NUMERIC(14,2),
			LTR_CCH		VARCHAR(12)
		)
		Insert into #ALS_CHK
		Select distinct @DATE, LOCATION, CARDNMBR, CACS_STATE AS RTE_CCH, 0 AS CDC, 0 AS BALANCE ,null AS OPENDATE, '' AS NONSTART, null AS DATE_PMT
		, IIF(DATE_PMT=@DATE,LAST_PMT,0) AS PMT_CCH
		, '' AS LTR_CCH
		From BUDTRAN 
		Where MIS_DATE = @DATE
		And DATE_STATE = @DATE
		And CACS_STATE IN (Select BizCodeValue from ReportPara_Code where RptType='SKIP' and BizType='001' and IsValid=1)
		SET @STEP = '4'
		Insert into #CC1_N
		SELECT * 
		FROM #ALS_CHK 
		WHERE CARDNMBR+RTE_CCH NOT IN (select CARDNMBR+RTE_CCH FROM #CC1_N)
		SET @STEP = '5'
		UPDATE #CC1_N 
			SET #CC1_N.LTR_CCH = #CC2_N.LTR_CCH 
		From #CC2_N 
		WHERE #CC2_N.CARDNMBR = #CC1_N.CARDNMBR
		AND #CC2_N.RTE_CCH = #CC1_N.RTE_CCH
		SET @STEP = '6'
		UPDATE #CC1_N 
			SET #CC1_N.OPENDATE = CARDS.DATEOPEN,
				#CC1_N.CDC = CARDS.CDC,
				#CC1_N.BALANCE = CARDS.BALANCE,
				#CC1_N.DATE_PMT = CARDS.DATE_PMT
		FROM CARDS
		WHERE CARDS.CARDNMBR = #CC1_N.CARDNMBR
		AND CARDS.MIS_DATE = @DATE
		SET @STEP = '7'
		UPDATE #CC1_N 
			SET #CC1_N.OPENDATE = BUDTRAN.OPENDATE,
				#CC1_N.CDC = BUDTRAN.CDC,
				#CC1_N.BALANCE = BUDTRAN.BALANCE,
				#CC1_N.DATE_PMT = BUDTRAN.DATE_PMT
		FROM BUDTRAN
		WHERE BUDTRAN.CARDNMBR = #CC1_N.CARDNMBR
		AND BUDTRAN.MIS_DATE = @DATE
		SET @STEP = '8'
		UPDATE #CC1_N SET NONSTART='Y' WHERE DATE_PMT is null
		SET @STEP = '9'
		DECLARE @CARDNMBR_O VARCHAR(16)
		DECLARE @CARDNMBR VARCHAR(16)
		DECLARE CURSOR_ACCT_TR CURSOR FAST_FORWARD FOR
		  SELECT CARDNMBR_O, CARDNMBR
		  FROM ACCT_TR
		  WHERE MIS_DATE <= @DATE
		OPEN CURSOR_ACCT_TR
		FETCH NEXT FROM CURSOR_ACCT_TR INTO @CARDNMBR_O, @CARDNMBR
		WHILE @@FETCH_STATUS = 0
		BEGIN
			UPDATE SKIP 
				SET OLD_AC = @CARDNMBR_O,
					CARDNMBR = @CARDNMBR
			WHERE CARDNMBR = @CARDNMBR_O
			FETCH NEXT FROM CURSOR_ACCT_TR INTO @CARDNMBR_O, @CARDNMBR
		END
		CLOSE CURSOR_ACCT_TR
		DEALLOCATE CURSOR_ACCT_TR
		SET @STEP = '10'
		DECLARE @CNT int
		DECLARE @LOCATION_CCH VARCHAR(6)
		DECLARE @CARDNMBR_CCH VARCHAR(16)
		DECLARE @TOO_DATE_CCH DATE
		DECLARE @TOO_BAL_CCH NUMERIC(14)
		DECLARE @TOO_CDC_CCH NUMERIC(2)
		DECLARE @NONSTART_CCH VARCHAR(1)
		DECLARE @OPENDATE_CCH DATE
		DECLARE @DATE_PMT_CCH DATE
		DECLARE @LTR_CCH_CCH VARCHAR(12)
		DECLARE @RTE_CCH_CCH VARCHAR(3)		
		DECLARE @BizCodeValue VARCHAR(3)
		DECLARE @BizCodeValue_bef VARCHAR(3)
		DECLARE @SQLCmd nvarchar(1000)
		DECLARE @DATE_BEF DATE
		DECLARE @TOO_DATE DATE
		DECLARE CURSOR_CC1_N CURSOR FAST_FORWARD FOR
		  SELECT distinct LOCATION, CARDNMBR, DATE_CCH AS T10_DATE, BALANCE AS T10_BAL, CDC AS T10_CDC, NONSTART, OPENDATE, DATE_PMT, '' AS LTR_CCH 
		  FROM #CC1_N 
		  WHERE RTE_CCH = 'T10'
		OPEN CURSOR_CC1_N
		FETCH NEXT FROM CURSOR_CC1_N INTO @LOCATION_CCH, @CARDNMBR_CCH, @TOO_DATE_CCH, @TOO_BAL_CCH, @TOO_CDC_CCH, @NONSTART_CCH, @OPENDATE_CCH, @DATE_PMT_CCH, @LTR_CCH_CCH
		WHILE @@FETCH_STATUS = 0
		BEGIN
			Select @CNT = Count(*) FROM SKIP WHERE CARDNMBR = @CARDNMBR_CCH
			IF (@CNT > 0)
			BEGIN
				SET @STEP = '10-1'
				Select @CNT = Count(*) FROM SKIP WHERE CARDNMBR = @CARDNMBR_CCH AND END_DATE is not null
				IF (@CNT > 0)
				BEGIN
					SET @STEP = '10-2'
					Insert Into SKIP(LOCATION, CARDNMBR, T10_DATE, T10_BAL, T10_CDC, NONSTART, OPENDATE, DATE_PMT, LTR_CCH)
					values (@LOCATION_CCH, @CARDNMBR_CCH, @TOO_DATE_CCH, @TOO_BAL_CCH, @TOO_CDC_CCH, @NONSTART_CCH, @OPENDATE_CCH, @DATE_PMT_CCH, @LTR_CCH_CCH)
				END
			END
			ELSE
			BEGIN
				SET @STEP = '10-3'
				Insert Into SKIP(LOCATION, CARDNMBR, T10_DATE, T10_BAL, T10_CDC, NONSTART, OPENDATE, DATE_PMT, LTR_CCH)
				values (@LOCATION_CCH, @CARDNMBR_CCH, @TOO_DATE_CCH, @TOO_BAL_CCH, @TOO_CDC_CCH, @NONSTART_CCH, @OPENDATE_CCH, @DATE_PMT_CCH, @LTR_CCH_CCH)
			END
			FETCH NEXT FROM CURSOR_CC1_N INTO @LOCATION_CCH, @CARDNMBR_CCH, @TOO_DATE_CCH, @TOO_BAL_CCH, @TOO_CDC_CCH, @NONSTART_CCH, @OPENDATE_CCH, @DATE_PMT_CCH, @LTR_CCH_CCH
		END
		CLOSE CURSOR_CC1_N
		DEALLOCATE CURSOR_CC1_N
		SET @STEP = '11'
		/*DECLARE @CNT int
		DECLARE @LOCATION_CCH VARCHAR(6)
		DECLARE @CARDNMBR_CCH VARCHAR(16)
		DECLARE @TOO_DATE_CCH DATE
		DECLARE @TOO_BAL_CCH NUMERIC(14)
		DECLARE @TOO_CDC_CCH NUMERIC(2)
		DECLARE @DATE_PMT_CCH DATE
		DECLARE @LTR_CCH_CCH VARCHAR(12)
		DECLARE @RTE_CCH_CCH VARCHAR(3)		
		DECLARE @BizCodeValue VARCHAR(3)
		DECLARE @BizCodeValue_bef VARCHAR(3)
		DECLARE @SQLCmd nvarchar(1000)
		DECLARE @DATE_BEF DATE
		DECLARE @TOO_DATE DATE
		DECLARE @DATE VARCHAR(10)
		SET @DATE = '2018-08-07'*/
		DECLARE CURSOR_SKIPITEM CURSOR FAST_FORWARD FOR
		  Select BizCodeValue from ReportPara_Code where RptType='SKIP' and BizType='001' and IsValid=1 Order By BizCode asc
		OPEN CURSOR_SKIPITEM
		FETCH NEXT FROM CURSOR_SKIPITEM INTO @BizCodeValue
		WHILE @@FETCH_STATUS = 0
		BEGIN
			IF (@BizCodeValue <> 'T10')
			BEGIN
				DECLARE CURSOR_SKIPITEM_CCH CURSOR FAST_FORWARD FOR
				  SELECT LOCATION, CARDNMBR, DATE_CCH AS TOO_DATE, BALANCE AS TOO_BAL, CDC AS TOO_CDC, DATE_PMT, RTE_CCH, LTR_CCH
				  FROM #CC1_N 
				  WHERE RTE_CCH = @BizCodeValue
				OPEN CURSOR_SKIPITEM_CCH
				FETCH NEXT FROM CURSOR_SKIPITEM_CCH INTO @LOCATION_CCH, @CARDNMBR_CCH, @TOO_DATE_CCH, @TOO_BAL_CCH, @TOO_CDC_CCH, @DATE_PMT_CCH, @RTE_CCH_CCH, @LTR_CCH_CCH
				WHILE @@FETCH_STATUS = 0
				BEGIN
					SET @STEP = '11-1'
					SELECT @CNT = COUNT(*) FROM SKIP WHERE CARDNMBR = @CARDNMBR_CCH And (END_DATE is null Or END_DATE = @DATE)
					IF (@CNT > 0)
					BEGIN
						if object_id('tempdb..#VALUE') is not null drop table #VALUE
						Create table #VALUE
						(
							DATE_BEF DATE,
							TOO_DATE DATE
						)
						/*
						DECLARE @SQLCmd nvarchar(1000)
						DECLARE @RTE_CCH nvarchar(3)
						DECLARE @BizCodeValue_bef nvarchar(3)
						DECLARE @CARDNMBR nvarchar(16)
						DECLARE @DATE VARCHAR(10)
						SET @RTE_CCH = 'T20'
						SET @BizCodeValue_bef = 'T10'
						SET @CARDNMBR = '13000100865847'
						SET @DATE='2018-08-06'
						*/
						SET @STEP = '11-2'
						SET @SQLCmd = N'Insert Into #VALUE '
						SET @SQLCmd = @SQLCmd + N'Select ' + @BizCodeValue_bef + '_DATE, ' + @RTE_CCH_CCH + '_DATE From SKIP Where CARDNMBR = ''' + @CARDNMBR_CCH + ''' '
						SET @SQLCmd = @SQLCmd + N'And (END_DATE is null Or END_DATE=''' + @DATE + ''') '
						EXECUTE sp_executesql @SQLCmd
						select @DATE_BEF = DATE_BEF, --前一個TOO ITEM  DATE
							   @TOO_DATE = TOO_DATE  --目前TOO_DATE
						From #VALUE
						SET @STEP = '11-3'
						DECLARE @END_DATE DATE, @T10_DATE DATE, @T31_DATE DATE, @T32_DATE DATE, @T33_DATE DATE
						Select @END_DATE = END_DATE
							 , @T10_DATE = ISNULL(T10_DATE,CAST(@DATE AS DATE))	--因後面要比對，若為null先轉為資料日期
							 , @T31_DATE = ISNULL(T31_DATE,CAST(@DATE AS DATE))	--同上
							 , @T32_DATE = ISNULL(T32_DATE,CAST(@DATE AS DATE))	--同上
							 , @T33_DATE = ISNULL(T33_DATE,CAST(@DATE AS DATE))	--同上
						From SKIP 
						WHERE CARDNMBR = @CARDNMBR_CCH And (END_DATE is null Or END_DATE = @DATE)
						IF (@END_DATE IS NULL OR @END_DATE = CAST(@DATE AS DATE))
						BEGIN
							IF (@RTE_CCH_CCH = 'T31' or @RTE_CCH_CCH = 'T32' or @RTE_CCH_CCH = 'T33')
							BEGIN
								SET @STEP = '11-4'
								IF (CAST(@DATE AS DATE) >= @DATE_BEF AND @TOO_DATE IS NULL)
								BEGIN
									SET @STEP = '11-5'
									SET @SQLCmd = N'UPDATE SKIP SET ' + @RTE_CCH_CCH + '_DATE = ''' + CAST(@TOO_DATE_CCH AS VARCHAR) + ''', ' + @RTE_CCH_CCH + '_CDC = ' + CAST(@TOO_CDC_CCH AS VARCHAR) + ', ' + @RTE_CCH_CCH + '_BAL = ' + CAST(@TOO_BAL_CCH AS VARCHAR) + ''
									SET @SQLCmd = @SQLCmd + N' WHERE CARDNMBR = ' + @CARDNMBR_CCH + ' And (END_DATE is null Or END_DATE = ' + @DATE + ') '
									EXECUTE sp_executesql @SQLCmd
								END
							END
							ELSE
							BEGIN
								SET @STEP = '11-6'
								IF (CAST(@DATE AS DATE) >= @T10_DATE AND CAST(@DATE AS DATE) >= @T31_DATE AND CAST(@DATE AS DATE) >= @T32_DATE AND CAST(@DATE AS DATE) >= @T33_DATE AND @TOO_DATE IS NULL)
								BEGIN
									SET @STEP = '11-7'
									SET @SQLCmd = N'UPDATE SKIP SET ' + @RTE_CCH_CCH + '_DATE = ''' + CAST(@TOO_DATE_CCH AS VARCHAR) + ''', ' + @RTE_CCH_CCH + '_CDC = ' + CAST(@TOO_CDC_CCH AS VARCHAR) + ', ' + @RTE_CCH_CCH + '_BAL = ' + CAST(@TOO_BAL_CCH AS VARCHAR) + ''
									SET @SQLCmd = @SQLCmd + N' WHERE CARDNMBR = ''' + @CARDNMBR_CCH + ''' And (END_DATE is null Or END_DATE = ''' + @DATE + ''') '
									EXECUTE sp_executesql @SQLCmd
								END
							END
							SET @STEP = '11-8'
							IF (@RTE_CCH_CCH = 'T11')
							BEGIN
								UPDATE SKIP SET END_DATE = @TOO_DATE_CCH, RESULT = 'O' WHERE CARDNMBR = @CARDNMBR_CCH And (END_DATE is null Or END_DATE = @DATE)
							END
							IF (@RTE_CCH_CCH = 'T41')
							BEGIN
								UPDATE SKIP SET END_DATE = @TOO_DATE_CCH, RESULT = 'F' WHERE CARDNMBR = @CARDNMBR_CCH And (END_DATE is null Or END_DATE = @DATE)
							END
							IF (@RTE_CCH_CCH = 'T21')
							BEGIN
								UPDATE SKIP SET END_DATE = @TOO_DATE_CCH, RESULT = 'S' WHERE CARDNMBR = @CARDNMBR_CCH And (END_DATE is null Or END_DATE = @DATE)
							END
							IF (SUBSTRING(@LTR_CCH_CCH,1,2) = 'S3')
							BEGIN
								UPDATE SKIP SET LTR_CCH = @LTR_CCH_CCH WHERE CARDNMBR = @CARDNMBR_CCH And (END_DATE is null Or END_DATE = @DATE)
							END
						END
					END
					FETCH NEXT FROM CURSOR_SKIPITEM_CCH INTO @LOCATION_CCH, @CARDNMBR_CCH, @TOO_DATE_CCH, @TOO_BAL_CCH, @TOO_CDC_CCH, @DATE_PMT_CCH, @RTE_CCH_CCH, @LTR_CCH_CCH
				END
				CLOSE CURSOR_SKIPITEM_CCH
				DEALLOCATE CURSOR_SKIPITEM_CCH
			END
			SET @STEP = '11-9'
			SET @BizCodeValue_bef = @BizCodeValue
		FETCH NEXT FROM CURSOR_SKIPITEM INTO @BizCodeValue
		END
		CLOSE CURSOR_SKIPITEM
		DEALLOCATE CURSOR_SKIPITEM
		SET @STEP = '12'
		UPDATE SKIP 
			SET SKIP.CURR_DATE = CAST(substring(CARDS.DATEAS,1,4)+'-'+substring(CARDS.DATEAS,5,2)+'-'+substring(CARDS.DATEAS,7,2) AS DATE),
				SKIP.CURR_CDC = CARDS.CDC,
				SKIP.CURR_BAL = CARDS.BALANCE,
				SKIP.CURR_STATE = CARDS.CACS_STATE
		FROM CARDS
		WHERE CARDS.CARDNMBR = SKIP.CARDNMBR
		AND CARDS.mis_date = @DATE
		AND SKIP.END_DATE IS NULL
		AND SKIP.RESULT IS NULL
		SET @STEP = '13'
		UPDATE SKIP 
			SET SKIP.CURR_DATE = CAST(substring(BUDTRAN.DATEAS,1,4)+'-'+substring(BUDTRAN.DATEAS,5,2)+'-'+substring(BUDTRAN.DATEAS,7,2) AS DATE),
				SKIP.CURR_CDC = BUDTRAN.CDC,
				SKIP.CURR_BAL = BUDTRAN.BALANCE,
				SKIP.CURR_STATE = BUDTRAN.CACS_STATE
		FROM BUDTRAN
		WHERE BUDTRAN.CARDNMBR = SKIP.CARDNMBR
		AND BUDTRAN.mis_date = @DATE
		AND SKIP.END_DATE IS NULL
		AND SKIP.RESULT IS NULL
		SET @STEP = '14'
		UPDATE SKIP 
			SET SKIP.CURR_DATE = CAST(substring(CARDSREC.DATEAS,1,4)+'-'+substring(CARDSREC.DATEAS,5,2)+'-'+substring(CARDSREC.DATEAS,7,2) AS DATE),
				SKIP.CURR_CDC = CARDSREC.CDC,
				SKIP.CURR_BAL = CARDSREC.BALANCE,
				SKIP.CURR_STATE = CARDSREC.CACS_STATE
		FROM CARDSREC
		WHERE CARDSREC.CARDNMBR = SKIP.CARDNMBR
		AND CARDSREC.mis_date = @DATE
		AND SKIP.END_DATE IS NULL
		AND SKIP.RESULT IS NULL
		SET @STEP = '15'
		UPDATE SKIP 
			SET SKIP.CURR_DATE = CAST(substring(ASSETREC.DATEAS,1,4)+'-'+substring(ASSETREC.DATEAS,5,2)+'-'+substring(ASSETREC.DATEAS,7,2) AS DATE),
				SKIP.CURR_CDC = ASSETREC.CDC,
				SKIP.CURR_BAL = ASSETREC.BALANCE,
				SKIP.CURR_STATE = ASSETREC.CACS_STATE
		FROM ASSETREC
		WHERE ASSETREC.CARDNMBR = SKIP.CARDNMBR
		AND ASSETREC.mis_date = @DATE
		AND SKIP.END_DATE IS NULL
		AND SKIP.RESULT IS NULL
		SET @STEP = '16'
		UPDATE SKIP SET END_DATE = @DATE, RESULT = 'O' 
		WHERE SUBSTRING(CURR_STATE,1,1) <> 'T'
		OR (SUBSTRING(CURR_STATE,1,1) = 'T' AND (SUBSTRING(CURR_STATE,2,2) IN ('11','12','17') OR CAST(SUBSTRING(CURR_STATE,2,2) AS INT) > 41))
		OR (SUBSTRING(CURR_STATE,1,1) = 'T' AND (CAST(SUBSTRING(CURR_STATE,2,2) AS INT) < 10))
		SET @STEP = '17'
		UPDATE SKIP 
			SET SKIP.T21_DATE = @DATE,
				SKIP.END_DATE = @DATE,
				SKIP.RESULT = 'S',
				SKIP.T21_BAL = 0,
				SKIP.T21_CDC = 0
		FROM CCH
		WHERE CCH.CARDNMBR = SKIP.CARDNMBR
		AND CCH.mis_date = @DATE
		AND SKIP.END_DATE IS NULL
		AND CCH.RTE_CCH = 'I01'
		SET @STEP = '18'
		UPDATE SKIP 
			SET SKIP.END_DATE = @DATE,
				SKIP.RESULT = 'O'
		FROM CCH
		WHERE CCH.CARDNMBR = SKIP.CARDNMBR
		AND CCH.mis_date = @DATE
		AND SKIP.END_DATE IS NULL
		AND CCH.RTE_CCH = 'T11'
		SET @STEP = '19'
		UPDATE SKIP 
			SET SKIP.PMT_CCH = CCH.PMT_CCH
		FROM CCH
		WHERE CCH.CARDNMBR = SKIP.CARDNMBR
		AND CCH.CARDNMBR IN (SELECT CARDNMBR FROM CCH WHERE DATE_CCH=@DATE AND RTE_CCH='I01')
		AND SKIP.PMT_CCH IS NULL
		AND SKIP.END_DATE = @DATE
		SET @STEP = '20'
		EXEC SP_INSERT_LOG @DATE,'TRANS_SKIP_sp','SKIP匯入完成','N'
		SET @OUTPUT = 'S'
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @DATE,'TRANS_SKIP_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

