	Declare @sqlstr Nvarchar(800)
	Declare @sqlstr_1 Nvarchar(800)
	SET @asofdate = convert(varchar(10),cast(@asofdate as date),111)
	if exists(select * from INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'UPD_ID_CACS_CHG1')
	BEGIN
		DROP table UPD_ID_CACS_CHG1
	END
	IF (@COL_ID='')
		BEGIN
		SET @COL_ID='CUSTID'
	END
