	SET @sqlstr = 'INSERT INTO #CHGID1 Select OLDID, NEWID FROM CHANGEIDBASE_GROUP WHERE LTRIM(OLDID) IN (SELECT '+ @COL_ID +' FROM '+ @MAP_DBF+' WHERE MIS_DATE ='''+ @asofdate+''''+')'
	EXEC(@sqlstr)
	SET @sqlstr = 'Select * INTO DBO.UPD_ID_CACS_CHG1 FROM '+ @MAP_DBF +' WHERE MIS_DATE ='''+ @asofdate+''''+ ' AND '+ @COL_ID +' IN (SELECT OLDID FROM #CHGID1)'
	EXEC(@sqlstr)
	IF (select COUNT(*) AS CUNT from UPD_ID_CACS_CHG1)>0
	BEGIN
		SET @sqlstr = 'UPDATE UPD_ID_CACS_CHG1 SET ID_TAG=LTRIM(A.NEWID) FROM #CHGID1 A,UPD_ID_CACS_CHG1 B WHERE A.OLDID=B.' +@COL_ID
		exec(@sqlstr)
	END
	SET @sqlstr = 'DELETE FROM '+ @MAP_DBF+' WHERE '+@COL_ID+' IN (SELECT OLDID FROM #CHGID1) and convert(varchar(10),mis_date,111)  ='''+ @asofdate+''''
	EXEC (@sqlstr)
	SET @sqlstr='INSERT INTO '+@MAP_DBF+' SELECT * FROM UPD_ID_CACS_CHG1'
	EXEC(@sqlstr)
	SET @sqlstr='UPDATE '+@MAP_DBF+' SET ID_TAG=SPACE(10) WHERE ('+@COL_ID+' ='' '' OR '+@COL_ID+' IS NULL) and convert(varchar(10),mis_date,111)  ='''+ @asofdate+'''' 
	EXEC(@sqlstr)
	SET @sqlstr='UPDATE '+@MAP_DBF+' SET ID_TAG=LTRIM('+@COL_ID+') WHERE (ID_TAG='' '' OR ID_TAG IS NULL) AND ('+@COL_ID+' !='' '' OR '+@COL_ID+' IS NOT NULL) AND LEN('+@COL_ID+') <= 10 and convert(varchar(10),mis_date,111)  ='''+ @asofdate+'''' 
	EXEC(@sqlstr)
	SET @sqlstr='UPDATE '+@MAP_DBF+' SET ID_TAG=RIGHT(LTRIM('+@COL_ID+'),10) WHERE (ID_TAG='' '' OR ID_TAG IS NULL) AND ('+@COL_ID+' !='' '' OR '+@COL_ID+' IS NOT NULL) AND LEN('+@COL_ID+')>10 and convert(varchar(10),mis_date,111)  ='''+ @asofdate+'''' 
	EXEC(@sqlstr)
	/*CHECK*/
	SET @sqlstr_1= 'SELECT B.NEWID, B.OLDID, B.DATEAPPEND, A.* FROM '+@MAP_DBF+' A LEFT JOIN CHANGEIDBASE_GROUP B ON A.'+@COL_ID+'=B.OLDID and convert(varchar(10),a.mis_date,111)  ='''+ @asofdate+''''
	SET @sqlstr='SELECT DEL2.* FROM ('+@sqlstr_1+ ') as DEL2 WHERE (NEWID !='' '' OR NEWID IS NOT NULL) AND ID_TAG != NEWID'
	exec(@sqlstr)
END

