ALTER PROCEDURE [dbo].[UPDATE_CARDS_DBF_sp]
	@V_MIS_DATE VARCHAR(10)
  , @OUTPUT VARCHAR(500) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @STEP VARCHAR(5)
	DECLARE @CARDNMBR VARCHAR(16)
	DECLARE @LOCATION VARCHAR(6)
	DECLARE @TEXT3 VARCHAR(10)
	DECLARE @TEXT4 VARCHAR(10)
	DECLARE @TEXT6 VARCHAR(10)
	DECLARE @TEXT7 VARCHAR(10)
