	BEGIN TRY
		SET @STEP = '1'
		DELETE FROM COGDB2.dbo.NDM_UPD WHERE DATEAS=CONVERT(VARCHAR,GETDATE(),112) AND PROCESS='FIX_CACS9' AND EDUSER='MISBATCH'
		SET @STEP = '2'
		DECLARE @DATE_1 VARCHAR(8) = CONVERT(VARCHAR,DATEADD(D,-1,GETDATE()),112)
		DECLARE @DATE_2 VARCHAR(8) = CONVERT(VARCHAR,DATEADD(D,-2,GETDATE()),112)
		DECLARE @CARDS_MM_BEF VARCHAR(10) = 'CARDS'
		DECLARE @BUDTRAN_MM_BEF VARCHAR(10) = 'BUDTRAN'
		IF (RIGHT(@DATE_1,2) = '01')
		BEGIN
			SET @CARDS_MM_BEF = @CARDS_MM_BEF + '_' + SUBSTRING(@DATE_2,5,2)
			SET @BUDTRAN_MM_BEF = @BUDTRAN_MM_BEF + '_' + SUBSTRING(@DATE_2,5,2)
			PRINT @CARDS_MM_BEF
			PRINT @BUDTRAN_MM_BEF
		END
		SET @STEP = '3'
		if object_id('tempdb..#PF24') is not null drop table #PF24
		CREATE TABLE #PF24
		(
			CARDNMBR varchar(16)
		  , location varchar(6)
		  , TEXT3 varchar(10)
		  , TEXT4 varchar(10)
		  , TEXT6 varchar(10)
		  , TEXT7 varchar(10)
		)
		DECLARE @SQLCmd NVARCHAR(4000)
		SET @SQLCmd = N'INSERT INTO #PF24 SELECT CARDNMBR,LOCATION,ISNULL(TEXT3,'''') TEXT3,ISNULL(TEXT4,'''') TEXT4,ISNULL(TEXT6,'''') TEXT6,ISNULL(TEXT7,'''') TEXT7 '
		SET @SQLCmd = @SQLCmd + N'FROM PF24 '
		SET @SQLCmd = @SQLCmd + N'WHERE MIS_DATE=''' + @DATE_1 + ''' '
		SET @SQLCmd = @SQLCmd + N'AND CARDNMBR IN (SELECT CARDNMBR FROM BUDTRAN WHERE MIS_DATE=''' + @DATE_1 + ''' AND PRINCIPAL-RESERVE>0 '
		SET @SQLCmd = @SQLCmd + N'AND CARDNMBR NOT IN (SELECT CARDNMBR FROM ' + @BUDTRAN_MM_BEF + ' WHERE MIS_DATE=''' + @DATE_2 + ''')) '
		SET @SQLCmd = @SQLCmd + N'AND (ISNULL(TEXT4,'''') <> '''' OR ISNULL(TEXT5,'''') <> '''' OR ISNULL(TEXT6,'''') <> '''' OR ISNULL(TEXT7,'''') <> '''' ) '
		SET @SQLCmd = @SQLCmd + N'UNION '
		SET @SQLCmd = @SQLCmd + N'SELECT CARDNMBR,LOCATION,ISNULL(TEXT3,'''') TEXT3,ISNULL(TEXT4,'''') TEXT4,ISNULL(TEXT6,'''') TEXT6,ISNULL(TEXT7,'''') TEXT7 '
		SET @SQLCmd = @SQLCmd + N'FROM PF24 '
		SET @SQLCmd = @SQLCmd + N'WHERE MIS_DATE=''' + @DATE_1 + ''' '
		SET @SQLCmd = @SQLCmd + N'AND CARDNMBR IN (SELECT CARDNMBR FROM CARDS WHERE MIS_DATE=''' + @DATE_1 + ''' AND WOAMT=0 AND ISNULL(WODATE,'''')='''' '
		SET @SQLCmd = @SQLCmd + N'AND CARDNMBR NOT IN (SELECT CARDNMBR FROM ' + @CARDS_MM_BEF + ' WHERE MIS_DATE=''' + @DATE_2 + ''')) '
		SET @SQLCmd = @SQLCmd + N'AND (ISNULL(TEXT4,'''') <> '''' OR ISNULL(TEXT5,'''') <> '''' OR ISNULL(TEXT6,'''') <> '''' OR ISNULL(TEXT7,'''') <> '''' ) '
		EXECUTE sp_executesql @SQLCmd
		SET @STEP = '4'
		DECLARE CURSOR_TEMP CURSOR FAST_FORWARD FOR
			SELECT CARDNMBR,LOCATION,TEXT3,TEXT4,TEXT6,TEXT7 FROM #PF24
			/*SELECT CARDNMBR,LOCATION,ISNULL(TEXT3,'') TEXT3,ISNULL(TEXT4,'') TEXT4,ISNULL(TEXT6,'') TEXT6,ISNULL(TEXT7,'') TEXT7
			FROM PF24 
			WHERE MIS_DATE=CONVERT(VARCHAR,DATEADD(D,-1,GETDATE())) 
