			AND CARDNMBR NOT IN (SELECT CARDNMBR FROM BUDTRAN WHERE MIS_DATE=CONVERT(VARCHAR,DATEADD(D,-2,GETDATE()),112)))
			AND (ISNULL(TEXT4,'') <> '' OR ISNULL(TEXT5,'') <> '' OR ISNULL(TEXT6,'') <> '' OR ISNULL(TEXT7,'') <> '' )
			UNION
			SELECT CARDNMBR,LOCATION,ISNULL(TEXT3,'') TEXT3,ISNULL(TEXT4,'') TEXT4,ISNULL(TEXT6,'') TEXT6,ISNULL(TEXT7,'') TEXT7
			FROM PF24 
			WHERE MIS_DATE=CONVERT(VARCHAR,DATEADD(D,-1,GETDATE())) 
			AND CARDNMBR IN (SELECT CARDNMBR FROM CARDS WHERE MIS_DATE=CONVERT(VARCHAR,DATEADD(D,-1,GETDATE())) AND WOAMT=0
			AND CARDNMBR NOT IN (SELECT CARDNMBR FROM CARDS WHERE MIS_DATE=CONVERT(VARCHAR,DATEADD(D,-2,GETDATE()),112)))
			AND (ISNULL(TEXT4,'') <> '' OR ISNULL(TEXT5,'') <> '' OR ISNULL(TEXT6,'') <> '' OR ISNULL(TEXT7,'') <> '' )*/
			/* tets sql
			SELECT CARDNMBR,LOCATION,ISNULL(TEXT3,'') TEXT3,ISNULL(TEXT4,'') TEXT4,ISNULL(TEXT6,'') TEXT6,ISNULL(TEXT7,'') TEXT7
			FROM PF24 WHERE MIS_DATE=convert(varchar,dateadd(D,-1,getdate())) AND CARDNMBR IN (
			SELECT CARDNMBR FROM BUDTRAN WHERE MIS_DATE=convert(varchar,dateadd(D,-1,getdate())) and principal-reserve>0
			AND CARDNMBR NOT IN (SELECT CARDNMBR FROM BUDTRAN WHERE MIS_DATE=convert(varchar,dateadd(D,-2,getdate()),112)))
			AND (ISNULL(TEXT4,'') <> '' OR ISNULL(TEXT5,'') <> '' OR ISNULL(TEXT6,'') <> '' OR ISNULL(TEXT7,'') <> '' )
			UNION
			SELECT CARDNMBR,LOCATION,ISNULL(TEXT3,'') TEXT3,ISNULL(TEXT4,'') TEXT4,ISNULL(TEXT6,'') TEXT6,ISNULL(TEXT7,'') TEXT7
			FROM PF24 WHERE MIS_DATE=convert(varchar,dateadd(D,-1,getdate())) AND CARDNMBR IN (
			SELECT CARDNMBR FROM CARDS WHERE MIS_DATE=convert(varchar,dateadd(D,-1,getdate())) and woamt=0
			AND CARDNMBR NOT IN (SELECT CARDNMBR FROM CARDS WHERE MIS_DATE=convert(varchar,dateadd(D,-2,getdate()),112)))
			AND (ISNULL(TEXT4,'') <> '' OR ISNULL(TEXT5,'') <> '' OR ISNULL(TEXT6,'') <> '' OR ISNULL(TEXT7,'') <> '' )
			*/
		OPEN CURSOR_TEMP
		FETCH NEXT FROM CURSOR_TEMP INTO @CARDNMBR,@LOCATION,@TEXT3,@TEXT4,@TEXT6,@TEXT7
		WHILE @@FETCH_STATUS = 0
		BEGIN
			SET @STEP = '5'
			IF (LEN(@TEXT3)>0 OR LEN(@TEXT4)>0 OR LEN(@TEXT6)>0)
			BEGIN
				SET @STEP = '6'
				INSERT INTO COGDB2.dbo.NDM_UPD(DATEAS,LOCATION,CARDNMBR,E_NUM,THEVALUE,THETYPE,PROCESS,EDUSER,EDTIME)
					VALUES(CONVERT(VARCHAR,GETDATE(),112),@LOCATION,@CARDNMBR,383,'','BKP','FIX_CACS9','MISBATCH',GETDATE())
				INSERT INTO COGDB2.dbo.NDM_UPD(DATEAS,LOCATION,CARDNMBR,E_NUM,THEVALUE,THETYPE,PROCESS,EDUSER,EDTIME)
					VALUES(CONVERT(VARCHAR,GETDATE(),112),@LOCATION,@CARDNMBR,384,'','BKP','FIX_CACS9','MISBATCH',GETDATE())
				INSERT INTO COGDB2.dbo.NDM_UPD(DATEAS,LOCATION,CARDNMBR,E_NUM,THEVALUE,THETYPE,PROCESS,EDUSER,EDTIME)
					VALUES(CONVERT(VARCHAR,GETDATE(),112),@LOCATION,@CARDNMBR,386,'','BKP','FIX_CACS9','MISBATCH',GETDATE())
			END
			IF (LEN(@TEXT7)>0)
			BEGIN
				SET @STEP = '7'
				SET @RTNCARDNMBR = @RTNCARDNMBR + ',' + @CARDNMBR
			END
			SET @STEP = '8'
			FETCH NEXT FROM CURSOR_TEMP INTO @CARDNMBR,@LOCATION,@TEXT3,@TEXT4,@TEXT6,@TEXT7
		END
		CLOSE CURSOR_TEMP
		DEALLOCATE CURSOR_TEMP
		SET @STEP = '9'
		IF (LEN(@RTNCARDNMBR)>0)
		BEGIN
			SET @OUTPUT = 'S|' + SUBSTRING(@RTNCARDNMBR,2,LEN(@RTNCARDNMBR)-1)
		END
		ELSE
		BEGIN
			SET @OUTPUT = 'S|X'
		END
		SET @STEP = '10'
	END TRY
	BEGIN CATCH
		SET @OUTPUT = 'F|SETP=[' + @STEP + '] ERRORMSG=' + ERROR_MESSAGE()
		EXEC SP_INSERT_LOG @V_MIS_DATE,'UPDATE_CARDS_DBF_sp',@OUTPUT,'Y'
	END CATCH
	RETURN
END

